import React, { useState, useEffect } from "react";
import { Eye, EyeOff } from "lucide-react"; // Utiliser des icônes sérieuses

export default function CardSettings() {
  const [userData, setUserData] = useState({
    nom: "",
    prenom: "",
    email: "",
    tel: ""
  });

  const [initialUserData, setInitialUserData] = useState(null);

  const [passwordData, setPasswordData] = useState({
    currentPassword: "",
    newPassword: "",
    confirmPassword: ""
  });

  const [showCurrentPassword, setShowCurrentPassword] = useState(false);
  const [showNewPassword, setShowNewPassword] = useState(false);

  useEffect(() => {
    const storedUser = localStorage.getItem("user");
    if (storedUser) {
      const parsedUser = JSON.parse(storedUser);
      const user = {
        nom: parsedUser.nom || "",
        prenom: parsedUser.prenom || "",
        email: parsedUser.email || "",
        tel: parsedUser.tel || ""
      };
      setUserData(user);
      setInitialUserData(user);
    }
  }, []);

  const handleUserChange = (e) => {
    setUserData({
      ...userData,
      [e.target.id]: e.target.value
    });
  };

  const handlePasswordChange = (e) => {
    setPasswordData({
      ...passwordData,
      [e.target.id]: e.target.value
    });
  };

  const handleCancel = () => {
    if (window.confirm("Voulez-vous vraiment réinitialiser les champs ?")) {
      if (initialUserData) {
        setUserData(initialUserData);
      }
      setPasswordData({
        currentPassword: "",
        newPassword: "",
        confirmPassword: ""
      });
    }
  };

  return (
    <>
      <div className="relative flex flex-col w-full max-w-[1000px] mb-6 shadow-lg rounded-lg bg-white border border-gray-200 overflow-hidden">
        <div className="rounded-t bg-gray-50 mb-0 px-6 py-6">
          <div className="text-center flex justify-between">
            <h6 className="text-gray-800 text-2xl font-bold">Mon Compte</h6>
          </div>
        </div>

        <div className="flex-auto px-4 lg:px-10 py-10 pt-0 bg-gray-100 rounded-b-lg overflow-auto">
          <form>
            <h6 className="text-gray-600 text-sm mt-3 mb-6 font-semibold uppercase">
              Informations Personnelles
            </h6>

            <div className="flex flex-wrap gap-4">
              {/* Nom */}
              <div className="w-full lg:w-6/12 px-4">
                <label className="block text-gray-700 mb-2" htmlFor="nom">Nom</label>
                <input
                  type="text"
                  className="form-input"
                  id="nom"
                  value={userData.nom}
                  onChange={handleUserChange}
                />
              </div>

              {/* Prénom */}
              <div className="w-full lg:w-6/12 px-4">
                <label className="block text-gray-700 mb-2" htmlFor="prenom">Prénom</label>
                <input
                  type="text"
                  className="form-input"
                  id="prenom"
                  value={userData.prenom}
                  onChange={handleUserChange}
                />
              </div>

              {/* Email */}
              <div className="w-full lg:w-6/12 px-4">
                <label className="block text-gray-700 mb-2" htmlFor="email">Email</label>
                <input
                  type="email"
                  className="form-input"
                  id="email"
                  value={userData.email}
                  onChange={handleUserChange}
                />
              </div>

              {/* Tel */}
              <div className="w-full lg:w-6/12 px-4">
                <label className="block text-gray-700 mb-2" htmlFor="tel">Numéro de téléphone</label>
                <input
                  type="text"
                  className="form-input"
                  id="tel"
                  value={userData.tel}
                  onChange={handleUserChange}
                />
              </div>
            </div>

            <hr className="mt-6 border-b-1 border-gray-300" />

            {/* Changer mot de passe */}
            <h6 className="text-gray-600 text-sm mt-3 mb-6 font-semibold uppercase">
              Changer le mot de passe
            </h6>

            <div className="flex flex-wrap gap-4">
              {/* Ancien mot de passe */}
              <div className="w-full lg:w-6/12 px-4 relative">
                <label className="block text-gray-700 mb-2" htmlFor="currentPassword">
                  Ancien mot de passe
                </label>
                <div className="relative">
                  <input
                    type={showCurrentPassword ? "text" : "password"}
                    className="form-input pr-10"
                    id="currentPassword"
                    value={passwordData.currentPassword}
                    onChange={handlePasswordChange}
                  />
                  <button
                    type="button"
                    className="absolute inset-y-0 right-3 flex items-center text-gray-600"
                    onClick={() => setShowCurrentPassword(!showCurrentPassword)}
                  >
                    {showCurrentPassword ? <EyeOff size={20} /> : <Eye size={20} />}
                  </button>
                </div>
              </div>

              {/* Nouveau mot de passe */}
              <div className="w-full lg:w-6/12 px-4 relative">
                <label className="block text-gray-700 mb-2" htmlFor="newPassword">
                  Nouveau mot de passe
                </label>
                <div className="relative">
                  <input
                    type={showNewPassword ? "text" : "password"}
                    className="form-input pr-10"
                    id="newPassword"
                    value={passwordData.newPassword}
                    onChange={handlePasswordChange}
                  />
                  <button
                    type="button"
                    className="absolute inset-y-0 right-3 flex items-center text-gray-600"
                    onClick={() => setShowNewPassword(!showNewPassword)}
                  >
                    {showNewPassword ? <EyeOff size={20} /> : <Eye size={20} />}
                  </button>
                </div>
              </div>
            </div>

            <div className="flex justify-end mt-6 gap-4">
              <button
                type="button"
                className="btn-cancel"
                onClick={handleCancel}
              >
                Annuler
              </button>
              <button
                type="submit"
                className="btn-save"
              >
                Enregistrer
              </button>
            </div>
          </form>
        </div>
      </div>

      {/* Styles */}
      <style jsx>{`
        .form-input {
          width: 100%;
          padding: 0.75rem;
          border: 1px solid #d1d5db;
          border-radius: 0.375rem;
          background-color: #ffffff;
          color: #4a5568;
          font-size: 1rem;
          transition: border-color 0.2s ease;
        }

        .form-input:focus {
          border-color: #3182ce;
          outline: none;
        }

        .btn-cancel {
          background-color: #edf2f7;
          padding: 0.75rem 1.5rem;
          border-radius: 0.375rem;
          font-weight: 600;
          color: #2d3748;
          transition: background 0.2s ease;
        }

        .btn-cancel:hover {
          background-color: #e2e8f0;
        }

        .btn-save {
          background-color: #2b6cb0;
          padding: 0.75rem 1.5rem;
          border-radius: 0.375rem;
          font-weight: 600;
          color: white;
          transition: background 0.2s ease;
        }

        .btn-save:hover {
          background-color: #3182ce;
        }
      `}</style>
    </>
  );
}
