import React, { useState, useEffect } from "react";
import IndexNavbar from "components/Navbars/IndexNavbar.js";
import Footerr from "components/Footers/Footerr";

function MyTickets() {
  // ... (le reste du composant reste inchangé)

  return (
    <>
      <IndexNavbar />

      <div style={container}>
        <div style={contentWrapper}>
          <div style={headerContainer}>
            <div style={imageWrapper}>
              <img 
                src="https://img.icons8.com/clouds/300/customer-support.png" 
                alt="Support client" 
                style={headerImage}
              />
              <div style={imageHalo}></div>
            </div>
            
            <div style={titleContainer}>
              <h1 style={title}>
                Support Client
                <span style={titleUnderline}></span>
              </h1>
              <div style={titleDecoration}></div>
            </div>
          </div>

          {/* ... (le reste du JSX) */}
        </div>
      </div>

      <Footerr />
    </>
  );
}

// NOUVEAUX STYLES
const imageWrapper = {
  position: 'relative',
  marginBottom: '2rem',
};

const imageHalo = {
  position: 'absolute',
  top: '-15px',
  left: '-15px',
  right: '-15px',
  bottom: '-15px',
  backgroundColor: 'rgba(26, 35, 126, 0.1)',
  borderRadius: '50%',
  zIndex: 0,
};

const titleContainer = {
  position: 'relative',
  marginTop: '3rem',
  padding: '1.5rem 0',
};

const titleDecoration = {
  position: 'absolute',
  bottom: 0,
  left: '50%',
  transform: 'translateX(-50%)',
  width: '200px',
  height: '8px',
  background: 'linear-gradient(90deg, #1a237e 0%, #4a5699 50%, #1a237e 100%)',
  borderRadius: '4px',
  boxShadow: '0 2px 8px rgba(26, 35, 126, 0.2)',
};

// STYLES MODIFIÉS
const headerContainer = {
  display: 'flex',
  alignItems: 'center',
  gap: '2rem',
  margin: '4rem 0 5rem 0',
  position: 'relative',
  flexDirection: 'column',
  textAlign: 'center',
};

const title = {
  color: '#1a237e',
  fontSize: '2.8rem',
  fontWeight: '700',
  margin: 0,
  textShadow: '3px 3px 6px rgba(0,0,0,0.1)',
  position: 'relative',
  letterSpacing: '-0.5px',
};

const titleUnderline = {
  display: 'block',
  width: '80px',
  height: '4px',
  backgroundColor: '#ff6f00',
  borderRadius: '2px',
  margin: '1rem auto 0',
  transform: 'skewX(-15deg)',
};

// ... (conserver les autres styles existants)

export default MyTickets;
