import React, { useState } from "react";
import { Link } from "react-router-dom";
import IndexNavbar from "components/Navbars/IndexNavbar.js";
import Footerr from "components/Footers/Footerr";

export default function NewTicketForm() {
  const [formData, setFormData] = useState({
    sujet: "",
    type: "",
    urgence: "",
    description: "",
  });
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [error, setError] = useState(null);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsSubmitting(true);
    setError(null);

    try {
      const newTicket = {
        ...formData,
        id: Date.now(),
        date: new Date().toLocaleDateString(),
        statut: "Ouvert",
      };

      const response = await fetch("/addticket", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(formData),
      });

      if (!response.ok) throw new Error(`Erreur HTTP ${response.status}`);

      const storedTickets = JSON.parse(localStorage.getItem("tickets")) || [];
      storedTickets.push(newTicket);
      localStorage.setItem("tickets", JSON.stringify(storedTickets));

      window.location.href = "/MyTickets";
    } catch (error) {
      const storedTickets = JSON.parse(localStorage.getItem("tickets")) || [];
      storedTickets.push({
        ...formData,
        id: Date.now(),
        date: new Date().toLocaleDateString(),
        statut: "Erreur d'envoi - Sauvegardé localement",
      });
      localStorage.setItem("tickets", JSON.stringify(storedTickets));
      setError("Erreur : Ticket sauvegardé localement. " + error.message);
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <>
      <IndexNavbar />
      <div className="min-h-screen bg-gray-100 py-10 px-4 flex justify-center items-center">
        <div className="bg-white shadow-lg rounded-lg w-full max-w-3xl p-8">
          <h2 className="text-2xl font-semibold text-blue-900 mb-2">
            🎫 Créer un ticket d'assistance
          </h2>
          <p className="text-gray-600 mb-6">
            Veuillez remplir les champs ci-dessous pour soumettre votre demande.
          </p>

          {error && (
            <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded mb-4">
              {error}
            </div>
          )}

          <form onSubmit={handleSubmit} className="space-y-5">
            <div>
              <label className="block text-sm font-medium text-gray-700">
                Sujet
              </label>
              <input
                type="text"
                name="sujet"
                value={formData.sujet}
                onChange={handleChange}
                placeholder="Ex: Problème de virement"
                className="mt-1 block w-full rounded-md border border-gray-300 p-2 shadow-sm focus:border-blue-500 focus:ring focus:ring-blue-300"
                required
              />
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700">
                Type
              </label>
              <select
                name="type"
                value={formData.type}
                onChange={handleChange}
                className="mt-1 block w-full rounded-md border border-gray-300 p-2 shadow-sm focus:border-blue-500 focus:ring focus:ring-blue-300"
                required
              >
                <option value="">-- Sélectionner --</option>
                <option value="IT">IT - Informatique</option>
                <option value="RH">RH - Ressources Humaines</option>
                <option value="Comptabilité">Comptabilité</option>
              </select>
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700">
                Urgence
              </label>
              <select
                name="urgence"
                value={formData.urgence}
                onChange={handleChange}
                className="mt-1 block w-full rounded-md border border-gray-300 p-2 shadow-sm focus:border-blue-500 focus:ring focus:ring-blue-300"
                required
              >
                <option value="">-- Choisir --</option>
                <option value="Urgent">Urgent</option>
                <option value="Normal">Normal</option>
                <option value="Faible">Faible</option>
              </select>
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700">
                Description
              </label>
              <textarea
                name="description"
                rows="4"
                value={formData.description}
                onChange={handleChange}
                placeholder="Décrivez le problème rencontré..."
                className="mt-1 block w-full rounded-md border border-gray-300 p-2 shadow-sm focus:border-blue-500 focus:ring focus:ring-blue-300"
                required
              ></textarea>
            </div>

            <div className="flex justify-between items-center">
              <Link
                to="/MyTickets"
                className="text-blue-800 hover:underline text-sm"
              >
                ← Retour
              </Link>
              <button
                type="submit"
                className="bg-blue-900 text-white px-5 py-2 rounded-md shadow hover:bg-blue-800 transition"
                disabled={isSubmitting}
              >
                {isSubmitting ? "Envoi..." : "Soumettre"}
              </button>
            </div>
          </form>
        </div>
      </div>
      <Footerr />
    </>
  );
}
