import React, { useState } from "react";
import { db } from "../firebase"; // assure-toi que firebase.js est bien configuré
import { collection, addDoc } from "firebase/firestore";

export default function MesTickets() {
  const [formData, setFormData] = useState({
    title: "",
    type: "",
    date: "",
  });

  const handleChange = (e) => {
    setFormData((prev) => ({
      ...prev,
      [e.target.name]: e.target.value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      await addDoc(collection(db, "tickets"), {
        title: formData.title,
        type: formData.type,
        date: formData.date,
      });

      alert("Ticket ajouté avec succès !");
      setFormData({ title: "", type: "", date: "" });
    } catch (error) {
      console.error("Erreur lors de l'ajout :", error);
      alert("Erreur lors de l'ajout du ticket.");
    }
  };

  return (
    <div style={{ padding: "2rem", fontFamily: "Arial, sans-serif" }}>
      <h2 style={{ marginBottom: "1rem" }}>Mes Tickets</h2>

      <form onSubmit={handleSubmit} style={{ display: "flex", flexDirection: "column", gap: "1rem", maxWidth: "400px" }}>
        <input
          type="text"
          name="title"
          value={formData.title}
          placeholder="Titre du ticket"
          onChange={handleChange}
          required
          style={{ padding: "0.5rem", borderRadius: "5px", border: "1px solid #ccc" }}
        />
        <input
          type="text"
          name="type"
          value={formData.type}
          placeholder="Type (ex: Réclamation, Demande info...)"
          onChange={handleChange}
          required
          style={{ padding: "0.5rem", borderRadius: "5px", border: "1px solid #ccc" }}
        />
        <input
          type="date"
          name="date"
          value={formData.date}
          onChange={handleChange}
          required
          style={{ padding: "0.5rem", borderRadius: "5px", border: "1px solid #ccc" }}
        />
        <button type="submit" style={{ padding: "0.7rem", backgroundColor: "#007bff", color: "white", border: "none", borderRadius: "5px" }}>
          Ajouter Ticket
        </button>
      </form>
    </div>
  );
}
