import React, { useEffect, useState } from "react";
import axios from "axios";

export function CardStatsUtilisateurs() {
  const [stats, setStats] = useState(null);

  useEffect(() => {
    axios.get("http://localhost:5000/api/stats") // Change l'URL si besoin
      .then((res) => setStats(res.data))
      .catch((err) => console.error("Erreur stats :", err));
  }, []);

  if (!stats) return <div>Chargement...</div>;

  return (
    <div className="flex flex-wrap gap-4">
      <CardStats
        statSubtitle="UTILISATEURS"
        statTitle={stats.utilisateurs}
        statArrow="up"
        statPercent="100"
        statPercentColor="text-emerald-500"
        statDescripiron="Nombre total"
        statIconName="fas fa-users"
        statIconColor="bg-lightBlue-500"
      />
      <CardStats
        statSubtitle="TECHNICIENS"
        statTitle={stats.techniciens}
        statArrow="up"
        statPercent="100"
        statPercentColor="text-emerald-500"
        statDescripiron="Nombre total"
        statIconName="fas fa-tools"
        statIconColor="bg-orange-500"
      />
      <CardStats
        statSubtitle="ADMINS"
        statTitle={stats.admins}
        statArrow="up"
        statPercent="100"
        statPercentColor="text-emerald-500"
        statDescripiron="Nombre total"
        statIconName="fas fa-user-shield"
        statIconColor="bg-red-500"
      />
    </div>
  );
}
