import React, { useState } from "react";
import IndexNavbar from "components/Navbars/IndexNavbar.js";
import Footerr from "components/Footers/Footerr";
import { Link } from "react-router-dom";

function NewTicketForm() {
  const [formData, setFormData] = useState({
    sujet: "",
    type: "",
    urgence: "",
    description: "",
  });
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [error, setError] = useState(null);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsSubmitting(true);
    setError(null);

    try {
      const response = await fetch('http://localhost:3000/api/tickets', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData)
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.error || '√âchec de la cr√©ation du ticket');
      }

      window.location.href = "/MyTickets";
      
    } catch (error) {
      console.error('Erreur:', error);
      setError(error.message);
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <>
      <IndexNavbar />
      <div style={container}>
        <div style={card}>
          <div style={leftSide}>
            <h2 style={title}>üé´ Cr√©er un ticket d'assistance</h2>
            <p style={subtitle}>
              Merci de remplir les informations n√©cessaires pour traiter votre
              demande.
            </p>

            {error && <div style={errorStyle}>{error}</div>}

            <form style={form} onSubmit={handleSubmit}>
              <div style={inputGroup}>
                <label htmlFor="sujet" style={label}>
                  Sujet
                </label>
                <input
                  type="text"
                  id="sujet"
                  name="sujet"
                  value={formData.sujet}
                  onChange={handleChange}
                  placeholder="Ex : Virement non effectu√©"
                  style={input}
                  required
                />
              </div>

              <div style={inputGroup}>
                <label htmlFor="type" style={label}>
                  Type
                </label>
                <select
                  id="type"
                  name="type"
                  value={formData.type}
                  onChange={handleChange}
                  style={input}
                  required
                >
                  <option value="">-- S√©lectionner --</option>
                  <option value="Technique">Technique</option>
                  <option value="Mat√©riel">Mat√©riel</option>
                  <option value="Logiciel">Logiciel</option>
                </select>
              </div>

              <div style={inputGroup}>
                <label htmlFor="urgence" style={label}>
                  Urgence
                </label>
                <select
                  id="urgence"
                  name="urgence"
                  value={formData.urgence}
                  onChange={handleChange}
                  style={input}
                  required
                >
                  <option value="">-- Choisir --</option>
                  <option value="Faible">Faible</option>
                  <option value="Moyenne">Moyenne</option>
                  <option value="Urgente">Urgente</option>
                </select>
              </div>

              <div style={inputGroup}>
                <label htmlFor="description" style={label}>
                  Description
                </label>
                <textarea
                  id="description"
                  name="description"
                  rows="4"
                  value={formData.description}
                  onChange={handleChange}
                  placeholder="D√©crivez le probl√®me rencontr√©..."
                  style={textarea}
                  required
                ></textarea>
              </div>

              <div style={buttonGroup}>
                <Link to="/MyTickets" style={backLink}>
                  ‚Üê Retour
                </Link>
                <button 
                  type="submit" 
                  style={submitButton}
                  disabled={isSubmitting}
                >
                  {isSubmitting ? 'Envoi en cours...' : 'Soumettre'}
                </button>
              </div>
            </form>
          </div>

          <div style={rightSide}>
            <img
              src="https://images.unsplash.com/photo-1605902711622-cfb43c4437d4?auto=format&fit=crop&w=1000&q=80"
              alt="Assistance bancaire"
              style={imageStyle}
            />
          </div>
        </div>
      </div>
      <Footerr />
    </>
  );
}

// Styles (ajout du style d'erreur)
const errorStyle = {
  color: '#dc3545',
  backgroundColor: '#f8d7da',
  padding: '0.75rem 1.25rem',
  borderRadius: '0.25rem',
  marginBottom: '1rem',
  border: '1px solid #f5c6cb'
};

// Le reste des styles reste identique
const container = {
  backgroundColor: "#f1f3f6",
  minHeight: "100vh",
  display: "flex",
  justifyContent: "center",
  alignItems: "center",
  padding: "2rem",
};

const card = {
  display: "flex",
  flexDirection: "row",
  backgroundColor: "#fff",
  borderRadius: "16px",
  boxShadow: "0 8px 24px rgba(0, 0, 0, 0.07)",
  overflow: "hidden",
  maxWidth: "1080px",
  width: "100%",
};

// ... (tous les autres styles restent identiques)

export default NewTicketForm;
