import React, { useEffect, useState } from "react";
import axios from "axios";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faTrash, faPen } from "@fortawesome/free-solid-svg-icons";

export default function CardTable() {
  const [users, setUsers] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [newUser, setNewUser] = useState({
    nom: "",
    prenom: "",
    email: "",
    role: "",
    tel: "",
  });
  const [isEditing, setIsEditing] = useState(false);
  const [editId, setEditId] = useState(null);

  useEffect(() => {
    axios.get("/allusers")
      .then(res => setUsers(res.data))
      .catch(err => console.error(err));
  }, []);

  const handleChange = (e) => {
    setNewUser({ ...newUser, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (isEditing) {
      axios.put(`/updateuser/${editId}`, newUser)
        .then(res => {
          const updated = users.map(u => (u._id === editId ? res.data : u));
          setUsers(updated);
          setIsEditing(false);
          setShowForm(false);
        });
    } else {
      axios.post("/adduser", newUser)
        .then(res => {
          setUsers([...users, res.data]);
          setShowForm(false);
        });
    }
    setNewUser({ nom: "", prenom: "", email: "", role: "", tel: "" });
  };

  const handleEdit = (user) => {
    setNewUser(user);
    setEditId(user._id);
    setIsEditing(true);
    setShowForm(true);
  };

  const handleDelete = (id) => {
    if (window.confirm("Confirmer la suppression ?")) {
      axios.delete(`/deleteuser/${id}`)
        .then(() => setUsers(users.filter(u => u._id !== id)));
    }
  };

  return (
    <div className="p-4 bg-white rounded shadow">
      <h3 className="text-xl font-bold mb-4">Liste des utilisateurs</h3>
      <button
        className="mb-4 bg-green-600 hover:bg-green-700 text-white px-4 py-2 rounded"
        onClick={() => {
          setShowForm(true);
          setIsEditing(false);
          setNewUser({ nom: "", prenom: "", email: "", role: "", tel: "" });
        }}
      >
        + Ajouter un utilisateur
      </button>

      <table className="w-full table-auto border">
        <thead className="bg-gray-200">
          <tr>
            <th>Nom</th>
            <th>Prénom</th>
            <th>Email</th>
            <th>Rôle</th>
            <th>Téléphone</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {users.map(user => (
            <tr key={user._id} className="text-center border-t">
              <td>{user.nom}</td>
              <td>{user.prenom}</td>
              <td>{user.email}</td>
              <td>{user.role}</td>
              <td>{user.tel}</td>
              <td>
                <button className="text-blue-600 mr-2" onClick={() => handleEdit(user)}>
                  <FontAwesomeIcon icon={faPen} />
                </button>
                <button className="text-red-600" onClick={() => handleDelete(user._id)}>
                  <FontAwesomeIcon icon={faTrash} />
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {showForm && (
        <form onSubmit={handleSubmit} className="mt-4 bg-gray-100 p-4 rounded">
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <input type="text" name="nom" placeholder="Nom" value={newUser.nom} onChange={handleChange} required className="p-2 border rounded" />
            <input type="text" name="prenom" placeholder="Prénom" value={newUser.prenom} onChange={handleChange} required className="p-2 border rounded" />
            <input type="email" name="email" placeholder="Email" value={newUser.email} onChange={handleChange} required className="p-2 border rounded" />
            <input type="text" name="role" placeholder="Rôle" value={newUser.role} onChange={handleChange} required className="p-2 border rounded" />
            <input type="text" name="tel" placeholder="Téléphone" value={newUser.tel} onChange={handleChange} required className="p-2 border rounded" />
          </div>
          <div className="mt-4 flex justify-end gap-2">
            <button type="button" onClick={() => setShowForm(false)} className="bg-gray-500 text-white px-4 py-2 rounded">
              Annuler
            </button>
            <button type="submit" className="bg-green-600 hover:bg-green-700 text-white px-4 py-2 rounded">
              {isEditing ? "Modifier" : "Ajouter"}
            </button>
          </div>
        </form>
      )}
    </div>
  );
}
