import React, { useState } from "react";
import PropTypes from "prop-types";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faTrash, faPen, faSquareCheck, faPlus, faSearch } from '@fortawesome/free-solid-svg-icons';

export default function CardTable({ color }) {
  const [items, setItems] = useState([
    { id: 1, subject: 'Problème d\'accès à l\'internet', type: 'Urgent', status: 'En attente', date: '2025-03-29', description: 'Le site intranet était inaccessible depuis plusieurs jours.' },
    { id: 2, subject: 'Mise à jour logiciel', type: 'Normal', status: 'Terminé', date: '2025-04-15', description: 'Mise à jour du logiciel de gestion RH.' }
  ]);
  const [filter, setFilter] = useState('Tous');
  const [searchQuery, setSearchQuery] = useState('');
  const [isDialogOpen, setIsDialogOpen] = useState(false);
  const [selectedItemId, setSelectedItemId] = useState(null);

  const handleCancelDelete = () => {
    setIsDialogOpen(false);
    setSelectedItemId(null);
  };

  const handleDeleteConfirmation = (id) => {
    setSelectedItemId(id);
    setIsDialogOpen(true);
  };

  const handleConfirmDelete = () => {
    setItems(items.filter(item => item.id !== selectedItemId));
    setIsDialogOpen(false);
  };

  const handleComplete = (id) => {
    setItems(items.map(item =>
      item.id === id ? { ...item, status: 'Terminé' } : item
    ));
  };

  const handleEdit = (id) => {
    setItems(items.map(item =>
      item.id === id ? { ...item, subject: 'Problème résolu', description: 'Le problème a été résolu avec succès.' } : item
    ));
  };

  const filteredItems = [...items]
    .filter(item => {
      const matchesFilter = filter === 'Tous' || item.type === filter;
      const matchesSearch = item.subject.toLowerCase().includes(searchQuery.toLowerCase()) || 
                            item.description.toLowerCase().includes(searchQuery.toLowerCase());
      return matchesFilter && matchesSearch;
    })
    .sort((a, b) => (a.type === 'Urgent' ? -1 : 1));

  return (
    <div className={`relative flex flex-col min-w-0 rounded-lg shadow-lg mb-6 ${
      color === "light" ? "bg-white" : "bg-slate-800 text-white"
    }`}>
      
      {/* Dialogue de confirmation */}
      {isDialogOpen && (
        <div className="fixed inset-0 bg-black/50 flex items-start justify-center pt-24 z-50">
          <div className="bg-white p-8 rounded-lg shadow-xl max-w-md w-full border-t-4 border-red-600">
            <p className="text-lg text-gray-700 mb-6 font-semibold">
              Êtes-vous sûr de vouloir supprimer ce ticket ?
            </p>
            <div className="flex justify-end gap-4">
              <button onClick={handleCancelDelete} className="px-4 py-2 rounded bg-gray-200 text-gray-700 hover:bg-gray-300 transition">
                Annuler
              </button>
              <button onClick={handleConfirmDelete} className="px-4 py-2 rounded bg-red-600 text-white hover:bg-red-700 transition">
                Supprimer
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Barre d'outils */}
      <div className="flex flex-wrap items-center justify-between p-6 gap-4 border-b">
        <div className="flex items-center gap-4 flex-1 max-w-3xl">
          <div className="relative w-full max-w-xs">
            <FontAwesomeIcon icon={faSearch} className="absolute left-3 top-3 text-gray-400" />
            <input
              type="text"
              placeholder="Rechercher un ticket..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              className="pl-10 pr-4 py-2 border rounded-lg w-full focus:outline-none focus:ring-2 focus:ring-blue-500 bg-gray-50 text-sm"
            />
          </div>
          <select 
            value={filter} 
            onChange={(e) => setFilter(e.target.value)} 
            className="px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 bg-gray-50 text-sm"
          >
            <option value="Tous">Tous</option>
            <option value="Urgent">Urgent</option>
            <option value="Normal">Normal</option>
          </select>
        </div>
        <button className="flex items-center gap-2 px-6 py-3 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition">
          <FontAwesomeIcon icon={faPlus} className="h-4 w-4" />
          <span className="font-semibold">Créer</span>
        </button>
      </div>

      {/* Tableau */}
      <div className="overflow-x-auto p-6">
        <table className="w-full border-collapse table-fixed">
          <thead>
            <tr className={`text-left text-sm ${
              color === "light" ? "bg-gray-50 text-gray-500" : "bg-slate-700 text-slate-200"
            }`}>
              <th className="px-4 py-3 font-medium w-[50px]">ID</th>
              <th className="px-4 py-3 font-medium w-[200px]">Sujet</th>
              <th className="px-4 py-3 font-medium w-[100px]">Type</th>
              <th className="px-4 py-3 font-medium w-[100px]">Statut</th>
              <th className="px-4 py-3 font-medium w-[120px]">Date</th>
              <th className="px-4 py-3 font-medium w-[300px]">Description</th>
              <th className="px-4 py-3 font-medium w-[160px]">Actions</th>
            </tr>
          </thead>
          <tbody>
            {filteredItems.map((item) => (
              <tr key={item.id} className="border-t hover:bg-gray-50 transition">
                <td className="px-4 py-3 text-sm font-medium">{item.id}</td>
                <td className="px-4 py-3 text-sm">{item.subject}</td>
                <td className="px-4 py-3 text-sm font-semibold text-red-600">{item.type}</td>
                <td className="px-4 py-3 text-sm">
                  <span className={`font-semibold ${item.status === 'En attente' ? 'text-red-600' : 'text-green-600'}`}>
                    {item.status}
                  </span>
                </td>
                <td className="px-4 py-3 text-sm">{item.date}</td>
                <td className="px-4 py-3 text-sm text-gray-600 truncate">{item.description}</td>
                <td className="px-4 py-3 text-sm">
                  <div className="flex gap-2">
                    <button onClick={() => handleDeleteConfirmation(item.id)} className="p-2 text-white bg-red-500 rounded-lg hover:bg-red-600 transition">
                      <FontAwesomeIcon icon={faTrash} />
                    </button>
                    <button onClick={() => handleEdit(item.id)} className="p-2 text-white bg-indigo-500 rounded-lg hover:bg-indigo-600 transition">
                      <FontAwesomeIcon icon={faPen} />
                    </button>
                    <button onClick={() => handleComplete(item.id)} className="p-2 text-white bg-green-500 rounded-lg hover:bg-green-600 transition">
                      <FontAwesomeIcon icon={faSquareCheck} />
                    </button>
                  </div>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

CardTable.defaultProps = {
  color: "light",
};

CardTable.propTypes = {
  color: PropTypes.oneOf(["light", "dark"]),
};
