import React, { useState } from "react";
import { FaFilter, FaEdit, FaTrash } from "react-icons/fa";
import { X } from "lucide-react";

export default function TechnicienTable({ color }) {
  const [techniciens, setTechniciens] = useState([
    {
      id: 1,
      nom: "Dubois",
      prenom: "Marc",
      email: "marc.dubois@example.com",
      specialite: "Informatique",
      role: "Technicien",
    },
    {
      id: 2,
      nom: "Leroy",
      prenom: "Julie",
      email: "julie.leroy@example.com",
      specialite: "R√©seaux",
      role: "Technicien",
    },
    {
      id: 3,
      nom: "Moreau",
      prenom: "Luc",
      email: "luc.moreau@example.com",
      specialite: "√âlectricit√©",
      role: "Technicien",
    },
    {
      id: 4,
      nom: "Roux",
      prenom: "√âmilie",
      email: "emilie.roux@example.com",
      specialite: "Maintenance",
      role: "Technicien",
    },
  ]);

  const [searchQuery, setSearchQuery] = useState("");
  const [filterSpecialite, setFilterSpecialite] = useState("");
  const [filterId, setFilterId] = useState("");

  const [modalOuvert, setModalOuvert] = useState(false);
  const [newTechnicien, setNewTechnicien] = useState({
    nom: "",
    prenom: "",
    email: "",
    specialite: "",
    role: "Technicien",
  });
  const [errors, setErrors] = useState({});

  const [editModalOuvert, setEditModalOuvert] = useState(false);
  const [editingTechnicien, setEditingTechnicien] = useState(null);
  const [editErrors, setEditErrors] = useState({});

  const [deleteModalOuvert, setDeleteModalOuvert] = useState(false);
  const [technicienIdToDelete, setTechnicienIdToDelete] = useState(null);

  const handleChange = (e) => {
    setNewTechnicien({ ...newTechnicien, [e.target.name]: e.target.value });
  };

  const handleEditChange = (e) => {
    setEditingTechnicien({ ...editingTechnicien, [e.target.name]: e.target.value });
  };

  const validateForm = (technicien) => {
    const errors = {};
    if (!technicien.nom.trim()) errors.nom = "Le nom est requis";
    if (!technicien.prenom.trim()) errors.prenom = "Le pr√©nom est requis";
    if (!technicien.email.trim()) errors.email = "L'email est requis";
    if (!technicien.specialite) errors.specialite = "La sp√©cialit√© est requise";
    return errors;
  };

  const handleCreateSubmit = () => {
    const errors = validateForm(newTechnicien);
    if (Object.keys(errors).length > 0) {
      setErrors(errors);
    } else {
      const newItem = {
        ...newTechnicien,
        id: techniciens.length + 1,
      };
      setTechniciens([...techniciens, newItem]);
      setModalOuvert(false);
      setNewTechnicien({ nom: "", prenom: "", email: "", specialite: "", role: "Technicien" });
    }
  };

  const handleEditSubmit = () => {
    const errors = validateForm(editingTechnicien);
    if (Object.keys(errors).length > 0) {
      setEditErrors(errors);
    } else {
      setTechniciens(techniciens.map(tech => 
        tech.id === editingTechnicien.id ? editingTechnicien : tech
      ));
      setEditModalOuvert(false);
    }
  };

  const confirmDelete = () => {
    setTechniciens(techniciens.filter(tech => tech.id !== technicienIdToDelete));
    setDeleteModalOuvert(false);
  };

  const filteredTechniciens = techniciens.filter(tech => {
    const matchesId = filterId ? tech.id.toString().includes(filterId) : true;
    const matchesSearch = searchQuery
      ? tech.nom.toLowerCase().includes(searchQuery.toLowerCase()) ||
        tech.prenom.toLowerCase().includes(searchQuery.toLowerCase()) ||
        tech.specialite.toLowerCase().includes(searchQuery.toLowerCase())
      : true;
    const matchesSpecialite = filterSpecialite ? tech.specialite === filterSpecialite : true;

    return matchesId && matchesSearch && matchesSpecialite;
  });

  const specialiteStyle = (specialite) => ({
    padding: "0.25rem 0.5rem",
    borderRadius: "9999px",
    fontSize: "0.75rem",
    fontWeight: "500",
    backgroundColor:
      specialite === "Informatique" ? "#dbeafe" :
      specialite === "R√©seaux" ? "#dcfce7" :
      specialite === "√âlectricit√©" ? "#fef3c7" : "#fee2e2",
    color:
      specialite === "Informatique" ? "#1d4ed8" :
      specialite === "R√©seaux" ? "#15803d" :
      specialite === "√âlectricit√©" ? "#b45309" : "#b91c1c",
  });

  return (
    <div className={`relative mx-auto max-w-screen-xl flex flex-col min-w-0 rounded-lg shadow-lg mb-10 ${
      color === "light" ? "bg-white" : "bg-slate-800 text-white"
    }`}>
      <div className="px-6 pt-6 border-b-2 border-gray-300">
        <h1 className={`text-2xl font-bold text-center ${
          color === "light" ? "text-gray-800" : "text-white"
        }`}>Gestion des Techniciens</h1>
      </div>

      <div className="flex justify-between px-6 pt-6 pb-4 items-center gap-4">
        <input
          type="text"
          placeholder="üîç Rechercher par ID..."
          value={filterId}
          onChange={(e) => setFilterId(e.target.value)}
          className="w-full sm:w-64 px-4 py-2 border rounded-lg shadow-sm text-gray-700 focus:outline-none focus:ring-2 focus:ring-blue-500 mr-4"
        />
        <div className="flex gap-4 items-center">
          <FaFilter className="text-gray-700 text-xl mr-2" />
          <select
            value={filterSpecialite}
            onChange={(e) => setFilterSpecialite(e.target.value)}
            className="w-50 sm:w-64 border rounded-lg shadow-sm text-gray-700 focus:outline-none focus:ring-2 focus:ring-blue-500 mr-4"
          >
            <option value="">Toutes les sp√©cialit√©s</option>
            <option value="Informatique">Informatique</option>
            <option value="R√©seaux">R√©seaux</option>
            <option value="√âlectricit√©">√âlectricit√©</option>
            <option value="Maintenance">Maintenance</option>
          </select>

          <button 
            className="bg-blue-500 hover:bg-blue-600 text-white px-6 py-2 rounded" 
            onClick={() => setModalOuvert(true)}
          >
            + Ajouter
          </button>
        </div>
      </div>

      <div className="overflow-x-auto px-6 pt-4 pb-14 w-full">
        <table className="w-full border-collapse" style={{ tableLayout: 'fixed' }}>
          <thead>
            <tr className={`text-left ${
              color === "light" ? "bg-gray-50 text-gray-500" : "bg-slate-700 text-slate-200"
            }`}>
              <th className="px-6 py-4 font-medium w-[5%]">ID</th>
              <th className="px-6 py-4 font-medium w-[15%]">Nom</th>
              <th className="px-6 py-4 font-medium w-[15%]">Pr√©nom</th>
              <th className="px-6 py-4 font-medium w-[20%]">Email</th>
              <th className="px-6 py-4 font-medium w-[15%]">Sp√©cialit√©</th>
              <th className="px-6 py-4 font-medium w-[10%]">Actions</th>
            </tr>
          </thead>
          <tbody>
            {filteredTechniciens.map((tech) => (
              <tr 
                key={tech.id} 
                className={`border-t ${
                  color === "light" ? "hover:bg-gray-50" : "hover:bg-slate-700"
                } transition-colors`}
              >
                <td className="px-6 py-4 whitespace-nowrap">{tech.id}</td>
                <td className="px-6 py-4 whitespace-nowrap">{tech.nom}</td>
                <td className="px-6 py-4 whitespace-nowrap">{tech.prenom}</td>
                <td className="px-6 py-4 whitespace-nowrap">{tech.email}</td>
                <td className="px-6 py-4 whitespace-nowrap">
                  <span style={specialiteStyle(tech.specialite)}>{tech.specialite}</span>
                </td>
                <td className="px-6 py-4 whitespace-nowrap flex gap-2">
                  <button onClick={() => { setEditingTechnicien(tech); setEditModalOuvert(true); }} className="bg-blue-100 text-blue-600 p-2 rounded">
                    <FaEdit />
                  </button>
                  <button onClick={() => { setTechnicienIdToDelete(tech.id); setDeleteModalOuvert(true); }} className="bg-red-100 text-red-600 p-2 rounded">
                    <FaTrash />
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {/* Modal cr√©ation */}
      {modalOuvert && (
        <div className="fixed inset-0 bg-black bg-opacity-40 flex items-center justify-center">
          <div className="bg-white rounded p-6 w-96">
            <div className="flex justify-between items-center mb-4">
              <h2 className="text-xl font-bold">Ajouter Technicien</h2>
              <button onClick={() => setModalOuvert(false)}><X /></button>
            </div>

            <div className="space-y-4">
              <input name="nom" placeholder="Nom" value={newTechnicien.nom} onChange={handleChange} className="border p-2 rounded w-full" />
              {errors.nom && <p className="text-red-500 text-sm">{errors.nom}</p>}

              <input name="prenom" placeholder="Pr√©nom" value={newTechnicien.prenom} onChange={handleChange} className="border p-2 rounded w-full" />
              {errors.prenom && <p className="text-red-500 text-sm">{errors.prenom}</p>}

              <input name="email" type="email" placeholder="Email" value={newTechnicien.email} onChange={handleChange} className="border p-2 rounded w-full" />
              {errors.email && <p className="text-red-500 text-sm">{errors.email}</p>}

              <select name="specialite" value={newTechnicien.specialite} onChange={handleChange} className="border p-2 rounded w-full">
                <option value="">S√©lectionner sp√©cialit√©</option>
                <option value="Informatique">Informatique</option>
                <option value="R√©seaux">R√©seaux</option>
                <option value="√âlectricit√©">√âlectricit√©</option>
                <option value="Maintenance">Maintenance</option>
              </select>
              {errors.specialite && <p className="text-red-500 text-sm">{errors.specialite}</p>}
            </div>

            <div className="flex justify-end gap-2 mt-4">
              <button onClick={() => setModalOuvert(false)} className="bg-gray-200 px-4 py-2 rounded">Annuler</button>
              <button onClick={handleCreateSubmit} className="bg-blue-500 hover:bg-blue-600 text-white px-4 py-2 rounded">Enregistrer</button>
            </div>
          </div>
        </div>
      )}

      {/* Modal modification */}
      {editModalOuvert && editingTechnicien && (
        <div className="fixed inset-0 bg-black bg-opacity-40 flex items-center justify-center">
          <div className="bg-white rounded p-6 w-96">
            <div className="flex justify-between items-center mb-4">
              <h2 className="text-xl font-bold">Modifier Technicien</h2>
              <button onClick={() => setEditModalOuvert(false)}><X /></button>
            </div>

            <div className="space-y-4">
              <input name="nom" placeholder="Nom" value={editingTechnicien.nom} onChange={handleEditChange} className="border p-2 rounded w-full" />
              <input name="prenom" placeholder="Pr√©nom" value={editingTechnicien.prenom} onChange={handleEditChange} className="border p-2 rounded w-full" />
              <input name="email" type="email" placeholder="Email" value={editingTechnicien.email} onChange={handleEditChange} className="border p-2 rounded w-full" />
              <select name="specialite" value={editingTechnicien.specialite} onChange={handleEditChange} className="border p-2 rounded w-full">
                <option value="">S√©lectionner sp√©cialit√©</option>
                <option value="Informatique">Informatique</option>
                <option value="R√©seaux">R√©seaux</option>
                <option value="√âlectricit√©">√âlectricit√©</option>
                <option value="Maintenance">Maintenance</option>
              </select>
            </div>

            <div className="flex justify-end gap-2 mt-4">
              <button onClick={() => setEditModalOuvert(false)} className="bg-gray-200 px-4 py-2 rounded">Annuler</button>
              <button onClick={handleEditSubmit} className="bg-blue-500 hover:bg-blue-600 text-white px-4 py-2 rounded">Enregistrer</button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}
