import React, { useState } from "react";
import PropTypes from "prop-types";

export default function CardTable({ color }) {
  const [items, setItems] = useState([
    { 
      id: 1, 
      subject: 'Problème d\'accès à l\'internet', 
      type: 'Urgent', 
      status: 'En attente', 
      date: '2025-03-29', 
      description: 'Le site intranet était inaccessible depuis plusieurs jours.' 
    },
    { 
      id: 2, 
      subject: 'Mise à jour logiciel', 
      type: 'Normal', 
      status: 'Terminé', 
      date: '2025-04-15', 
      description: 'Mise à jour du logiciel de gestion RH.' 
    }
  ]);
  const [searchQuery, setSearchQuery] = useState('');

  const filteredItems = [...items].filter(item =>
    item.subject.toLowerCase().includes(searchQuery.toLowerCase()) ||
    item.description.toLowerCase().includes(searchQuery.toLowerCase())
  );

  return (
    <div className={`relative mx-auto max-w-screen-xl flex flex-col min-w-0 rounded-lg shadow-lg mb-10 ${
      color === "light" ? "bg-white" : "bg-slate-800 text-white"
    }`}>
      <div className="px-6 pt-6 border-b-2 border-gray-300">
        <h1 className={`text-2xl font-bold text-center ${
          color === "light" ? "text-gray-800" : "text-white"
        }`}>
          Liste des tickets
        </h1>
      </div>

      <div className="flex justify-start px-6 pt-6 pb-4">
        <input
          type="text"
          placeholder="🔍 Rechercher un ticket..."
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
          className="w-full sm:w-64 px-4 py-2 border rounded-lg shadow-sm text-gray-700 focus:outline-none focus:ring-2 focus:ring-blue-500"
        />
      </div>

      <div className="overflow-x-auto px-6 pt-4 pb-14">
        <table className="w-full border-collapse text-sm">
          <thead>
            <tr className={`text-left ${
              color === "light"
                ? "bg-gray-50 text-gray-500"
                : "bg-slate-700 text-slate-200"
            }`}>
              <th className="px-6 py-4 font-medium min-w-[100px]">ID</th>
              <th className="px-6 py-4 font-medium min-w-[200px]">Sujet</th>
              <th className="px-6 py-4 font-medium min-w-[120px]">Type</th>
              <th className="px-6 py-4 font-medium min-w-[120px]">Statut</th>
              <th className="px-6 py-4 font-medium min-w-[120px]">Date</th>
              <th className="px-6 py-4 font-medium min-w-[300px]">Description</th>
              <th className="px-6 py-4 font-medium min-w-[200px]">Actions</th>
            </tr>
          </thead>
          <tbody>
            {filteredItems.map((item) => (
              <tr
                key={item.id}
                className="border-t hover:bg-gray-50 transition-colors"
              >
                <td className="px-6 py-4">{item.id}</td>
                <td className="px-6 py-4">{item.subject}</td>
                <td className="px-6 py-4 font-semibold text-red-600">{item.type}</td>
                <td className="px-6 py-4">
                  <div className="inline-flex items-center gap-2">
                    <div className={`w-2 h-2 rounded-full ${
                      item.status === 'En attente' ? 'bg-orange-500' : 'bg-green-500'
                    }`}></div>
                    <span className={`border-b-2 ${
                      item.status === 'En attente' 
                        ? 'border-orange-300 text-orange-700' 
                        : 'border-green-300 text-green-700'
                    }`}>
                      {item.status}
                    </span>
                  </div>
                </td>
                <td className="px-6 py-4">{item.date}</td>
                <td className="px-6 py-4 text-gray-600 truncate max-w-[300px]">{item.description}</td>
                <td className="px-6 py-4">
                  {/* Colonne Actions vide */}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

CardTable.defaultProps = {
  color: "light",
};

CardTable.propTypes = {
  color: PropTypes.oneOf(["light", "dark"]),
};
