import React, { useState, useEffect } from "react";
import IndexNavbar from "components/Navbars/IndexNavbar.js";
import Footerr from "components/Footers/Footerr";

// --- STYLES EN HAUT ---
const container = {
  flex: 1,
  display: 'flex',
  alignItems: 'center',
  justifyContent: 'center',
  padding: '2rem',
  backgroundColor: '#f4f6f8',
};

const contentWrapper = {
  width: '100%',
  maxWidth: '800px',
};

const bannerImage = {
  width: '100%',
  maxHeight: '200px',
  borderRadius: '12px',
  objectFit: 'cover',
  marginBottom: '1rem',
  boxShadow: '0 4px 10px rgba(0,0,0,0.1)'
};

const title = {
  color: '#1a237e',
  fontSize: '2.5rem',
  textAlign: 'center',
  marginBottom: '1rem',
  marginTop: '4rem',
  fontWeight: 'bold',
  textShadow: '1px 1px 2px rgba(0, 0, 0, 0.1)',
  borderBottom: '3px solid #c5cae9',
  display: 'inline-block',
  paddingBottom: '0.5rem',
  letterSpacing: '1px'
};

const subtitle = {
  textAlign: 'center',
  color: '#555',
  marginBottom: '2rem'
};

const ticketGrid = {
  display: 'grid',
  gap: '2rem',
  gridTemplateColumns: '1fr'
};

const ticketList = {
  backgroundColor: '#f8f9fa',
  borderRadius: '8px',
  padding: '1rem'
};

const ticketItem = {
  backgroundColor: 'white',
  borderRadius: '6px',
  padding: '1rem',
  marginBottom: '1rem',
  cursor: 'pointer',
  border: '1px solid #eee',
  transition: 'all 0.3s',
};

const ticketHeader = {
  display: 'flex',
  justifyContent: 'space-between',
  alignItems: 'center',
  marginBottom: '0.5rem'
};

const ticketId = {
  color: '#666',
  fontSize: '0.9rem'
};

const status = (statut) => ({
  padding: '0.3rem 0.8rem',
  borderRadius: '4px',
  fontSize: '0.85rem',
  backgroundColor: {
    'Ouvert': '#ffebee',
    'En cours': '#e3f2fd',
    'Résolu': '#e8f5e9'
  }[statut],
  color: {
    'Ouvert': '#c62828',
    'En cours': '#1565c0',
    'Résolu': '#2e7d32'
  }[statut],
  fontWeight: 'bold'
});

const ticketTitle = {
  fontSize: '1.1rem',
  color: '#333',
  margin: '0 0 0.5rem 0'
};

const ticketDate = {
  color: '#999',
  fontSize: '0.85rem',
  margin: '0'
};

const detailsPanel = {
  backgroundColor: 'white',
  borderRadius: '8px',
  padding: '1.5rem',
  border: '1px solid #eee'
};

const detailsHeader = {
  display: 'flex',
  justifyContent: 'space-between',
  alignItems: 'center',
  marginBottom: '1.5rem'
};

const detailsTitle = {
  fontSize: '1.5rem',
  color: '#1a237e',
  margin: '0'
};

const closeButton = {
  background: 'none',
  border: 'none',
  fontSize: '1.5rem',
  color: '#666',
  cursor: 'pointer',
  padding: '0 0.5rem'
};

const detailsContent = {
  lineHeight: '1.6'
};

const detailRow = {
  display: 'flex',
  justifyContent: 'space-between',
  marginBottom: '1rem',
  paddingBottom: '1rem',
  borderBottom: '1px solid #eee'
};

const detailLabel = {
  color: '#666',
  fontWeight: 'bold',
  marginRight: '1rem'
};

const detailDescription = {
  marginTop: '2rem'
};

// --- COMPOSANT PRINCIPAL ---
function MyTickets() {
  const [selectedTicket, setSelectedTicket] = useState(null);

  useEffect(() => {
    document.body.style.margin = "0";
    document.body.style.minHeight = "100vh";
    document.body.style.display = "flex";
    document.body.style.flexDirection = "column";
  }, []);

  const tickets = [
    {
      id: 45612,
      sujet: "Carte bloquée à l'étranger",
      departement: "Cartes Bancaires",
      statut: "En cours",
      date: "15/03/2024",
      description: "Ma carte Visa Premier est bloquée lors de mes déplacements en Australie."
    },
    {
      id: 45873,
      sujet: "Virement international urgent",
      departement: "Opérations",
      statut: "Ouvert",
      date: "14/03/2024",
      description: "Demande de virement urgent vers compte HSBC Hong Kong."
    },
    {
      id: 45901,
      sujet: "Problème accès mobile banking",
      departement: "Digital",
      statut: "Résolu",
      date: "13/03/2024",
      description: "Impossible de me connecter à l'application mobile depuis 2 jours."
    },
    {
      id: 46022,
      sujet: "Demande de relevé papier",
      departement: "Comptes",
      statut: "Ouvert",
      date: "12/03/2024",
      description: "Reçu un relevé incomplet pour le mois de février."
    },
    {
      id: 46105,
      sujet: "Question sur prêt immobilier",
      departement: "Crédits",
      statut: "En cours",
      date: "11/03/2024",
      description: "Clarification nécessaire sur les taux d'intérêt proposés."
    }
  ];

  return (
    <>
      <IndexNavbar />

      <div style={container}>
        <div style={contentWrapper}>
          <img 
            src="/images/support-banner.jpg" 
            alt="Bannière Support"
            style={bannerImage}
          />

          <h1 style={title}>Espace Support Client</h1>
          <p style={subtitle}>Consultez vos tickets et suivez leur avancement</p>

          <div style={ticketGrid}>
            <div style={ticketList}>
              {tickets.map((ticket) => (
                <div 
                  key={ticket.id}
                  style={ticketItem} 
                  onClick={() => setSelectedTicket(ticket)}
                >
                  <div style={ticketHeader}>
                    <span style={ticketId}>#{ticket.id}</span>
                    <span style={status(ticket.statut)}>{ticket.statut}</span>
                  </div>
                  <h3 style={ticketTitle}>{ticket.sujet}</h3>
                  <p style={ticketDate}>{ticket.date}</p>
                </div>
              ))}
            </div>

            {selectedTicket && (
              <div style={detailsPanel}>
                <div style={detailsHeader}>
                  <h2 style={detailsTitle}>Détails du ticket</h2>
                  <button 
                    style={closeButton}
                    onClick={() => setSelectedTicket(null)}
                  >
                    ×
                  </button>
                </div>
                <div style={detailsContent}>
                  <div style={detailRow}>
                    <span style={detailLabel}>Numéro :</span>
                    <span>#{selectedTicket.id}</span>
                  </div>
                  <div style={detailRow}>
                    <span style={detailLabel}>Statut :</span>
                    <span style={status(selectedTicket.statut)}>
                      {selectedTicket.statut}
                    </span>
                  </div>
                  <div style={detailRow}>
                    <span style={detailLabel}>Date :</span>
                    <span>{selectedTicket.date}</span>
                  </div>
                  <div style={detailDescription}>
                    <p style={detailLabel}>Description :</p>
                    <p>{selectedTicket.description}</p>
                  </div>
                </div>
              </div>
            )}
          </div>
        </div>
      </div>

      <Footerr />
    </>
  );
}

export default MyTickets;
