import React, { useState } from "react";

const UserTable = () => {
  const [users, setUsers] = useState([
    {
      id: "1",
      nom: "Rayen",
      email: "rayen@example.com",
      tel: "12345678",
      role: "Admin",
      statut: "Actif",
      dateAjout: "2024-04-01",
    },
    {
      id: "2",
      nom: "Nour",
      email: "nour@example.com",
      tel: "98765432",
      role: "User",
      statut: "Inactif",
      dateAjout: "2024-04-15",
    },
  ]);

  const [editingUserId, setEditingUserId] = useState(null);
  const [formData, setFormData] = useState({
    id: "",
    nom: "",
    email: "",
    tel: "",
    role: "",
    statut: "",
    dateAjout: "",
  });

  const handleEditClick = (user) => {
    setEditingUserId(user.id);
    setFormData(user);
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleSave = () => {
    const updatedUsers = users.map((u) =>
      u.id === editingUserId ? formData : u
    );
    setUsers(updatedUsers);
    setEditingUserId(null);
  };

  return (
    <div style={styles.container}>
      <h2 style={styles.title}>Liste des utilisateurs</h2>
      <table style={styles.table}>
        <thead>
          <tr>
            <th style={styles.th}>ID</th>
            <th style={styles.th}>Nom</th>
            <th style={styles.th}>Email</th>
            <th style={styles.th}>Téléphone</th>
            <th style={styles.th}>Rôle</th>
            <th style={styles.th}>Statut</th>
            <th style={styles.th}>Date d'ajout</th>
            <th style={styles.th}>Actions</th>
          </tr>
        </thead>
        <tbody>
          {users.map((user) => (
            <tr key={user.id}>
              <td style={styles.td}>{user.id}</td>
              <td style={styles.td}>{user.nom}</td>
              <td style={styles.td}>{user.email}</td>
              <td style={styles.td}>{user.tel}</td>
              <td style={styles.td}>{user.role}</td>
              <td style={styles.td}>{user.statut}</td>
              <td style={styles.td}>{user.dateAjout}</td>
              <td style={styles.td}>
                <button
                  style={styles.button}
                  onClick={() => handleEditClick(user)}
                >
                  Modifier
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {editingUserId && (
        <div style={styles.formContainer}>
          <h3 style={styles.subtitle}>Modifier l'utilisateur</h3>
          <form onSubmit={(e) => e.preventDefault()}>
            <label style={styles.label}>ID:</label>
            <input
              style={styles.input}
              name="id"
              value={formData.id}
              disabled
            />

            <label style={styles.label}>Nom:</label>
            <input
              style={styles.input}
              name="nom"
              value={formData.nom}
              onChange={handleInputChange}
            />

            <label style={styles.label}>Email:</label>
            <input
              style={styles.input}
              name="email"
              type="email"
              value={formData.email}
              onChange={handleInputChange}
            />

            <label style={styles.label}>Téléphone:</label>
            <input
              style={styles.input}
              name="tel"
              value={formData.tel}
              onChange={handleInputChange}
            />

            <label style={styles.label}>Rôle:</label>
            <input
              style={styles.input}
              name="role"
              value={formData.role}
              onChange={handleInputChange}
            />

            <label style={styles.label}>Statut:</label>
            <input
              style={styles.input}
              name="statut"
              value={formData.statut}
              onChange={handleInputChange}
            />

            <label style={styles.label}>Date d'ajout:</label>
            <input
              style={styles.input}
              name="dateAjout"
              value={formData.dateAjout}
              onChange={handleInputChange}
            />

            <button style={styles.saveButton} onClick={handleSave}>
              Enregistrer
            </button>
          </form>
        </div>
      )}
    </div>
  );
};

// CSS inline injecté
const styles = {
  container: {
    padding: "20px",
    fontFamily: "Arial",
  },
  title: {
    textAlign: "center",
    marginBottom: "20px",
  },
  table: {
    width: "100%",
    borderCollapse: "collapse",
    marginBottom: "20px",
  },
  th: {
    border: "1px solid #ddd",
    padding: "10px",
    backgroundColor: "#f0f0f0",
  },
  td: {
    border: "1px solid #ddd",
    padding: "10px",
    textAlign: "center",
  },
  button: {
    padding: "6px 12px",
    backgroundColor: "#007bff",
    color: "white",
    border: "none",
    borderRadius: "4px",
    cursor: "pointer",
  },
  formContainer: {
    backgroundColor: "#f9f9f9",
    border: "1px solid #ccc",
    padding: "20px",
    borderRadius: "8px",
    maxWidth: "600px",
    margin: "0 auto",
  },
  subtitle: {
    textAlign: "center",
    marginBottom: "15px",
  },
  label: {
    display: "block",
    marginTop: "10px",
    marginBottom: "5px",
    fontWeight: "bold",
  },
  input: {
    width: "100%",
    padding: "8px",
    marginBottom: "10px",
    borderRadius: "4px",
    border: "1px solid #ccc",
  },
  saveButton: {
    backgroundColor: "#28a745",
    color: "#fff",
    padding: "10px 20px",
    border: "none",
    borderRadius: "5px",
    cursor: "pointer",
  },
};

export default UserTable;
