import React, { useEffect, useState } from "react";
import axios from "axios";

export default function CardStats() {
  const [stats, setStats] = useState(null);

  useEffect(() => {
    axios.get("/stats") // change si besoin (localhost:5000/stats)
      .then((res) => setStats(res.data))
      .catch((err) => console.error("Erreur chargement stats:", err));
  }, []);

  if (!stats) return <div>Chargement des statistiques...</div>;

  const cards = [
    {
      label: "UTILISATEURS",
      value: stats.utilisateurs,
      icon: "fas fa-users",
      color: "bg-lightBlue-500",
    },
    {
      label: "TECHNICIENS",
      value: stats.techniciens,
      icon: "fas fa-tools",
      color: "bg-orange-500",
    },
    {
      label: "ADMINS",
      value: stats.admins,
      icon: "fas fa-user-shield",
      color: "bg-red-500",
    },
  ];

  return (
    <div className="flex flex-wrap justify-center gap-6">
      {cards.map((card, index) => (
        <div
          key={index}
          className="w-full sm:w-1/2 md:w-1/3 xl:w-1/4 p-4 bg-white rounded-lg shadow-lg flex items-center"
        >
          <div
            className={`text-white text-3xl p-4 rounded-full mr-4 ${card.color}`}
          >
            <i className={card.icon}></i>
          </div>
          <div>
            <p className="text-sm text-gray-500 uppercase font-bold">
              {card.label}
            </p>
            <h3 className="text-2xl font-semibold text-gray-800">
              {card.value}
            </h3>
            <p className="text-green-500 text-sm mt-1">+100% Nombre total</p>
          </div>
        </div>
      ))}
    </div>
  );
}
