import React, { useEffect, useState } from 'react';
import { Calendar, dateFnsLocalizer } from 'react-big-calendar';
import format from 'date-fns/format';
import parse from 'date-fns/parse';
import startOfWeek from 'date-fns/startOfWeek';
import getDay from 'date-fns/getDay';
import fr from 'date-fns/locale/fr';
import 'react-big-calendar/lib/css/react-big-calendar.css';

const locales = { 'fr': fr };
const localizer = dateFnsLocalizer({
  format,
  parse,
  startOfWeek,
  getDay,
  locales,
});

const Calender = () => {
  const [events, setEvents] = useState([]);

  useEffect(() => {
    const fetchTickets = async () => {
      try {
        const response = await fetch('/alltickets');
        if (!response.ok) throw new Error('Erreur rÃ©seau');
        
        const tickets = await response.json();
        
        const formattedEvents = tickets.map(ticket => ({
          title: ticket.sujet,
          start: new Date(ticket.createdAt),
          end: new Date(ticket.createdAt),
          statut: ticket.statut,
          description: ticket.description,
          type: ticket.type,
          urgence: ticket.urgence
        }));
        
        setEvents(formattedEvents);
      } catch (error) {
        console.error('Erreur:', error);
      }
    };

    fetchTickets();
  }, []);

  const eventStyleGetter = (event) => {
    let backgroundColor = '#3174ad';
    if (event.statut === 'ouvert') backgroundColor = '#e74c3c';
    if (event.statut === 'en cours') backgroundColor = '#f39c12';
    if (event.statut === 'fermÃ©') backgroundColor = '#27ae60';

    return {
      style: {
        backgroundColor,
        color: 'white',
        borderRadius: '5px',
        border: 'none',
        padding: '2px 5px',
        fontSize: '0.8em'
      }
    };
  };


  return (
<div
  style={{
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'flex-start', // Aligne en haut
    height: '100vh', // Hauteur totale de la fenÃªtre
    backgroundColor: '#fff',
    borderRadius: '10px',
    boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
    padding: '20px',
    marginTop: '0', // Pas de marge supplÃ©mentaire
  }}
>
<h2 style={{ fontSize: '24px', color: '#2c3e50', marginBottom: '1rem' }}>ðŸ“… Calendrier des Tickets</h2>
<div
    style={{
      backgroundColor: '#fff',
      borderRadius: '10px',
      boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
      padding: '20px',
    }}
  >
        <Calendar
          localizer={localizer}
          events={events}
          startAccessor="start"
          endAccessor="end"
          style={{ height: 600 }}
          tooltipAccessor={event => `${event.title} (${event.type})`}
          eventPropGetter={eventStyleGetter}
        />
      </div>
    </div>
  );
};

export default Calender;
