import React, { useEffect, useRef } from 'react';
import * as powerbi from 'powerbi-client';

const Dashboard = () => {
  const embedContainer = useRef(null);

  useEffect(() => {
    // Configuration de l'embed Power BI
    const embedConfig = {
      type: 'report',  // Type d'élément à afficher (ici un rapport)
      id: 'f7a7df31-5087-43e4-aa18-2db86a75abde',  // Ton reportId
      embedUrl: 'https://app.powerbi.com/groups/13721a56-4896-485e-b3c3-d9a70c3680fb/reports/f7a7df31-5087-43e4-aa18-2db86a75abde/8e9eb7efae15c66beeeb?experience=power-bi', // L'URL complète
      accessToken: 'TON_ACCESS_TOKEN',  // Remplace avec ton token d'accès
      tokenType: powerbi.models.TokenType.Embed,
      settings: {
        panes: {
          filters: { visible: false },
          pageNavigation: { visible: true }
        }
      }
    };

    // Intégration du rapport Power BI
    const embed = new powerbi.Embed(embedContainer.current, embedConfig);

    return () => {
      // Nettoyage si le composant est démonté
      embed.off("loaded");
    };
  }, []);

  return (
    <div>
      <h2>Mon Dashboard Power BI</h2>
      <div ref={embedContainer} style={{ height: '600px', width: '100%' }} />
    </div>
  );
};

export default Dashboard;
