import IndexNavbar from "components/Navbars/IndexNavbar.js";
import Footer from "components/Footers/Footerr"; // Importez votre Footer ici

function NewTicketForm() {
  return (
    <>
      <IndexNavbar /> {/* Navbar en haut */}

      <div style={containerStyle}>
        <div style={headerSection}>
          <h1 style={titleStyle}>New ticket</h1>
          <button style={returnButtonStyle}>
            ‚Üê Return to tickets list
          </button>
        </div>

        <div style={formSection}>
          <div style={inputGroup}>
            <label style={labelStyle}>Subject</label>
            <input 
              type="text" 
              placeholder="My Website Cannot be Opened." 
              style={inputStyle}
            />
          </div>

          <div style={inputGroup}>
            <label style={labelStyle}>Department</label>
            <select style={selectStyle}>
              <option>IT Department</option>
            </select>
          </div>

          <div style={inputGroup}>
            <label style={labelStyle}>Ticket body</label>
            <textarea 
              rows="5" 
              placeholder="Please help! I" 
              style={textareaStyle}
            />
          </div>

          <div style={createButtonContainer}>
            <button style={createButtonStyle}>Create ticket</button>
          </div>
        </div>
      </div>

      <Footer /> {/* Footer en bas */}
    </>
  );
}

// Styles
const containerStyle = {
  fontFamily: "'Segoe UI', sans-serif",
  maxWidth: '1000px',
  minHeight: '100vh',
  margin: '0 auto',
  padding: '2rem 3rem',
  display: 'flex',
  flexDirection: 'column',
  justifyContent: 'center'
};

const headerSection = {
  display: 'flex',
  justifyContent: 'space-between',
  alignItems: 'center',
  marginBottom: '2rem'
};

const titleStyle = {
  fontSize: '2.2rem',
  fontWeight: '600',
  color: '#1a1a1a'
};

const returnButtonStyle = {
  padding: '0.8rem 1.5rem',
  backgroundColor: '#2c7be5',
  color: 'white',
  border: 'none',
  borderRadius: '6px',
  cursor: 'pointer',
  fontWeight: '600',
  fontSize: '1rem',
  transition: 'all 0.3s ease',
};

const formSection = {
  backgroundColor: '#f8f9fa',
  borderRadius: '12px',
  padding: '2.5rem',
  boxShadow: '0 2px 8px rgba(0,0,0,0.1)'
};

const inputGroup = {
  marginBottom: '1.5rem'
};

const labelStyle = {
  display: 'block',
  fontWeight: '500',
  marginBottom: '0.5rem',
  color: '#444'
};

const inputStyle = {
  width: '100%',
  padding: '0.8rem',
  border: '1px solid #ced4da',
  borderRadius: '4px',
  fontSize: '1rem'
};

const selectStyle = {
  ...inputStyle,
  backgroundColor: 'white',
  cursor: 'pointer'
};

const textareaStyle = {
  ...inputStyle,
  resize: 'vertical',
  minHeight: '100px'
};

const createButtonContainer = {
  display: 'flex',
  justifyContent: 'flex-end',
  marginTop: '2rem'
};

const createButtonStyle = {
  padding: '1rem 2rem',
  backgroundColor: '#22c55e',
  color: 'white',
  border: 'none',
  borderRadius: '6px',
  cursor: 'pointer',
  fontWeight: '600',
  fontSize: '1.1rem',
  transition: 'all 0.3s ease',
};

export default NewTicketForm;
