import React, { useState } from "react";
import PropTypes from "prop-types";
import { FaFilter } from "react-icons/fa";

export default function CardTable({ color }) {
  const [showForm, setShowForm] = useState(false);
  const [departmentName, setDepartmentName] = useState("");
  const [items] = useState([
    { 
      id: 1, 
      subject: 'Probl√®me d\'acc√®s √† l\'internet', 
      type: 'Urgent', 
      status: 'En attente', 
      date: '2025-03-29', 
      description: 'Le site intranet √©tait inaccessible depuis plusieurs jours.' 
    },
    { 
      id: 2, 
      subject: 'Mise √† jour logiciel', 
      type: 'Normal', 
      status: 'Termin√©', 
      date: '2025-04-15', 
      description: 'Mise √† jour du logiciel de gestion RH.' 
    }
  ]);

  const [searchQuery, setSearchQuery] = useState('');
  const [filterType, setFilterType] = useState('');
  const [filterId, setFilterId] = useState('');

  const filteredItems = items.filter(item => {
    const matchesId = filterId ? item.id.toString().includes(filterId) : true;
    const matchesSearch = searchQuery
      ? item.subject.toLowerCase().includes(searchQuery.toLowerCase()) ||
        item.description.toLowerCase().includes(searchQuery.toLowerCase())
      : true;
    const matchesType = filterType ? item.type === filterType : true;

    return matchesId && matchesSearch && matchesType;
  });

  const badgeStyle = {
    backgroundColor: '#e6fffa',
    color: '#047857',
    padding: '0.25rem 0.5rem',
    borderRadius: '9999px',
    fontSize: '0.75rem',
    fontWeight: '500',
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Ajouter ici la logique de soumission
    setShowForm(false);
    setDepartmentName("");
  };

  return (
    <div className={`relative mx-auto max-w-screen-xl flex flex-col min-w-0 rounded-lg shadow-lg mb-10 ${
      color === "light" ? "bg-white" : "bg-slate-800 text-white"
    }`}>
      
      {/* Formulaire modal */}
      {showForm && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white p-8 rounded-lg w-96 shadow-xl">
            <h2 className="text-2xl font-bold mb-6 text-gray-800">D√âPARTEMENT</h2>
            <form onSubmit={handleSubmit}>
              <div className="mb-6">
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  Ajouter un d√©partement
                </label>
                <input
                  type="text"
                  className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                  placeholder="Nom du d√©partement"
                  value={departmentName}
                  onChange={(e) => setDepartmentName(e.target.value)}
                  required
                />
              </div>
              <div className="flex justify-end gap-4">
                <button
                  type="button"
                  className="px-4 py-2 text-gray-600 hover:text-gray-800 font-medium"
                  onClick={() => setShowForm(false)}
                >
                  Annuler
                </button>
                <button
                  type="submit"
                  className="px-4 py-2 bg-blue-500 text-white rounded-lg hover:bg-blue-600 font-medium transition-colors"
                >
                  Ajouter
                </button>
              </div>
            </form>
          </div>
        </div>
      )}

      <style jsx>{`
        .gp-add-button {
          display: flex;
          align-items: center;
          gap: 0.5rem;
          background-color: #0ea5e9;
          color: white;
          padding: 0.55rem 2.5rem;
          border-radius: 0.375rem;
          font-weight: 500;
          transition: all 0.2s ease;
        }
        .gp-add-button:hover {
          background-color: #0284c7;
        }
      `}</style>

      <div className="px-6 pt-6 border-b-2 border-gray-300">
        <h1 className={`text-2xl font-bold text-center ${
          color === "light" ? "text-gray-800" : "text-white"
        }`}>
          Liste des tickets
        </h1>
      </div>

      <div className="flex justify-between px-6 pt-6 pb-4 items-center gap-4">
        <input
          type="text"
          placeholder="üîç Rechercher par ID..."
          value={filterId}
          onChange={(e) => setFilterId(e.target.value)}
          className="w-full sm:w-64 px-4 py-2 border rounded-lg shadow-sm text-gray-700 focus:outline-none focus:ring-2 focus:ring-blue-500 mr-4"  
        />

        <div className="flex gap-4 items-center">
          <FaFilter className="text-gray-700 text-xl mr-2" />
          
          <select
            value={filterType}
            onChange={(e) => setFilterType(e.target.value)}
            className="w-50 sm:w-64 border rounded-lg shadow-sm text-gray-700 focus:outline-none focus:ring-2 focus:ring-blue-500 mr-4"
          >
            <option value="">Filtrer par</option>
            <option value="Urgent">Urgent</option>
            <option value="Normal">Normal</option>
          </select>

          <button 
            className="gp-add-button flex items-center gap-2 text-white"
            onClick={() => setShowForm(true)}
          >
            <span>+</span>
            <span>Cr√©er</span>
          </button>
        </div>
      </div>

      <div className="overflow-x-auto px-6 pt-4 pb-14">
        <table className="w-full border-collapse">
          <thead>
            <tr className={`text-left ${
              color === "light" 
                ? "bg-gray-50 text-gray-500" 
                : "bg-slate-700 text-slate-200"
            }`}>
              <th className="px-6 py-4 font-medium">ID</th>
              <th className="px-6 py-4 font-medium">Sujet</th>
              <th className="px-6 py-4 font-medium">Type</th>
              <th className="px-6 py-4 font-medium">Statut</th>
              <th className="px-6 py-4 font-medium">Date</th>
              <th className="px-6 py-4 font-medium">Description</th>
              <th className="px-6 py-4 font-medium">Actions</th>
            </tr>
          </thead>
          
          <tbody>
            {filteredItems.map((item) => (
              <tr
                key={item.id}
                className={`border-t ${color === "light" ? "hover:bg-gray-50" : "hover:bg-slate-700"} transition-colors`}
              >
                <td className="px-6 py-4 align-middle">{item.id}</td>
                <td className="px-6 py-4 align-middle">{item.subject}</td>
                <td className="px-6 py-4 align-middle font-semibold text-red-600">
                  {item.type}
                </td>
                <td className="px-6 py-4 align-middle">
                  <span 
                    style={{
                      ...badgeStyle,
                      backgroundColor: item.status === 'En attente' ? '#e6fffa' : '#ffe6e6',
                      color: item.status === 'En attente' ? '#047857' : '#d32f2f',
                    }}
                  >
                    {item.status}
                  </span>
                </td>
                <td className="px-6 py-4 align-middle">{item.date}</td>
                <td className={`px-6 py-4 align-middle truncate max-w-[300px] ${
                  color === "light" ? "text-gray-600" : "text-slate-300"
                }`}>
                  {item.description}
                </td>
                <td className="px-6 py-4 align-middle">{/* Actions */}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

CardTable.defaultProps = {
  color: "light",
};

CardTable.propTypes = {
  color: PropTypes.oneOf(["light", "dark"]),
};
