import React, { useState, useEffect } from "react";
import axios from "axios";
import PropTypes from "prop-types";
import { FaFilter, FaEye, FaCheck, FaCheckDouble } from "react-icons/fa";
import { X } from "lucide-react";

export default function TicketTabletech({ color }) {
  const [items, setItems] = useState([]);
  const [searchQuery, setSearchQuery] = useState("");
  const [filterType, setFilterType] = useState("");
  const [filterId, setFilterId] = useState("");
  const [error, setError] = useState(null);
  const [showSuccessMessage, setShowSuccessMessage] = useState(false);

  // Ã‰tats crÃ©ation
  const [modalOuvert, setModalOuvert] = useState(false);
  const [newTicket, setNewTicket] = useState({
    subject: "",
    type: "",
    urgency: "",
    description: "",
  });
  const [errors, setErrors] = useState({});

  // Ã‰tats Ã©dition


  const [editErrors, setEditErrors] = useState({});

  // Ã‰tats suppression


  const handleTakeTicket = async (id) => {
    try {
      const token = localStorage.getItem("jwt_token");
      await axios.put(
        `/updateticket/${id}`,
        { statut: "en cours" },
        { headers: { Authorization: `Bearer ${token}` } }
      );
  
      setItems(prevItems =>
        prevItems.map(item =>
          item.id === id ? {
            ...item,
            statut: "en cours",
            status: "En cours"
          } : item
        )
      );
      setShowSuccessMessage(true);
      setTimeout(() => setShowSuccessMessage(false), 3000);
    } catch (error) {
      console.error("Erreur de prise de ticket:", error);
    }
  };
  
  const handleConfirmTicket = async (id) => {
    try {
      const token = localStorage.getItem("jwt_token");
      await axios.put(
        `/updateticket/${id}`,
        { statut: "fermÃ©" },
        { headers: { Authorization: `Bearer ${token}` } }
      );
  
      setItems(prevItems =>
        prevItems.map(item =>
          item.id === id ? {
            ...item,
            statut: "fermÃ©",
            status: "FermÃ©"
          } : item
        )
      );
  
      setShowSuccessMessage(true);
      setTimeout(() => setShowSuccessMessage(false), 3000);
    } catch (error) {
      console.error("Erreur de confirmation:", error);
    }
  };
  // Ã‰tats dÃ©tails
  const [detailsModalOpen, setDetailsModalOpen] = useState(false);
  const [selectedTicket, setSelectedTicket] = useState(null);

  useEffect(() => {
    const fetchTickets = async () => {
      try {
        const { data } = await axios.get('/alltickets');
        const formatted = data.map(t => {
          // Corrigez le format de date ici
          const [day, month, year] = t.date.split('/');
          const isoDate = `${year}-${month}-${day}`;
  
          return {
            id: t._id,
            surnom: t.createur?.surnom || 'Anonyme',
            email: t.createur?.email || 'N/A',
         
          
            urgency: t.urgence,
            
            description: t.description,
            status: t.statut.charAt(0).toUpperCase() + t.statut.slice(1),
            date: new Date(isoDate).toLocaleDateString('fr-FR', {
              day: '2-digit',
              month: '2-digit',
              year: 'numeric'
            }),
            
            subject: t.sujet,
            type: t.type,
            statut: t.statut
          };
        });
        setItems(formatted);
      } catch (error) {
        console.error("Erreur chargement:", error.response?.data);
      }
    };
    fetchTickets();
  }, [modalOuvert]);

  const handleChange = (e) => {
    setNewTicket({ ...newTicket, [e.target.name]: e.target.value });
  };



  const validateForm = (ticket) => { // Retirer le paramÃ¨tre isEdit
    const errors = {};
    
    if (!ticket.subject?.trim()) {
      errors.subject = "Le sujet est requis.";
    }
    
    if (!ticket.type) {
      errors.type = "Le type est requis.";
    }
    
    if (!ticket.urgency) {
      errors.urgency = "L'urgence est requise.";
    }
    
    if (!ticket.description?.trim()) {
      errors.description = "La description est requise.";
    }
    
    return errors;
  };

  const handleCreateSubmit = async () => {
    const errors = validateForm(newTicket);
    if (Object.keys(errors).length > 0) return setErrors(errors);
  
    try {
      // RÃ©cupÃ©ration du token JWT
      const token = localStorage.getItem("jwt_token");
      if (!token) {
        throw new Error("Authentication required");
      }
  
      // CrÃ©ation du ticket avec authentification
      const response = await axios.post('/addticket', {
        sujet: newTicket.subject,
        type: newTicket.type,
        urgence: newTicket.urgency,
        description: newTicket.description
      }, {
        headers: {
          Authorization: `Bearer ${token}`
        }
      });
  
      // Mise Ã  jour optimiste UI
      setItems(prev => [...prev, {
        id: response.data._id,
        subject: response.data.sujet,
        type: response.data.type,
        urgency: response.data.urgence,
        description: response.data.description,
        status: 'Ouvert',
        date: new Date(response.data.date).toLocaleDateString('fr-FR')
      }]);
  
      // Fermeture modal et reset form
      setModalOuvert(false);
      setNewTicket({ subject: "", type: "", urgency: "", description: "" });
  
      // Sauvegarde locale fallback
      const offlineTicket = {
        ...newTicket,
        id: response.data._id,
        date: new Date().toLocaleDateString('fr-FR'),
        statut: 'Ouvert'
      };
      
      const storedTickets = JSON.parse(localStorage.getItem("tickets")) || [];
      localStorage.setItem("tickets", JSON.stringify([...storedTickets, offlineTicket]));
  
    } catch (error) {
      console.error('Erreur crÃ©ation:', error);
      
      // Gestion erreur rÃ©seau/hors ligne
      if (!navigator.onLine || error.response?.status === 401) {
        const offlineTicket = {
          ...newTicket,
          id: `offline-${Date.now()}`,
          date: new Date().toLocaleDateString('fr-FR'),
          statut: 'En attente de synchronisation'
        };
  
        const storedTickets = JSON.parse(localStorage.getItem("tickets")) || [];
        localStorage.setItem("tickets", JSON.stringify([...storedTickets, offlineTicket]));
  
        setError(`Erreur rÃ©seau - Ticket sauvegardÃ© localement`);
      } else if (error.response?.data?.errors) {
        setErrors(error.response.data.errors);
      } else {
        setError(error.message);
      }
    }
  };



  const filteredItems = items.filter(item => {
   
    const matchesId = filterId ? item.id.toString().includes(filterId) : true;
    const matchesSearch = searchQuery
    ? item.surnom.toLowerCase().includes(searchQuery.toLowerCase()) ||
      item.email.toLowerCase().includes(searchQuery.toLowerCase())
    : true;
    const matchesUrgency = filterType ? item.urgency === filterType : true;

    return matchesId && matchesSearch && matchesUrgency;
  });

  const badgeStyle = (status) => ({
    padding: "0.25rem 0.5rem",
    borderRadius: "9999px",
    fontSize: "0.75rem",
    fontWeight: "500",
    backgroundColor:
      status === "Ouvert" ? "#fee2e2" :
      status === "En cours" ? "#ffedd5" : "#dcfce7",
    color:
      status === "Ouvert" ? "#dc2626" :
      status === "En cours" ? "#ea580c" : "#16a34a",
  });

  return (
    <div className={`relative mx-auto max-w-screen-xl flex flex-col min-w-0 rounded-lg shadow-lg mb-10 ${
      color === "light" ? "bg-white" : "bg-slate-800 text-white"
    }`}>
      
      <style jsx>{`
        .gp-action-icon {
          padding: 0.45rem;
          border-radius: 0.375rem;
          transition: all 0.2s ease;
          display: flex;
          align-items: center;
          justify-content: center;
        }
        .gp-view {
          background-color: #dbeafe;
          color: #2563eb;
        }
        .gp-view:hover {
          background-color: #bfdbfe;
        }
        .gp-edit {
          background-color: #e0f2fe;
          color: #0284c7;
        }
        .gp-edit:hover {
          background-color: #bae6fd;
        }
        .gp-delete {
          background-color: #fee2e2;
          color: #dc2626;
        }
        .gp-delete:hover {
          background-color: #fecaca;
        }
        .gp-add-button {
          display: flex;
          align-items: center;
          gap: 0.5rem;
          background-color: #0ea5e9;
          color: white;
          padding: 0.55rem 2.5rem;
          border-radius: 0.375rem;
          font-weight: 500;
          transition: all 0.2s ease;
        }
        .gp-add-button:hover {
          background-color: #0284c7;
        }
        .gp-modal-overlay {
          position: fixed;
          top: 0;
          left: 0;
          width: 100vw;
          height: 100vh;
          background-color: rgba(0, 0, 0, 0.6);
          display: flex;
          align-items: center;
          justify-content: center;
          z-index: 50;
        }
        .gp-modal-container {
          background-color: white;
          border-radius: 0.75rem;
          padding: 2rem;
          width: 90%;
          max-width: 500px;
          box-shadow: 0 10px 25px rgba(0, 0, 0, 0.2);
        }
        .gp-modal-header {
          display: flex;
          justify-content: space-between;
          align-items: center;
          margin-bottom: 1rem;
        }
        .gp-form-group {
          margin-bottom: 1.5rem;
        }
        .gp-form-input {
          width: 100%;
          padding: 0.5rem;
          border: 1px solid #ccc;
          border-radius: 0.375rem;
        }
        .gp-disabled-input {
          background-color: #f3f4f6;
          cursor: not-allowed;
        }
        .gp-readonly-text {
          padding: 0.5rem;
          background-color: #f3f4f6;
          border-radius: 0.375rem;
          display: block;
        }
        .gp-btn {
          padding: 0.5rem 1.25rem;
          border-radius: 0.375rem;
          font-weight: 600;
          cursor: pointer;
          transition: all 0.2s ease;
        }
        .gp-btn-save {
          background-color: #0ea5e9;
          color: white;
        }
        .gp-btn-save:hover {
          background-color: #0284c7;
        }
        .gp-btn-cancel {
          background-color: #f3f4f6;
          color: #374151;
        }
        .gp-btn-cancel:hover {
          background-color: #e5e7eb;
        }
        .gp-btn-danger {
          background-color: #dc2626;
          color: white;
        }
        .gp-btn-danger:hover {
          background-color: #b91c1c;
        }
        .gp-delete-modal-content {
