import React, { useState } from "react";
import PropTypes from "prop-types";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faTrash, faPen, faSquareCheck } from '@fortawesome/free-solid-svg-icons';

export default function CardTable({ color }) {
  // État pour gérer la suppression, l'édition et le statut
  const [items, setItems] = useState([
    { id: 1, subject: 'Problème d\'accès à l\'internet', status: 'En attente', date: '2025-03-29', description: 'Le site intranet était inaccessible depuis plusieurs jours.' }
  ]);

  // Fonction pour supprimer un ticket
  const handleDelete = (id) => {
    setItems(items.filter(item => item.id !== id));
  };

  // Fonction pour marquer comme terminé
  const handleComplete = (id) => {
    setItems(items.map(item =>
      item.id === id ? { ...item, status: 'Terminé' } : item
    ));
  };

  // Fonction pour éditer un ticket (ici on change juste le statut à titre d'exemple)
  const handleEdit = (id) => {
    setItems(items.map(item =>
      item.id === id ? { ...item, subject: 'Problème résolu', description: 'Le problème a été résolu avec succès.' } : item
    ));
  };

  return (
    <div
      className={
        "relative flex flex-col min-w-0 break-words w-full mb-6 shadow-lg rounded " +
        (color === "light" ? "bg-white" : "bg-lightBlue-900 text-white")
      }
    >
      {/* En-tête personnalisé */}
      <div className="block w-full overflow-x-auto">
        <table className="items-center w-full bg-transparent border-collapse">
          <thead>
            <tr>
              {["Id", "Sujet", "Statut", "Date", "Description", "Actions"].map((header, index) => (
                <th
                  key={index}
                  className={
                    "px-6 py-3 border border-solid text-xs uppercase whitespace-nowrap font-semibold text-left " +
                    (color === "light"
                      ? "bg-blueGray-50 text-blueGray-500 border-blueGray-100"
                      : "bg-lightBlue-800 text-lightBlue-300 border-lightBlue-700")
                  }
                >
                  {header}
                </th>
              ))}
            </tr>
          </thead>
          <tbody>
            {items.map((item) => (
              <tr key={item.id} className="hover:bg-gray-100 transition-colors duration-200">
                <td className="border-t p-4 text-xs whitespace-nowrap">
                  <strong>{item.id}</strong>
                </td>
                <td className="border-t p-4 text-xs whitespace-nowrap">
                  {item.subject}
                </td>
                <td className="border-t p-4 text-xs whitespace-nowrap">
                  <span className="flex items-center">
                    <i className={`fas fa-circle ${item.status === 'En attente' ? 'text-orange-500' : 'text-green-500'} mr-2`}></i> {item.status}
                  </span>
                </td>
                <td className="border-t p-4 text-xs whitespace-nowrap">
                  {item.date}
                </td>
                <td className="border-t p-4 text-xs whitespace-nowrap">
                  {item.description}
                </td>
                <td className="border-t p-4 text-xs whitespace-nowrap">
                  <button
                    className="bg-red-500 hover:bg-red-600 text-white text-xs font-bold uppercase px-3 py-2 rounded shadow transition-all duration-150 mr-2"
                    onClick={() => handleDelete(item.id)}
                  >
                    <FontAwesomeIcon icon={faTrash} />
                  </button>
                  <button
                    className="bg-indigo-500 hover:bg-indigo-600 text-white text-xs font-bold uppercase px-3 py-2 rounded shadow transition-all duration-150 mr-2"
                    onClick={() => handleEdit(item.id)}
                  >
                    <FontAwesomeIcon icon={faPen} />
                  </button>
                  <button
                    className="bg-indigo-500 hover:bg-green-600 text-white text-xs font-bold uppercase px-3 py-2 rounded shadow transition-all duration-150"
                    onClick={() => handleComplete(item.id)}
                  >
                    <FontAwesomeIcon icon={faSquareCheck} />
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

CardTable.defaultProps = {
  color: "light",
};

CardTable.propTypes = {
  color: PropTypes.oneOf(["light", "dark"]),
};
