import React, { useState } from "react";
import IndexNavbar from "components/Navbars/IndexNavbar.js";
import Footerr from "components/Footers/Footerr";
import { Link } from "react-router-dom";

function NewTicketForm() {
  const [formData, setFormData] = useState({
    sujet: "",
    type: "",
    urgence: "",
    description: "",
  });
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [error, setError] = useState(null);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsSubmitting(true);
    setError(null);

    try {
      const newTicket = {
        ...formData,
        id: Date.now(),
        date: new Date().toLocaleDateString(),
        statut: "Ouvert",
      };

      const response = await fetch("/addticket", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(formData),
      });

      if (!response.ok) {
        throw new Error(`Erreur HTTP ${response.status}`);
      }

      const storedTickets = JSON.parse(localStorage.getItem("tickets")) || [];
      storedTickets.push(newTicket);
      localStorage.setItem("tickets", JSON.stringify(storedTickets));

      window.location.href = "/MyTickets";
    } catch (error) {
      const storedTickets = JSON.parse(localStorage.getItem("tickets")) || [];
      storedTickets.push({
        ...formData,
        id: Date.now(),
        date: new Date().toLocaleDateString(),
        statut: "Erreur d'envoi - Sauvegardé localement",
      });
      localStorage.setItem("tickets", JSON.stringify(storedTickets));

      setError(
        `Échec de l'envoi au serveur. Ticket sauvegardé localement. ${error.message}`
      );
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <>
      <IndexNavbar />
      <div className="bg-[#edf2f7] min-h-screen py-12 px-4 sm:px-6 lg:px-8">
        <div className="max-w-4xl mx-auto bg-white p-10 rounded-2xl shadow-lg flex flex-col md:flex-row gap-6">
          <div className="flex-1">
            <h2 className="text-2xl font-bold text-blue-900 mb-2">Créer un ticket</h2>
            <p className="text-gray-600 mb-4">
              Veuillez remplir ce formulaire pour signaler un incident ou une demande d'assistance.
            </p>

            {error && (
              <div className="bg-red-100 text-red-700 p-3 rounded mb-4 border border-red-300">
                {error}
              </div>
            )}

            <form onSubmit={handleSubmit} className="space-y-4">
              <div>
                <label className="block text-sm font-medium text-blue-900">Sujet</label>
                <input
                  type="text"
                  name="sujet"
                  value={formData.sujet}
                  onChange={handleChange}
                  className="mt-1 block w-full border border-gray-300 rounded-md shadow-sm p-2"
                  required
                />
              </div>
              <div>
                <label className="block text-sm font-medium text-blue-900">Type</label>
                <select
                  name="type"
                  value={formData.type}
                  onChange={handleChange}
                  className="mt-1 block w-full border border-gray-300 rounded-md shadow-sm p-2"
                  required
                >
                  <option value="">-- Sélectionner --</option>
                  <option value="IT">IT - Informatique</option>
                  <option value="RH">RH - Ressources Humaines</option>
                  <option value="Comptabilité">Comptabilité</option>
                </select>
              </div>
              <div>
                <label className="block text-sm font-medium text-blue-900">Urgence</label>
                <select
                  name="urgence"
                  value={formData.urgence}
                  onChange={handleChange}
                  className="mt-1 block w-full border border-gray-300 rounded-md shadow-sm p-2"
                  required
                >
                  <option value="">-- Choisir --</option>
                  <option value="Urgent">Urgent</option>
                  <option value="Normal">Normal</option>
                  <option value="Faible">Faible priorité</option>
                </select>
              </div>
              <div>
                <label className="block text-sm font-medium text-blue-900">Description</label>
                <textarea
                  name="description"
                  rows="4"
                  value={formData.description}
                  onChange={handleChange}
                  className="mt-1 block w-full border border-gray-300 rounded-md shadow-sm p-2"
                  required
                ></textarea>
              </div>
              <div className="flex justify-between items-center pt-4">
                <Link to="/MyTickets" className="text-blue-700 hover:underline">
                  ← Retour
                </Link>
                <button
                  type="submit"
                  className="bg-blue-900 text-white px-6 py-2 rounded-md font-semibold hover:bg-blue-800"
                  disabled={isSubmitting}
                >
                  {isSubmitting ? "Envoi..." : "Soumettre"}
                </button>
              </div>
            </form>
          </div>

          <div className="hidden md:block md:w-1/2">
            <img
              src="https://images.unsplash.com/photo-1605902711622-cfb43c4437d4?auto=format&fit=crop&w=1000&q=80"
              alt="Assistance bancaire"
              className="rounded-xl h-full object-cover"
            />
          </div>
        </div>
      </div>
      <Footerr />
    </>
  );
}

export default NewTicketForm;
