import React, { useEffect, useState } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faTrash, faPen } from "@fortawesome/free-solid-svg-icons";
import axios from "axios";

export default function CardTable({ color }) {
  const [users, setUsers] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [newUser, setNewUser] = useState({
    nom: "",
    prenom: "",
    email: "",
    id: "",
    password: "",
    tel: "",
    role: "",
  });

  const [isEditing, setIsEditing] = useState(false);
  const [editIndex, setEditIndex] = useState(null);

  const getUsers = async () => {
    try {
      const res = await axios.get("http://localhost:5000/api/user/allusers");
      if (res.data) {
        setUsers(res.data);
      }
    } catch (error) {
      console.error("Erreur de récupération des utilisateurs:", error);
    }
  };

  useEffect(() => {
    getUsers();
  }, []);

  const handleInputChange = (e) => {
    setNewUser({ ...newUser, [e.target.name]: e.target.value });
  };

  const handleUpdateUser = async (e) => {
    e.preventDefault();
    try {
      const id = newUser._id;
      await axios.put(`http://localhost:5000/api/user/updateuser/${id}`, newUser);
      await getUsers();
      setIsEditing(false);
      setEditIndex(null);
      setShowForm(false);
    } catch (error) {
      console.error("Erreur lors de la mise à jour de l'utilisateur:", error);
    }
  };

  const handleDeleteUser = async (index) => {
    const confirmDelete = window.confirm("Êtes-vous sûr de vouloir supprimer cet utilisateur ?");
    if (!confirmDelete) return;

    try {
      const userId = users[index]._id;
      await axios.delete(`http://localhost:5000/api/user/deleteuser/${userId}`);
      await getUsers();
    } catch (error) {
      console.error("Erreur lors de la suppression de l'utilisateur:", error);
    }
  };

  const handleEditUser = (index) => {
    const selectedUser = users[index];
    setNewUser({ ...selectedUser, password: "" }); // Vider le champ mot de passe
    setIsEditing(true);
    setEditIndex(index);
    setShowForm(true);
  };

  return (
    <div className={`relative flex flex-col min-w-0 break-words w-full mb-6 shadow-lg rounded-lg ${color === "light" ? "bg-white" : "bg-lightBlue-900 text-white"}`}>
      <div className="flex justify-between items-center px-6 py-4 border-b border-gray-200">
        <h3 className={`font-bold text-xl ${color === "light" ? "text-blueGray-700" : "text-white"}`}>
          Liste des utilisateurs
        </h3>
      </div>

      <div className="overflow-x-auto p-4">
        <table className="w-full text-sm text-left text-white">
          <thead className="text-xs uppercase bg-lightBlue-800 text-blueGray-100">
            <tr>
              <th className="px-6 py-3">Nom</th>
              <th className="px-6 py-3">Prénom</th>
              <th className="px-6 py-3">Email</th>
              <th className="px-6 py-3">Téléphone</th>
              <th className="px-6 py-3">Rôle</th>
              <th className="px-6 py-3 text-right">Actions</th>
            </tr>
          </thead>
          <tbody className="bg-lightBlue-900 divide-y divide-blueGray-700">
            {users.map((user, index) => (
              <tr key={user._id} className="hover:bg-lightBlue-800">
                <td className="px-6 py-4">{user.nom}</td>
                <td className="px-6 py-4">{user.prenom}</td>
                <td className="px-6 py-4">{user.email}</td>
                <td className="px-6 py-4">{user.tel}</td>
                <td className="px-6 py-4">{user.role}</td>
                <td className="px-6 py-4 text-right">
                  <button
                    onClick={() => handleDeleteUser(index)}
                    className="bg-red-500 hover:bg-red-600 text-white px-3 py-1 rounded mr-2 text-xs shadow"
                  >
                    <FontAwesomeIcon icon={faTrash} />
                  </button>
                  <button
                    onClick={() => handleEditUser(index)}
                    className="bg-lightBlue-500 hover:bg-lightBlue-600 text-white px-3 py-1 rounded text-xs shadow"
                  >
                    <FontAwesomeIcon icon={faPen} />
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>

        {showForm && isEditing && (
          <form
            onSubmit={handleUpdateUser}
            className="mt-6 bg-lightBlue-900 text-white p-6 rounded shadow-md"
          >
            <h4 className="text-lg font-semibold mb-4">Modifier l'utilisateur</h4>
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4 mb-4">
              <input type="hidden" name="_id" value={newUser._id} />
              <div>
                <label className="block text-sm mb-1">Nom</label>
                <input type="text" name="nom" value={newUser.nom} onChange={handleInputChange} required className="w-full p-2 bg-lightBlue-800 border border-lightBlue-700 text-white rounded" />
              </div>
              <div>
                <label className="block text-sm mb-1">Prénom</label>
                <input type="text" name="prenom" value={newUser.prenom} onChange={handleInputChange} required className="w-full p-2 bg-lightBlue-800 border border-lightBlue-700 text-white rounded" />
              </div>
              <div>
                <label className="block text-sm mb-1">Email</label>
                <input type="email" name="email" value={newUser.email} onChange={handleInputChange} required className="w-full p-2 bg-lightBlue-800 border border-lightBlue-700 text-white rounded" />
              </div>
              <div>
                <label className="block text-sm mb-1">Téléphone</label>
                <input type="text" name="tel" value={newUser.tel} onChange={handleInputChange} required className="w-full p-2 bg-lightBlue-800 border border-lightBlue-700 text-white rounded" />
              </div>
              <div>
                <label className="block text-sm mb-1">Mot de passe</label>
                <input type="password" name="password" value={newUser.password} onChange={handleInputChange} className="w-full p-2 bg-lightBlue-800 border border-lightBlue-700 text-white rounded" />
              </div>
              <div>
                <label className="block text-sm mb-1">Rôle</label>
                <select name="role" value={newUser.role} onChange={handleInputChange} required className="w-full p-2 bg-lightBlue-800 border border-lightBlue-700 text-white rounded">
                  <option value="">-- Sélectionner un rôle --</option>
                  <option value="admin">Admin</option>
                  <option value="utilisateur">Utilisateur</option>
                  <option value="moderateur">Modérateur</option>
                </select>
              </div>
            </div>
            <div className="flex justify-end">
              <button type="submit" className="bg-emerald-500 hover:bg-emerald-600 text-white px-6 py-2 rounded shadow">
                Modifier
              </button>
            </div>
          </form>
        )}
      </div>
    </div>
  );
}
