import React, { useState, useEffect } from "react";
import PropTypes from "prop-types";
import { FaEye } from "react-icons/fa";
import { X } from "lucide-react";

export default function AvisClientTable({ color }) {
  const [avis, setAvis] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [isError, setIsError] = useState(false);
  const [searchQuery, setSearchQuery] = useState("");
  const [filterId, setFilterId] = useState("");
  const [detailModalOuvert, setDetailModalOuvert] = useState(false);
  const [avisDetail, setAvisDetail] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch('/api/getcom', {
          headers: {
            'Authorization': `Bearer ${localStorage.getItem('token')}`
          }
        });
        
        if (!response.ok) throw new Error('Erreur serveur');
        
        const { data } = await response.json();
        setAvis(data);
      } catch (error) {
        console.error('Erreur:', error);
        setIsError(true);
      } finally {
        setIsLoading(false);
      }
    };

    fetchData();
  }, []);

  const filteredAvis = avis.filter(a => {
    const matchesId = filterId ? 
      a.ticket.toLowerCase().includes(filterId.toLowerCase()) : true;
    
    const matchesSearch = searchQuery ?
      Object.values(a).some(value => 
        value?.toString().toLowerCase().includes(searchQuery.toLowerCase())
      ) : true;

    return matchesId && matchesSearch;
  });

  return (
    <div className={`relative mx-auto max-w-screen-xl flex flex-col min-w-0 rounded-lg shadow-lg mb-10 ${
      color === "light" ? "bg-white" : "bg-slate-800 text-white"
    }`}>

      {/* Ent√™te et filtres */}
      <div className="px-6 pt-6 border-b-2 border-gray-300">
        <h1 className={`text-2xl font-bold text-center ${
          color === "light" ? "text-gray-800" : "text-white"
        }`}>
          Avis des clients
        </h1>
      </div>

      <div className="flex flex-col sm:flex-row gap-4 px-6 pt-6 pb-4">
        <input
          type="text"
          placeholder="üîç Filtrer par ID ticket..."
          value={filterId}
          onChange={(e) => setFilterId(e.target.value)}
          className="flex-1 p-2 border rounded-lg"
        />
        <input
          type="text"
          placeholder="üîç Recherche globale..."
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
          className="flex-1 p-2 border rounded-lg"
        />
      </div>

      {/* Contenu principal */}
      {isLoading ? (
        <div className="p-6 text-center text-gray-500">Chargement...</div>
      ) : isError ? (
        <div className="p-6 text-center text-red-500">Erreur de chargement</div>
      ) : (
        <div className="overflow-x-auto px-6 pt-4 pb-14">
          <table className="w-full">
            <thead className="bg-gray-50">
              <tr>
                <th className="p-4 text-left">ID Ticket</th>
                <th className="p-4 text-left">Nom</th>
                <th className="p-4 text-left">Pr√©nom</th>
                <th className="p-4 text-left">Commentaire</th>
                <th className="p-4 text-left">Actions</th>
              </tr>
            </thead>
            <tbody>
              {filteredAvis.map((a) => (
                <tr key={a.id} className="border-t hover:bg-gray-50">
                  <td className="p-4 font-mono">{a.ticket}</td>
                  <td className="p-4">{a.nom || 'Non renseign√©'}</td>
                  <td className="p-4">{a.prenom || 'Non renseign√©'}</td>
                  <td className="p-4 max-w-[300px] truncate">{a.commentaire}</td>
                  <td className="p-4">
                    <button
                      onClick={() => setAvisDetail(a)}
                      className="text-blue-500 hover:text-blue-700"
                    >
                      <FaEye size={20} />
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}

      {/* Modal D√©tails */}
      {avisDetail && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center">
          <div className="bg-white rounded-lg p-6 max-w-2xl w-full">
            <div className="flex justify-between items-center mb-4">
              <h2 className="text-xl font-bold">D√©tails de l'avis</h2>
              <button 
                onClick={() => setAvisDetail(null)}
                className="text-gray-500 hover:text-red-500"
              >
                <X size={24} />
              </button>
            </div>

            <div className="space-y-4">
              <div>
                <label className="block text-sm text-gray-500">ID Ticket</label>
                <p className="mt-1 font-mono">{avisDetail.ticket}</p>
              </div>

              <div className="grid grid-cols-2 gap-4">
                <div>
                  <label className="block text-sm text-gray-500">Nom</label>
                  <p className="mt-1">{avisDetail.nom || 'Non renseign√©'}</p>
                </div>
                <div>
                  <label className="block text-sm text-gray-500">Pr√©nom</label>
                  <p className="mt-1">{avisDetail.prenom || 'Non renseign√©'}</p>
                </div>
              </div>

              <div>
                <label className="block text-sm text-gray-500">Commentaire complet</label>
                <p className="mt-2 whitespace-pre-wrap p-4 bg-gray-50 rounded">
                  {avisDetail.commentaire}
                </p>
              </div>

              <div>
                <label className="block text-sm text-gray-500">Date de cr√©ation</label>
                <p className="mt-1">
                  {new Date(avisDetail.createdAt).toLocaleDateString('fr-FR', {
                    year: 'numeric',
                    month: 'long',
                    day: 'numeric',
                    hour: '2-digit',
                    minute: '2-digit'
                  })}
                </p>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

AvisClientTable.propTypes = {
  color: PropTypes.oneOf(['light', 'dark']),
};

AvisClientTable.defaultProps = {
  color: 'light',
};
