import React, { useState, useEffect } from "react";
import axios from "axios";
import PropTypes from "prop-types";

import { X } from "lucide-react";
import { 
  FaFilter, 
  FaEdit, 
  FaTrash, 
  FaEye, 
  FaTag, 
  FaListAlt, 
  FaExclamationTriangle, 
  FaInfoCircle, 
  FaCalendarAlt, 
  FaFileAlt, 
  FaIdBadge 
} from "react-icons/fa";

export default function TicketTable({ color }) {
  const [items, setItems] = useState([]);
  const [searchQuery, setSearchQuery] = useState("");
  const [filterType, setFilterType] = useState("");
  const [filterId, setFilterId] = useState("");

  // États création
  const [modalOuvert, setModalOuvert] = useState(false);
  const [newTicket, setNewTicket] = useState({
    subject: "",
    type: "",
    urgency: "",
    description: "",
  });
  const [errors, setErrors] = useState({});

  // États édition
  const [editModalOuvert, setEditModalOuvert] = useState(false);
  const [editingTicket, setEditingTicket] = useState(null);
  const [editErrors, setEditErrors] = useState({});

  // États suppression
  const [deleteModalOuvert, setDeleteModalOuvert] = useState(false);
  const [ticketToDelete, setTicketToDelete] = useState(null);

  // États détails
  const [detailsModalOpen, setDetailsModalOpen] = useState(false);
  const [selectedTicket, setSelectedTicket] = useState(null);

  useEffect(() => {
    const fetchTickets = async () => {
      try {
        const { data } = await axios.get('/alltickets');
        const formatted = data.map(t => ({
          id: t._id,
          subject: t.sujet,
          type: t.type,
          urgency: t.urgence,
          description: t.description,
          status: t.statut.charAt(0).toUpperCase() + t.statut.slice(1),
          date: new Date(t.date).toLocaleDateString('fr-FR', {
            day: '2-digit',
            month: '2-digit',
            year: 'numeric'
          })
        }));
        setItems(formatted);
      } catch (error) {
        console.error("Erreur chargement:", error.response?.data);
      }
    };
    fetchTickets();
  }, [modalOuvert, editModalOuvert, deleteModalOuvert]);

  const handleChange = (e) => {
    setNewTicket({ ...newTicket, [e.target.name]: e.target.value });
  };

  const handleEditChange = (e) => {
    setEditingTicket({ ...editingTicket, [e.target.name]: e.target.value });
  };

  const validateForm = (ticket, isEdit = false) => {
    const errors = {};
    if (!ticket.subject.trim()) errors.subject = "Le sujet est requis.";
    if (!ticket.type) errors.type = "Le type est requis.";
    if (!isEdit && !ticket.urgency) errors.urgency = "L'urgence est requise.";
    if (!ticket.description.trim()) errors.description = "La description est requise.";
    return errors;
  };

  const handleCreateSubmit = async () => {
    const errors = validateForm(newTicket);
    if (Object.keys(errors).length > 0) return setErrors(errors);

    try {
      const response = await axios.post('/addticket', {
        sujet: newTicket.subject,
        type: newTicket.type,
        urgence: newTicket.urgency,
        description: newTicket.description
      });

      setItems(prev => [...prev, {
        id: response.data._id,
        subject: response.data.sujet,
        type: response.data.type,
        urgency: response.data.urgence,
        description: response.data.description,
        status: 'Ouvert',
        date: new Date(response.data.date).toLocaleDateString('fr-FR')
      }]);
      
      setModalOuvert(false);
      setNewTicket({ subject: "", type: "", urgency: "", description: "" });

    } catch (error) {
      console.error('Erreur création:', error.response?.data);
    }
  };

  const handleEditSubmit = async () => {
    const errors = validateForm(editingTicket, true);
    if (Object.keys(errors).length > 0) return setEditErrors(errors);
  
    try {
      const updatedStatus = editingTicket.status.toLowerCase();
  
      await axios.put(`/updateticket/${editingTicket.id}`, {
        sujet: editingTicket.subject,
        type: editingTicket.type,
        urgence: editingTicket.urgency,
        description: editingTicket.description,
        statut: updatedStatus
      });
  
      setEditModalOuvert(false);
    } catch (error) {
      console.error("Erreur de mise à jour:", error.response?.data);
    }
  };

  const confirmDelete = async () => {
    try {
      await axios.delete(`/deleteticket/${ticketToDelete}`);
      setItems(prev => prev.filter(item => item.id !== ticketToDelete));
      setDeleteModalOuvert(false);
    } catch (error) {
      console.error("Erreur de suppression:", error.response?.data);
    }
  };

  const filteredItems = items.filter(item => {
    const matchesId = filterId ? item.id.toString().includes(filterId) : true;
    const matchesSearch = searchQuery
      ? item.subject.toLowerCase().includes(searchQuery.toLowerCase()) ||
        item.description.toLowerCase().includes(searchQuery.toLowerCase())
      : true;
    const matchesUrgency = filterType ? item.urgency === filterType : true;

    return matchesId && matchesSearch && matchesUrgency;
  });

  const badgeStyle = (status) => ({
    padding: "0.25rem 0.5rem",
    borderRadius: "9999px",
    fontSize: "0.75rem",
    fontWeight: "500",
    backgroundColor:
      status === "Ouvert" ? "#fee2e2" :
      status === "En cours" ? "#ffedd5" : "#dcfce7",
    color:
      status === "Ouvert" ? "#dc2626" :
      status === "En cours" ? "#ea580c" : "#16a34a",
  });

  return (
    <div className={`relative mx-auto max-w-screen-xl flex flex-col min-w-0 rounded-lg shadow-lg mb-10 ${
      color === "light" ? "bg-white" : "bg-slate-800 text-white"
    }`}>
      
      <style jsx>{`
        .gp-action-icon {
          padding: 0.45rem;
          border-radius: 0.375rem;
          transition: all 0.2s ease;
          display: flex;
          align-items: center;
          justify-content: center;
        }
        .gp-view {
          background-color: #dbeafe;
          color: #2563eb;
        }
        .gp-view:hover {
          background-color: #bfdbfe;
        }
        .gp-edit {
          background-color: #e0f2fe;
          color: #0284c7;
        }
        .gp-edit:hover {
          background-color: #bae6fd;
        }
        .gp-delete {
          background-color: #fee2e2;
          color: #dc2626;
        }
        .gp-delete:hover {
          background-color: #fecaca;
        }
        .gp-add-button {
          display: flex;
          align-items: center;
          gap: 0.5rem;
          background-color: #0ea5e9;
          color: white;
          padding: 0.55rem 2.5rem;
          border-radius: 0.375rem;
          font-weight: 500;
          transition: all 0.2s ease;
        }
        .gp-add-button:hover {
          background-color: #0284c7;
        }
        .gp-modal-overlay {
          position: fixed;
          top: 0;
          left: 0;
          width: 100vw;
          height: 100vh;
          background-color: rgba(0, 0, 0, 0.6);
          display: flex;
          align-items: center;
          justify-content: center;
          z-index: 50;
        }
        .gp-modal-container {
          background-color: white;
          border-radius: 0.75rem;
          padding: 2rem;
          width: 90%;
          max-width: 500px;
          box-shadow: 0 10px 25px rgba(0, 0, 0, 0.2);
        }
        .gp-modal-header {
          display: flex;
          justify-content: space-between;
          align-items: center;
          margin-bottom: 1rem;
        }
        .gp-form-group {
          margin-bottom: 1.5rem;
        }
        .gp-form-input {
          width: 100%;
          padding: 0.5rem;
          border: 1px solid #ccc;
          border-radius: 0.375rem;
        }
        .gp-disabled-input {
          background-color: #f3f4f6;
          cursor: not-allowed;
        }
        .gp-readonly-text {
          padding: 0.5rem;
          background-color: #f3f4f6;
          border-radius: 0.375rem;
          display: block;
        }
        .gp-btn {
          padding: 0.5rem 1.25rem;
          border-radius: 0.375rem;
          font-weight: 600;
          cursor: pointer;
          transition: all 0.2s ease;
        }
        .gp-btn-save {
          background-color: #0ea5e9;
          color: white;
        }
        .gp-btn-save:hover {
          background-color: #0284c7;
        }
        .gp-btn-cancel {
          background-color: #f3f4f6;
          color: #374151;
        }
        .gp-btn-cancel:hover {
          background-color: #e5e7eb;
        }
        .gp-btn-danger {
          background-color: #dc2626;
          color: white;
        }
        .gp-btn-danger:hover {
          background-color: #b91c1c;
        }
        .gp-delete-modal-content {
          text-align: center;
          padding: 2rem;
        }
        .gp-delete-buttons {
          display: flex;
          justify-content: center;
          gap: 1rem;
          margin-top: 2rem;
        }
      `}</style>

      <div className="px-6 pt-6 border-b-2 border-gray-300">
        <h1 className={`text-2xl font-bold text-center ${
          color === "light" ? "text-gray-800" : "text-white"
        }`}>
          Liste des tickets
        </h1>
      </div>

      <div className="flex justify-between px-6 pt-6 pb-4 items-center gap-4">
        <input
          type="text"
          placeholder="🔍 Rechercher par ID..."
          value={filterId}
          onChange={(e) => setFilterId(e.target.value)}
          className="w-full sm:w-64 px-4 py-2 border rounded-lg shadow-sm text-gray-700 focus:outline-none focus:ring-2 focus:ring-blue-500 mr-4"
        />

        <div className="flex gap-4 items-center">
          <FaFilter className="text-gray-700 text-xl mr-2" />

          <select
            value={filterType}
            onChange={(e) => setFilterType(e.target.value)}
            className="w-50 sm:w-64 border rounded-lg shadow-sm text-gray-700 focus:outline-none focus:ring-2 focus:ring-blue-500 mr-4"
          >
            <option value="">Filtrer par urgence</option>
            <option value="Urgent">Urgent</option>
            <option value="Normal">Normal</option>
          </select>

          <button 
            className="gp-add-button" 
            onClick={() => setModalOuvert(true)}
          >
            <span>+</span>
            <span>Créer</span>
          </button>
        </div>
      </div>

      <div className="overflow-x-auto px-6 pt-4 pb-14">
        <table className="w-full border-collapse">
          <thead>
            <tr className={`text-left ${
              color === "light" ? "bg-gray-50 text-gray-500" : "bg-slate-700 text-slate-200"
            }`}>
              <th className="px-6 py-4 font-medium">ID</th>
              <th className="px-6 py-4 font-medium">Sujet</th>
              <th className="px-6 py-4 font-medium">Type</th>
              <th className="px-6 py-4 font-medium">Urgence</th>
              <th className="px-6 py-4 font-medium">Statut</th>
              <th className="px-6 py-4 font-medium">Date</th>
              <th className="px-6 py-4 font-medium">Actions</th>
            </tr>
          </thead>
          <tbody>
            {filteredItems.map((item) => (
              <tr 
                key={item.id} 
                className={`border-t ${
                  color === "light" ? "hover:bg-gray-50" : "hover:bg-slate-700"
                } transition-colors`}
              >
                <td className="px-6 py-4">{item.id}</td>
                <td className="px-6 py-4">{item.subject}</td>
                <td className="px-6 py-4">{item.type}</td>
                <td className="px-6 py-4 font-semibold text-red-600">{item.urgency}</td>
                <td className="px-6 py-4">
                  <span style={badgeStyle(item.status)}>
                    {item.status}
                  </span>
                </td>
                <td className="px-6 py-4">{item.date}</td>
                <td className="px-6 py-4">
                  <div className="flex" style={{ gap: "8px" }}>
                    <button
                      onClick={() => {
                        setSelectedTicket(item);
                        setDetailsModalOpen(true);
                      }}
                      className="gp-action-icon gp-view"
                      title="Voir les détails"
                    >
                      <FaEye size={16} />
                    </button>
                    <button
                      onClick={() => {
                        setEditingTicket(item);
                        setEditModalOuvert(true);
                      }}
                      className="gp-action-icon gp-edit"
                      title="Modifier"
                    >
                      <FaEdit size={16} />
                    </button>
                    <button
                      onClick={() => {
                        setTicketToDelete(item.id);
                        setDeleteModalOuvert(true);
                      }}
                      className="gp-action-icon gp-delete"
                      title="Supprimer"
                    >
                      <FaTrash size={16} />
                    </button>
                  </div>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {/* Modal Création */}
      {modalOuvert && (
        <div className="gp-modal-overlay">
          <div className="gp-modal-container">
            <div className="gp-modal-header">
              <h2 className="text-xl font-bold">Créer un ticket</h2>
              <button onClick={() => setModalOuvert(false)}>
                <X size={24} />
              </button>
            </div>

            <div>
              <div className="gp-form-group">
                <label className="block font-semibold mb-1">Sujet</label>
                <input
                  type="text"
                  name="subject"
                  value={newTicket.subject}
                  onChange={handleChange}
                  className="gp-form-input"
                />
                {errors.subject && <p className="text-red-500 text-sm">{errors.subject}</p>}
              </div>

              <div className="gp-form-group">
                <label className="block font-semibold mb-1">Type</label>
                <select
                  name="type"
                  value={newTicket.type}
                  onChange={handleChange}
                  className="gp-form-input"
                >
                  <option value="">-- Sélectionner --</option>
                  <option value="Informatique">Informatique</option>
                  <option value="Ressources humaines">Ressources humaines</option>
                  <option value="Comptabilité">Comptabilité</option>
                </select>
                {errors.type && <p className="text-red-500 text-sm">{errors.type}</p>}
              </div>

              <div className="gp-form-group">
                <label className="block font-semibold mb-1">Urgence</label>
                <select
                  name="urgency"
                  value={newTicket.urgency}
                  onChange={handleChange}
                  className="gp-form-input"
                >
                  <option value="">-- Sélectionner --</option>
                  <option value="Urgent">Urgent</option>
                  <option value="Normal">Normal</option>
                </select>
                {errors.urgency && <p className="text-red-500 text-sm">{errors.urgency}</p>}
              </div>

              <div className="gp-form-group">
                <label className="block font-semibold mb-1">Description</label>
                <textarea
                  name="description"
                  value={newTicket.description}
                  onChange={handleChange}
                  rows={4}
                  className="gp-form-input"
                />
                {errors.description && <p className="text-red-500 text-sm">{errors.description}</p>}
              </div>

              <div className="flex justify-end mt-4" style={{ gap: "12px" }}>
                <button 
                  type="button"
                  onClick={() => setModalOuvert(false)} 
                  className="gp-btn gp-btn-cancel"
                >
                  Annuler
                </button>
                <button 
                  type="button"
                  onClick={handleCreateSubmit} 
                  className="gp-btn gp-btn-save"
                >
                  Créer
                </button>
              </div>
            </div>
          </div>
        </div>
      )}

      {/* Modal Détails */}
  {/* Modal Détails Simplifié */}
{detailsModalOpen && selectedTicket && (
  <div className="gp-modal-overlay">
    <div className="gp-modal-container" style={{ maxWidth: "500px" }}>
      <div className="gp-modal-header">
        <h2 className="text-lg font-bold">Détails du ticket</h2>
        <button 
          onClick={() => setDetailsModalOpen(false)}
          className="text-gray-500 hover:text-gray-700"
        >
          <X size={20} />
        </button>
      </div>

      <div className="p-4 space-y-3">
        <div>
          <p className="font-medium">ID :</p>
          <p className="text-sm">{selectedTicket.id}</p>
        </div>
        
        <div>
          <p className="font-medium">Sujet :</p>
          <p className="text-sm">{selectedTicket.subject}</p>
        </div>

        <div>
          <p className="font-medium">Type :</p>
          <p className="text-sm">{selectedTicket.type}</p>
        </div>

        <div>
          <p className="font-medium">Urgence :</p>
          <p className="text-sm">{selectedTicket.urgency}</p>
        </div>

        <div>
          <p className="font-medium">Statut :</p>
          <p className="text-sm">{selectedTicket.status}</p>
        </div>

        <div>
          <p className="font-medium">Date :</p>
          <p className="text-sm">{selectedTicket.date}</p>
        </div>

        <div>
          <p className="font-medium">Description :</p>
          <p className="text-sm whitespace-pre-wrap bg-gray-100 p-2 rounded mt-1">
            {selectedTicket.description}
          </p>
        </div>
      </div>

      <div className="p-4 border-t">
        <button
          onClick={() => setDetailsModalOpen(false)}
          className="text-sm text-blue-600 hover:text-blue-800"
        >
          Fermer
        </button>
      </div>
    </div>
  </div>
)}

      {/* Modal Édition */}
{/* Modal Détails */}
{detailsModalOpen && selectedTicket && (
  <div className="gp-modal-overlay">
    <div className="gp-modal-container" style={{ maxWidth: "600px" }}>
      <div className="gp-modal-header bg-blue-100 p-4 rounded-t-lg">
        <h2 className="text-2xl font-bold text-blue-800 flex items-center gap-2">
          <FaEye className="inline-block" />
          Détails du ticket #{selectedTicket.id}
        </h2>
        <button 
          onClick={() => setDetailsModalOpen(false)} 
          className="text-gray-500 hover:text-gray-700"
        >
          <X size={24} />
        </button>
      </div>

      <div className="p-6 space-y-4 bg-gray-50">
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          {/* Section Gauche */}
          <div className="space-y-3">
            <div className="flex items-center gap-2 bg-white p-3 rounded-lg shadow-sm">
              <FaTag className="text-blue-500 flex-shrink-0" />
              <div>
                <p className="text-sm font-medium text-gray-500">Sujet</p>
                <p className="font-semibold">{selectedTicket.subject}</p>
              </div>
            </div>

            <div className="flex items-center gap-2 bg-white p-3 rounded-lg shadow-sm">
              <FaListAlt className="text-green-500 flex-shrink-0" />
              <div>
                <p className="text-sm font-medium text-gray-500">Type</p>
                <p className="font-semibold">{selectedTicket.type}</p>
              </div>
            </div>

            <div className="flex items-center gap-2 bg-white p-3 rounded-lg shadow-sm">
              <FaExclamationTriangle className="text-red-500 flex-shrink-0" />
              <div>
                <p className="text-sm font-medium text-gray-500">Urgence</p>
                <p className="font-semibold">{selectedTicket.urgency}</p>
              </div>
            </div>
          </div>

          {/* Section Droite */}
          <div className="space-y-3">
            <div className="flex items-center gap-2 bg-white p-3 rounded-lg shadow-sm">
              <FaInfoCircle className="text-purple-500 flex-shrink-0" />
              <div>
                <p className="text-sm font-medium text-gray-500">Statut</p>
                <span style={badgeStyle(selectedTicket.status)}>
                  {selectedTicket.status}
                </span>
              </div>
            </div>

            <div className="flex items-center gap-2 bg-white p-3 rounded-lg shadow-sm">
              <FaCalendarAlt className="text-yellow-500 flex-shrink-0" />
              <div>
                <p className="text-sm font-medium text-gray-500">Date de création</p>
                <p className="font-semibold">{selectedTicket.date}</p>
              </div>
            </div>

            <div className="flex items-center gap-2 bg-white p-3 rounded-lg shadow-sm">
              <FaIdBadge className="text-gray-500 flex-shrink-0" />
              <div>
                <p className="text-sm font-medium text-gray-500">Identifiant</p>
                <p className="font-mono font-semibold text-sm">{selectedTicket.id}</p>
              </div>
            </div>
          </div>
        </div>

        {/* Description */}
        <div className="bg-white p-4 rounded-lg shadow-sm">
          <div className="flex items-center gap-2 mb-3">
            <FaFileAlt className="text-blue-500" />
            <h3 className="font-semibold text-gray-700">Description du ticket</h3>
          </div>
          <p className="text-gray-600 whitespace-pre-wrap bg-gray-50 p-3 rounded-md">
            {selectedTicket.description}
          </p>
        </div>
      </div>

      <div className="p-4 bg-gray-100 rounded-b-lg flex justify-end">
        <button
          onClick={() => setDetailsModalOpen(false)}
          className="px-4 py-2 bg-gray-300 hover:bg-gray-400 text-gray-800 rounded-lg transition-colors"
        >
          Fermer
        </button>
      </div>
    </div>
  </div>
)}

      {/* Modal Suppression */}
      {deleteModalOuvert && (
        <div className="gp-modal-overlay">
          <div className="gp-modal-container">
            <div className="gp-modal-header">
              <h2 className="text-xl font-bold">Confirmer la suppression</h2>
              <button onClick={() => setDeleteModalOuvert(false)}>
                <X size={24} />
              </button>
            </div>

            <div className="gp-delete-modal-content">
              <svg
                xmlns="http://www.w3.org/2000/svg"
                className="h-16 w-16 text-red-600 mx-auto mb-4"
                fill="none"
                viewBox="0 0 24 24"
                stroke="currentColor"
              >
                <path
                  strokeLinecap="round"
                  strokeLinejoin="round"
                  strokeWidth={2}
                  d="M12 9v2m0 4h.01m-6.938 4h13.856c1.54 0 2.502-1.667 1.732-3L13.732 4c-.77-1.333-2.694-1.333-3.464 0L3.34 16c-.77 1.333.192 3 1.732 3z"
                />
              </svg>
              
              <p className="text-lg font-medium">
                Êtes-vous sûr de vouloir supprimer ce ticket ?
              </p>

              <div className="gp-delete-buttons">
                <button
                  onClick={() => setDeleteModalOuvert(false)}
                  className="gp-btn gp-btn-cancel"
                >
                  Annuler
                </button>
                <button
                  onClick={confirmDelete}
                  className="gp-btn gp-btn-danger"
                >
                  Supprimer
                </button>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

TicketTable.defaultProps = {
  color: "light",
};

TicketTable.propTypes = {
  color: PropTypes.oneOf(["light", "dark"]),
};
