import React, { useEffect, useState } from 'react';
import { Calendar, dateFnsLocalizer } from 'react-big-calendar';
import format from 'date-fns/format';
import parse from 'date-fns/parse';
import startOfWeek from 'date-fns/startOfWeek';
import getDay from 'date-fns/getDay';
import fr from 'date-fns/locale/fr';
import 'react-big-calendar/lib/css/react-big-calendar.css';

const locales = { 'fr': fr };
const localizer = dateFnsLocalizer({
  format,
  parse,
  startOfWeek,
  getDay,
  locales,
});

const CalendrierTickets = () => {
  const [events, setEvents] = useState([]);

  useEffect(() => {
    const fetchTickets = async () => {
      try {
        const response = await fetch('http://localhost:5000/api/tickets');
        if (!response.ok) throw new Error('Erreur réseau');
        
        const tickets = await response.json();
        
        const formattedEvents = tickets.map(ticket => ({
          title: ticket.sujet,
          start: new Date(ticket.createdAt),
          end: new Date(ticket.createdAt),
          statut: ticket.statut,
          description: ticket.description,
          type: ticket.type,
          urgence: ticket.urgence
        }));
        
        setEvents(formattedEvents);
      } catch (error) {
        console.error('Erreur:', error);
      }
    };

    fetchTickets();
  }, []);

  const eventStyleGetter = (event) => {
    let backgroundColor = '#3174ad';
    if (event.statut === 'ouvert') backgroundColor = '#e74c3c';
    if (event.statut === 'en cours') backgroundColor = '#f39c12';
    if (event.statut === 'fermé') backgroundColor = '#27ae60';

    return {
      style: {
        backgroundColor,
        color: 'white',
        borderRadius: '5px',
        border: 'none',
        padding: '2px 5px',
        fontSize: '0.8em'
      }
    };
  };

  return (
    <div style={{
      backgroundColor: '#f5f6fa',
      minHeight: '100vh',
      padding: '2rem'
    }}>
      <div style={{
        maxWidth: '1200px',
        margin: '0 auto',
        backgroundColor: 'white',
        borderRadius: '15px',
        boxShadow: '0 0 20px rgba(0,0,0,0.1)',
        padding: '2rem'
      }}>
        <h2 style={{
          color: '#2c3e50',
          marginBottom: '1.5rem',
          display: 'flex',
          alignItems: 'center',
          gap: '0.5rem'
        }}>
          <svg width="24" height="24" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
            <path d="M8 7V3M16 7V3M7 11H17M5 21H19C20.1046 21 21 20.1046 21 19V7C21 5.89543 20.1046 5 19 5H5C3.89543 5 3 5.89543 3 7V19C3 20.1046 3.89543 21 5 21Z" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
          </svg>
          Calendrier des Tickets
        </h2>

        <div style={{ height: '70vh' }}>
          <Calendar
            localizer={localizer}
            events={events}
            startAccessor="start"
            endAccessor="end"
            style={{ height: '100%' }}
            culture='fr'
            messages={{
              today: "Aujourd'hui",
              previous: 'Précédent',
              next: 'Suivant',
              month: 'Mois',
              week: 'Semaine',
              day: 'Jour',
              agenda: 'Agenda',
              date: 'Date',
              time: 'Heure',
              event: 'Événement'
            }}
            tooltipAccessor={event => `
              Sujet: ${event.title}
              Statut: ${event.statut}
              Type: ${event.type}
              Urgence: ${event.urgence}
              Description: ${event.description}
            `}
            eventPropGetter={eventStyleGetter}
          />
        </div>

        <div style={{
          marginTop: '2rem',
          display: 'flex',
          gap: '1rem',
          justifyContent: 'center'
        }}>
          <div style={{ display: 'flex', alignItems: 'center', gap: '0.5rem' }}>
            <div style={{ width: '15px', height: '15px', backgroundColor: '#e74c3c' }}></div>
            <span>Ouvert</span>
          </div>
          <div style={{ display: 'flex', alignItems: 'center', gap: '0.5rem' }}>
            <div style={{ width: '15px', height: '15px', backgroundColor: '#f39c12' }}></div>
            <span>En cours</span>
          </div>
          <div style={{ display: 'flex', alignItems: 'center', gap: '0.5rem' }}>
            <div style={{ width: '15px', height: '15px', backgroundColor: '#27ae60' }}></div>
            <span>Fermé</span>
          </div>
        </div>
      </div>
    </div>
  );
};

export default CalendrierTickets;
