import React from "react";
import { Edit, Trash } from "lucide-react";

// Styles CSS encapsulés
const tableStyles = `
.gp-table-container {
  width: 100%;
  overflow-x: auto;
  margin-top: 5rem; /* Ajout de cette ligne pour espacer le tableau du haut */
}
  
  .gp-table {
    width: 100%;
    border-collapse: collapse;
    margin: 1rem 0;
  }
  
  .gp-th {
    text-align: left;
    padding: 0.75rem 1rem;
    font-size: 0.75rem;
    font-weight: 600;
    text-transform: uppercase;
    color: #6b7280;
    background-color: #f9fafb;
    border-bottom: 1px solid #e5e7eb;
  }
  
  .gp-td {
    padding: 1rem;
    vertical-align: middle;
    color: #374151;
    border-bottom: 1px solid #f3f4f6;
  }
  
  .gp-tr:hover {
    background-color: #f9fafb;
  }
  
  .gp-department-badge {
    background-color: #e6fffa;
    color: #047857;
    padding: 0.25rem 0.5rem;
    border-radius: 9999px;
    font-size: 0.75rem;
    font-weight: 500;
  }
  
  .gp-action-button {
    padding: 0.375rem;
    border-radius: 0.375rem;
    transition: all 0.2s ease;
  }
  
  .gp-edit-button {
    background-color: #dbeafe;
    color: #3b82f6;
  }
  
  .gp-delete-button {
    background-color: #fee2e2;
    color: #ef4444;
  }
`;

const DepartmentTable = () => {
  // Exemple de données
  const departments = [
    { _id: "5f8d8a7e", nom: "Ressources Humaines" },
    { _id: "6a2b3c4d", nom: "Informatique" },
    { _id: "9e8f7g6h", nom: "Marketing" }
  ];

  return (
    <>
      <style>{tableStyles}</style>
      <div className="gp-table-container">
        <table className="gp-table">
          <thead>
            <tr>
              <th className="gp-th">ID</th>
              <th className="gp-th">Département</th>
              <th className="gp-th" style={{ textAlign: "center" }}>
                Actions
              </th>
            </tr>
          </thead>
          <tbody>
            {departments.map((department) => (
              <tr className="gp-tr" key={department._id}>
                <td className="gp-td">{department._id}</td>
                <td className="gp-td">
                  <span className="gp-department-badge">
                    {department.nom}
                  </span>
                </td>
                <td className="gp-td">
                  <div style={{ display: "flex", justifyContent: "center", gap: "0.5rem" }}>
                    <button className="gp-action-button gp-edit-button">
                      <Edit size={16} />
                    </button>
                    <button className="gp-action-button gp-delete-button">
                      <Trash size={16} />
                    </button>
                  </div>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </>
  );
};

export default DepartmentTable;
