    <>
      <IndexNavbar />

      <div style={container}>
        <div style={contentWrapper}>
          <div style={headerContainer}>
            <img 
              src="https://img.icons8.com/clouds/300/customer-support.png" 
              alt="Support client" 
              style={headerImage}
            />
            <h1 style={title}>
              Support Client
              <span style={titleUnderline}></span>
            </h1>
          </div>

          {/* ... (le reste du JSX reste inchangé jusqu'au ticketGrid) */}

        </div>
      </div>

      <Footerr />
    </>
  );
}

// NOUVEAUX STYLES AJOUTÉS
const headerContainer = {
  display: 'flex',
  alignItems: 'center',
  gap: '2rem',
  marginBottom: '3rem',
  position: 'relative'
};

const headerImage = {
  width: '120px',
  height: '120px',
  filter: 'drop-shadow(0 4px 8px rgba(0,0,0,0.1))'
};

const titleUnderline = {
  display: 'block',
  width: '60px',
  height: '4px',
  backgroundColor: '#1a237e',
  borderRadius: '2px',
  marginTop: '0.5rem'
};

// STYLES MODIFIÉS
const title = {
  color: '#1a237e',
  fontSize: '2.5rem',
  fontWeight: '600',
  margin: 0,
  textShadow: '2px 2px 4px rgba(0,0,0,0.1)'
};

const ticketItem = {
  backgroundColor: 'white',
  borderRadius: '10px',
  padding: '1.5rem',
  marginBottom: '1rem',
  cursor: 'pointer',
  border: '1px solid #eee',
  boxShadow: '0 2px 8px rgba(0,0,0,0.05)',
  transition: 'transform 0.2s, box-shadow 0.2s',
  ':hover': {
    transform: 'translateY(-2px)',
    boxShadow: '0 4px 12px rgba(0,0,0,0.1)'
  }
};

const detailsPanel = {
  backgroundColor: 'white',
  borderRadius: '10px',
  padding: '2rem',
  boxShadow: '0 4px 12px rgba(0,0,0,0.1)',
  position: 'sticky',
  top: '2rem'
};

// ... (le reste des styles reste inchangé)

export default MyTickets;
