import React, { useState } from "react";
import IndexNavbar from "components/Navbars/IndexNavbar.js";
import Footerr from "components/Footers/Footerr";

function MyTickets() {
  const [selectedTicket, setSelectedTicket] = useState(null);

  const tickets = [
    {
      id: 1,
      sujet: "Problème d'accès au portail client",
      departement: "Support Technique",
      statut: "Ouvert",
      date: "2024-03-15",
      description: "Impossible d'accéder à mon espace client depuis hier soir.",
    },
    {
      id: 2,
      sujet: "Question sur un virement international",
      departement: "Opérations Bancaires",
      statut: "En cours",
      date: "2024-03-14",
      description: "Demande d'information sur les frais de virement vers l'Asie.",
    },
    {
      id: 3,
      sujet: "Carte bloquée à l'étranger",
      departement: "Cartes Bancaires",
      statut: "Résolu",
      date: "2024-03-10",
      description: "Déblocage de ma carte Visa Premier en Australie.",
    },
  ];

  return (
    <>
      <IndexNavbar />

      <div style={backgroundStyle}>
        <div style={containerStyle}>
          <h1 style={titleStyle}>Mes Tickets – Gestion des Requêtes</h1>

          {/* Liste des titres de tickets */}
          <div style={{ marginBottom: "1.5rem" }}>
            <h2 style={{ fontSize: "1.2rem", color: "#2980b9" }}>
              Liste des titres de tickets :
            </h2>
            <ul style={{ paddingLeft: "1.2rem" }}>
              {tickets.map((ticket) => (
                <li key={ticket.id} style={{ color: "#34495e", margin: "0.25rem 0" }}>
                  {ticket.sujet}
                </li>
              ))}
            </ul>
          </div>

          <div style={gridStyle}>
            <div style={ticketColumnStyle}>
              {tickets.map((ticket) => (
                <div
                  key={ticket.id}
                  style={ticketStyle}
                  onClick={() => setSelectedTicket(ticket)}
                >
                  <div style={ticketHeaderStyle}>
                    <div style={ticketTitleStyle}>
                      <h3 style={sujetStyle}>{ticket.sujet}</h3>
                      <p style={departementStyle}>{ticket.departement}</p>
                    </div>
                    <span style={statusStyle(ticket.statut)}>{ticket.statut}</span>
                  </div>
                  <div style={footerStyle}>
                    <span style={dateStyle}>{ticket.date}</span>
                    <svg style={iconStyle} viewBox="0 0 24 24">
                      <path d="M8.59 16.59L13.17 12 8.59 7.41 10 6l6 6-6 6-1.41-1.41z" />
                    </svg>
                  </div>
                </div>
              ))}
            </div>

            {selectedTicket && (
              <div style={detailColumnStyle}>
                <div style={detailHeaderStyle}>
                  <h2 style={detailTitleStyle}>Détails de la requête</h2>
                  <button
                    onClick={() => setSelectedTicket(null)}
                    style={closeButtonStyle}
                  >
                    ×
                  </button>
                </div>
                <div style={detailContentStyle}>
                  <div style={detailItemStyle}>
                    <label style={detailLabelStyle}>Sujet :</label>
                    <p style={detailValueStyle}>{selectedTicket.sujet}</p>
                  </div>
                  <div style={detailItemStyle}>
                    <label style={detailLabelStyle}>Statut :</label>
                    <p style={detailValueStyle}>
                      <span style={statusStyle(selectedTicket.statut)}>
                        {selectedTicket.statut}
                      </span>
                    </p>
                  </div>
                  <div style={detailItemStyle}>
                    <label style={detailLabelStyle}>Date :</label>
                    <p style={detailValueStyle}>{selectedTicket.date}</p>
                  </div>
                  <div style={detailItemStyle}>
                    <label style={detailLabelStyle}>Description :</label>
                    <p style={detailTextStyle}>{selectedTicket.description}</p>
                  </div>
                </div>
              </div>
            )}
          </div>
        </div>
      </div>

      <Footerr />
    </>
  );
}

// Styles
const backgroundStyle = {
  backgroundColor: "#f8f9fa",
  minHeight: "100vh",
  padding: "2rem 0",
};

const containerStyle = {
  maxWidth: "1200px",
  margin: "0 auto",
  padding: "0 1rem",
};

const titleStyle = {
  color: "#2c3e50",
  fontSize: "1.75rem",
  marginBottom: "2rem",
  fontWeight: "600",
};

const gridStyle = {
  display: "grid",
  gridTemplateColumns: "1fr",
  gap: "2rem",
};

const ticketColumnStyle = {
  backgroundColor: "white",
  borderRadius: "10px",
  boxShadow: "0 2px 8px rgba(0,0,0,0.1)",
  padding: "1rem",
};

const ticketStyle = {
  padding: "1rem",
  marginBottom: "1rem",
  borderRadius: "8px",
  border: "1px solid #e0e0e0",
  cursor: "pointer",
  transition: "all 0.2s ease",
};

const ticketHeaderStyle = {
  display: "flex",
  justifyContent: "space-between",
  alignItems: "flex-start",
  marginBottom: "0.5rem",
};

const ticketTitleStyle = {
  flex: 1,
};

const sujetStyle = {
  fontSize: "1rem",
  color: "#2c3e50",
  margin: "0 0 0.25rem 0",
};

const departementStyle = {
  fontSize: "0.875rem",
  color: "#7f8c8d",
  margin: "0",
};

const statusStyle = (statut) => ({
  fontSize: "0.75rem",
  fontWeight: "600",
  padding: "0.25rem 0.75rem",
  borderRadius: "20px",
  backgroundColor: {
    "Ouvert": "#e74c3c",
    "En cours": "#f1c40f",
    "Résolu": "#2ecc71",
  }[statut],
  color: "white",
});

const footerStyle = {
  display: "flex",
  justifyContent: "space-between",
  alignItems: "center",
  marginTop: "0.5rem",
};

const dateStyle = {
  fontSize: "0.75rem",
  color: "#95a5a6",
};

const iconStyle = {
  width: "24px",
  height: "24px",
  fill: "#bdc3c7",
};

const detailColumnStyle = {
  backgroundColor: "white",
  borderRadius: "10px",
  boxShadow: "0 2px 8px rgba(0,0,0,0.1)",
  padding: "1.5rem",
};

const detailHeaderStyle = {
  display: "flex",
  justifyContent: "space-between",
  alignItems: "center",
  marginBottom: "1.5rem",
};

const detailTitleStyle = {
  fontSize: "1.25rem",
  color: "#2c3e50",
  margin: "0",
};

const closeButtonStyle = {
  backgroundColor: "transparent",
  border: "none",
  fontSize: "1.5rem",
  color: "#7f8c8d",
  cursor: "pointer",
  padding: "0 0.5rem",
};

const detailContentStyle = {
  display: "flex",
  flexDirection: "column",
  gap: "1rem",
};

const detailItemStyle = {
  display: "flex",
  flexDirection: "column",
  gap: "0.25rem",
};

const detailLabelStyle = {
  fontSize: "0.875rem",
  color: "#7f8c8d",
  fontWeight: "500",
};

const detailValueStyle = {
  fontSize: "1rem",
  color: "#2c3e50",
  margin: "0",
};

const detailTextStyle = {
  fontSize: "0.9rem",
  color: "#34495e",
  lineHeight: "1.6",
  margin: "0",
};

export default MyTickets;
