import { useState } from 'react';
import { Trash2, Edit, Search, ChevronDown, ChevronUp, Filter, RefreshCw } from 'lucide-react';

export default function UserManagementTable() {
  const [sortColumn, setSortColumn] = useState('id');
  const [sortDirection, setSortDirection] = useState('asc');
  const [searchTerm, setSearchTerm] = useState('');
  const [activeFilters, setActiveFilters] = useState({});
  const [selectedRows, setSelectedRows] = useState([]);
  const [showFilters, setShowFilters] = useState(false);

  // Données d'exemple
  const users = [
    { id: 1, nom: 'Dupont', prenom: 'Jean', role: 'Administrateur', tel: '06 12 34 56 78' },
    { id: 2, nom: 'Martin', prenom: 'Sophie', role: 'Utilisateur', tel: '07 23 45 67 89' },
    { id: 3, nom: 'Bernard', prenom: 'Marie', role: 'Modérateur', tel: '06 34 56 78 90' },
    { id: 4, nom: 'Petit', prenom: 'Pierre', role: 'Utilisateur', tel: '07 45 67 89 01' },
    { id: 5, nom: 'Robert', prenom: 'Emma', role: 'Administrateur', tel: '06 56 78 90 12' },
    { id: 6, nom: 'Richard', prenom: 'Thomas', role: 'Modérateur', tel: '07 67 89 01 23' },
    { id: 7, nom: 'Moreau', prenom: 'Laura', role: 'Utilisateur', tel: '06 78 90 12 34' },
  ];

  // Filtrer les utilisateurs en fonction du terme de recherche
  const filteredUsers = users.filter(user => {
    const matchesSearch = 
      user.nom.toLowerCase().includes(searchTerm.toLowerCase()) ||
      user.prenom.toLowerCase().includes(searchTerm.toLowerCase()) ||
      user.role.toLowerCase().includes(searchTerm.toLowerCase()) ||
      user.tel.includes(searchTerm);
    
    // Vérifier les filtres actifs
    let matchesFilters = true;
    Object.entries(activeFilters).forEach(([key, value]) => {
      if (value && user[key].toLowerCase() !== value.toLowerCase()) {
        matchesFilters = false;
      }
    });
    
    return matchesSearch && matchesFilters;
  });

  // Trier les utilisateurs
  const sortedUsers = [...filteredUsers].sort((a, b) => {
    if (sortDirection === 'asc') {
      return a[sortColumn] > b[sortColumn] ? 1 : -1;
    } else {
      return a[sortColumn] < b[sortColumn] ? 1 : -1;
    }
  });

  // Gérer le clic sur l'en-tête pour trier
  const handleSort = (column) => {
    if (sortColumn === column) {
      setSortDirection(sortDirection === 'asc' ? 'desc' : 'asc');
    } else {
      setSortColumn(column);
      setSortDirection('asc');
    }
  };

  // Gérer la sélection d'une ligne
  const toggleRowSelection = (id) => {
    if (selectedRows.includes(id)) {
      setSelectedRows(selectedRows.filter(rowId => rowId !== id));
    } else {
      setSelectedRows([...selectedRows, id]);
    }
  };

  // Gérer le clic sur le bouton modifier
  const handleEdit = (id) => {
    alert(`Modifier l'utilisateur avec l'ID: ${id}`);
  };

  // Gérer le clic sur le bouton supprimer
  const handleDelete = (id) => {
    if (confirm('Êtes-vous sûr de vouloir supprimer cet utilisateur ?')) {
      alert(`Utilisateur avec l'ID: ${id} supprimé!`);
    }
  };

  // Les options de rôle pour les filtres
  const roleOptions = ['Administrateur', 'Modérateur', 'Utilisateur'];

  // Réinitialiser les filtres
  const resetFilters = () => {
    setActiveFilters({});
    setSearchTerm('');
  };

  return (
    <div className="bg-white rounded-lg shadow-lg overflow-hidden">
      {/* Header avec titre et actions */}
      <div className="p-6 bg-gradient-to-r from-blue-600 to-indigo-700 text-white">
        <div className="flex justify-between items-center">
          <h1 className="text-2xl font-bold">Gestion des utilisateurs</h1>
          <div className="flex space-x-2">
            <button 
              className="px-4 py-2 bg-white bg-opacity-20 hover:bg-opacity-30 rounded-md flex items-center"
              onClick={() => setShowFilters(!showFilters)}
            >
              <Filter size={16} className="mr-1" />
              Filtres
            </button>
            <button 
              className="px-4 py-2 bg-white text-blue-700 rounded-md hover:bg-gray-100 flex items-center font-medium"
              onClick={resetFilters}
            >
              <RefreshCw size={16} className="mr-1" />
              Réinitialiser
            </button>
          </div>
        </div>
      </div>

      {/* Barre de recherche et filtres */}
      <div className="p-4 border-b border-gray-200">
        <div className="flex items-center bg-gray-50 rounded-md px-3 py-2 border border-gray-300 focus-within:ring-2 focus-within:ring-blue-500 focus-within:border-blue-500">
          <Search size={18} className="text-gray-400" />
          <input
            type="text"
            placeholder="Rechercher..."
            className="ml-2 flex-1 outline-none bg-transparent"
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
        </div>

        {/* Filtres supplémentaires */}
        {showFilters && (
          <div className="mt-4 p-4 bg-gray-50 rounded-md border border-gray-200">
            <h3 className="font-medium text-gray-700 mb-3">Filtrer par rôle</h3>
            <div className="flex flex-wrap gap-2">
              {roleOptions.map(role => (
                <button
                  key={role}
                  className={`px-3 py-1.5 rounded-full text-sm ${
                    activeFilters.role === role
                      ? 'bg-blue-100 text-blue-800 border border-blue-300'
                      : 'bg-gray-100 text-gray-700 hover:bg-gray-200 border border-gray-200'
                  }`}
                  onClick={() => setActiveFilters({
                    ...activeFilters,
                    role: activeFilters.role === role ? null : role
                  })}
                >
                  {role}
                </button>
              ))}
            </div>
          </div>
        )}
      </div>

      {/* Tableau */}
      <div className="overflow-x-auto">
        <table className="w-full">
          <thead>
            <tr className="bg-gray-50">
              <th className="w-10 px-4 py-3">
                <input
                  type="checkbox"
                  className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
                  onChange={() => {
                    if (selectedRows.length === sortedUsers.length) {
                      setSelectedRows([]);
                    } else {
                      setSelectedRows(sortedUsers.map(user => user.id));
                    }
                  }}
                  checked={selectedRows.length === sortedUsers.length && sortedUsers.length > 0}
                />
              </th>
              {['id', 'nom', 'prenom', 'role', 'tel'].map((column) => (
                <th 
                  key={column}
                  className="px-4 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider cursor-pointer"
                  onClick={() => handleSort(column)}
                >
                  <div className="flex items-center">
                    <span>{column === 'tel' ? 'Téléphone' : column.charAt(0).toUpperCase() + column.slice(1)}</span>
                    {sortColumn === column && (
                      <span className="ml-1">
                        {sortDirection === 'asc' ? <ChevronUp size={16} /> : <ChevronDown size={16} />}
                      </span>
                    )}
                  </div>
                </th>
              ))}
              <th className="px-4 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Actions</th>
            </tr>
          </thead>
          <tbody className="bg-white divide-y divide-gray-200">
            {sortedUsers.length > 0 ? (
              sortedUsers.map((user) => (
                <tr 
                  key={user.id}
                  className={`hover:bg-blue-50 transition-colors ${
                    selectedRows.includes(user.id) ? 'bg-blue-50' : ''
                  }`}
                >
                  <td className="px-4 py-3 whitespace-nowrap">
                    <input
                      type="checkbox"
                      className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
                      checked={selectedRows.includes(user.id)}
                      onChange={() => toggleRowSelection(user.id)}
                    />
                  </td>
                  <td className="px-4 py-3 whitespace-nowrap font-medium text-gray-900">{user.id}</td>
                  <td className="px-4 py-3 whitespace-nowrap text-gray-700">{user.nom}</td>
                  <td className="px-4 py-3 whitespace-nowrap text-gray-700">{user.prenom}</td>
                  <td className="px-4 py-3 whitespace-nowrap">
                    <span className={`inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium ${
                      user.role === 'Administrateur' ? 'bg-red-100 text-red-800' :
                      user.role === 'Modérateur' ? 'bg-yellow-100 text-yellow-800' :
                      'bg-green-100 text-green-800'
                    }`}>
                      {user.role}
                    </span>
                  </td>
                  <td className="px-4 py-3 whitespace-nowrap text-gray-700">{user.tel}</td>
                  <td className="px-4 py-3 whitespace-nowrap text-sm font-medium">
                    <div className="flex space-x-2">
                      <button
                        onClick={() => handleEdit(user.id)}
                        className="text-indigo-600 hover:text-indigo-900 bg-indigo-50 p-1.5 rounded-md hover:bg-indigo-100 transition-colors"
                      >
                        <Edit size={16} />
                      </button>
                      <button
                        onClick={() => handleDelete(user.id)}
                        className="text-red-600 hover:text-red-900 bg-red-50 p-1.5 rounded-md hover:bg-red-100 transition-colors"
                      >
                        <Trash2 size={16} />
                      </button>
                    </div>
                  </td>
                </tr>
              ))
            ) : (
              <tr>
                <td colSpan="7" className="px-4 py-8 text-center text-gray-500">
                  Aucun utilisateur trouvé
                </td>
              </tr>
            )}
          </tbody>
        </table>
      </div>

      {/* Pagination */}
      <div className="px-4 py-3 border-t border-gray-200 bg-gray-50 flex items-center justify-between">
        <div className="flex-1 flex justify-between sm:hidden">
          <button className="px-4 py-2 border border-gray-300 rounded-md text-sm font-medium text-gray-700 bg-white hover:bg-gray-50">
            Précédent
          </button>
          <button className="ml-3 px-4 py-2 border border-gray-300 rounded-md text-sm font-medium text-gray-700 bg-white hover:bg-gray-50">
            Suivant
          </button>
        </div>
        <div className="hidden sm:flex-1 sm:flex sm:items-center sm:justify-between">
          <div>
            <p className="text-sm text-gray-700">
              Affichage de <span className="font-medium">1</span> à <span className="font-medium">{sortedUsers.length}</span> sur <span className="font-medium">{sortedUsers.length}</span> résultats
            </p>
          </div>
          <div>
            <nav className="relative z-0 inline-flex rounded-md shadow-sm -space-x-px" aria-label="Pagination">
              <button className="relative inline-flex items-center px-2 py-2 rounded-l-md border border-gray-300 bg-white text-sm font-medium text-gray-500 hover:bg-gray-50">
                Précédent
              </button>
              <button className="relative inline-flex items-center px-4 py-2 border border-gray-300 bg-blue-50 text-sm font-medium text-blue-600">
                1
              </button>
              <button className="relative inline-flex items-center px-4 py-2 border border-gray-300 bg-white text-sm font-medium text-gray-500 hover:bg-gray-50">
                2
              </button>
              <button className="relative inline-flex items-center px-4 py-2 border border-gray-300 bg-white text-sm font-medium text-gray-500 hover:bg-gray-50">
                3
              </button>
              <button className="relative inline-flex items-center px-2 py-2 rounded-r-md border border-gray-300 bg-white text-sm font-medium text-gray-500 hover:bg-gray-50">
                Suivant
              </button>
            </nav>
          </div>
        </div>
      </div>
    </div>
  );
}
