import React, { useEffect, useState } from "react";
import axios from "axios";

export default function CardStats() {
  const [stats, setStats] = useState(null);

  useEffect(() => {
    axios.get("/stats") // adapte selon ton backend
      .then((res) => setStats(res.data))
      .catch((err) => console.error("Erreur chargement stats:", err));
  }, []);

  if (!stats) return <div>Chargement des statistiques...</div>;

  return (
    <div className="flex flex-wrap justify-center gap-6">
      <StatCard
        label="UTILISATEURS"
        value={stats.utilisateurs}
        icon="fas fa-users"
        color="bg-lightBlue-500"
      />
      <StatCard
        label="TECHNICIENS"
        value={stats.techniciens}
        icon="fas fa-tools"
        color="bg-orange-500"
      />
      <StatCard
        label="ADMINS"
        value={stats.admins}
        icon="fas fa-user-shield"
        color="bg-red-500"
      />
    </div>
  );
}

function StatCard({ label, value, icon, color }) {
  return (
    <div className="w-full sm:w-1/2 md:w-1/3 p-4">
      <div className="bg-white rounded-lg shadow-lg p-4 flex items-center">
        <div className={`text-white text-3xl p-4 rounded-full mr-4 ${color}`}>
          <i className={icon}></i>
        </div>
        <div>
          <p className="text-sm text-gray-500 uppercase font-bold">{label}</p>
          <h3 className="text-2xl font-semibold text-gray-800">{value}</h3>
          <p className="text-green-500 text-sm mt-1">+100% Nombre total</p>
        </div>
      </div>
    </div>
  );
}
