import React, { useState, useEffect } from "react";
import IndexNavbar from "components/Navbars/IndexNavbar.js";
import Footerr from "components/Footers/Footerr";

function MyTickets() {
  const [tickets, setTickets] = useState([]);
  const [selectedTicket, setSelectedTicket] = useState(null);
  const [searchId, setSearchId] = useState("");
  const [isEditing, setIsEditing] = useState(false);
  const [editedTicket, setEditedTicket] = useState(null);

  useEffect(() => {
    document.body.style.margin = "0";
    document.body.style.minHeight = "100vh";
    document.body.style.display = "flex";
    document.body.style.flexDirection = "column";

    const fetchTickets = async () => {
      try {
        const response = await fetch('http://localhost:3001/alltickets');
        const data = await response.json();
        setTickets(data);
      } catch (error) {
        console.error("Erreur lors de la récupération des tickets:", error);
      }
    };
    
    fetchTickets();
  }, []);

  const status = (statut) => ({
    padding: "0.3rem 0.8rem",
    borderRadius: "4px",
    fontSize: "0.85rem",
    backgroundColor: {
      "Ouvert": "#ffebee",
      "En cours": "#e3f2fd",
      "Résolu": "#e8f5e9",
    }[statut],
    color: {
      "Ouvert": "#c62828",
      "En cours": "#1565c0",
      "Résolu": "#2e7d32",
    }[statut],
    fontWeight: "bold",
  });

  const handleUpdateTicket = async (updatedTicket) => {
    try {
      const response = await fetch(`http://localhost:3001/updateticket/${updatedTicket.id}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(updatedTicket),
      });

      if (response.ok) {
        setTickets(tickets.map(ticket => 
          ticket.id === updatedTicket.id ? updatedTicket : ticket
        ));
        setSelectedTicket(updatedTicket);
      }
    } catch (error) {
      console.error("Erreur lors de la mise à jour du ticket:", error);
    }
  };

  const handleEditClick = () => {
    setIsEditing(true);
    setEditedTicket({ ...selectedTicket });
  };

  const handleSaveClick = async () => {
    await handleUpdateTicket(editedTicket);
    setIsEditing(false);
  };

  const filteredTickets = tickets.filter(ticket =>
    searchId === "" || ticket.id.toString().includes(searchId)
  );

  return (
    <>
      <IndexNavbar />
      <div style={container}>
        <div style={contentWrapper}>
          <img 
            src="/images/support-banner.jpg" 
            alt="Bannière Support"
            style={bannerImage}
          />
          <h1 style={title}>Espace Support Client</h1>
          <p style={subtitle}>Consultez vos tickets et suivez leur avancement</p>

          <div style={{ marginBottom: "1rem", textAlign: "center" }}>
            <input
              type="text"
              placeholder="Rechercher par numéro de ticket"
              value={searchId}
              onChange={(e) => setSearchId(e.target.value)}
              style={searchInput}
            />
          </div>

          <div style={ticketGrid}>
            <div style={ticketList}>
              {filteredTickets.map((ticket) => (
                <div 
                  key={ticket.id}
                  style={ticketItem} 
                  onClick={() => setSelectedTicket(ticket)}
                >
                  <div style={ticketHeader}>
                    <span style={ticketId}>#{ticket.id}</span>
                    <span style={status(ticket.statut)}>{ticket.statut}</span>
                  </div>
                  <h3 style={ticketTitle}>{ticket.sujet}</h3>
                  <p style={ticketDate}>{ticket.date}</p>
                </div>
              ))}
              {filteredTickets.length === 0 && (
                <p style={{ textAlign: "center", color: "#999" }}>Aucun ticket trouvé.</p>
              )}
            </div>

            {selectedTicket && (
              <div style={detailsPanel}>
                <div style={detailsHeader}>
                  <h2 style={detailsTitle}>Détails du ticket</h2>
                  <button 
                    style={closeButton} 
                    onClick={() => {
                      setSelectedTicket(null);
                      setIsEditing(false);
                    }}
                  >×</button>
                </div>
                <div style={detailsContent}>
                  {isEditing ? (
                    <>
                      <div style={detailRow}>
                        <span style={detailLabel}>Statut :</span>
                        <select 
                          value={editedTicket.statut}
                          onChange={(e) => setEditedTicket({...editedTicket, statut: e.target.value})}
                          style={status(editedTicket.statut)}
                        >
                          <option value="Ouvert">Ouvert</option>
                          <option value="En cours">En cours</option>
                          <option value="Résolu">Résolu</option>
                        </select>
                      </div>
                      <div style={detailRow}>
                        <span style={detailLabel}>Sujet :</span>
                        <input
                          value={editedTicket.sujet}
                          onChange={(e) => setEditedTicket({...editedTicket, sujet: e.target.value})}
                          style={editInput}
                        />
                      </div>
                      <div style={detailDescription}>
                        <p style={detailLabel}>Description :</p>
                        <textarea
                          value={editedTicket.description}
                          onChange={(e) => setEditedTicket({...editedTicket, description: e.target.value})}
                          style={textArea}
                        />
                      </div>
                      <button onClick={handleSaveClick} style={saveButton}>
                        Enregistrer
                      </button>
                    </>
                  ) : (
                    <>
                      <div style={detailRow}>
                        <span style={detailLabel}>Numéro :</span>
                        <span>#{selectedTicket.id}</span>
                      </div>
                      <div style={detailRow}>
                        <span style={detailLabel}>Statut :</span>
                        <span style={status(selectedTicket.statut)}>{selectedTicket.statut}</span>
                      </div>
                      <div style={detailRow}>
                        <span style={detailLabel}>Date :</span>
                        <span>{selectedTicket.date}</span>
                      </div>
                      <div style={detailRow}>
                        <span style={detailLabel}>Sujet :</span>
                        <span>{selectedTicket.sujet}</span>
                      </div>
                      <div style={detailDescription}>
                        <p style={detailLabel}>Description :</p>
                        <p>{selectedTicket.description}</p>
                      </div>
                      <button onClick={handleEditClick} style={editButton}>
                        Modifier
                      </button>
                    </>
                  )}
                </div>
              </div>
            )}
          </div>
        </div>
      </div>
      <Footerr />
    </>
  );
}

// Nouveaux styles ajoutés
const searchInput = {
  padding: "0.5rem",
  width: "60%",
  maxWidth: "300px",
  borderRadius: "6px",
  border: "1px solid #ccc",
  fontSize: "1rem"
};

const editInput = {
  width: "70%",
  padding: "0.3rem",
  border: "1px solid #ddd",
  borderRadius: "4px"
};

const textArea = {
  width: '100%',
  minHeight: '100px',
  padding: '0.5rem',
  border: '1px solid #ddd',
  borderRadius: '4px',
  resize: 'vertical'
};

const editButton = {
  padding: "0.5rem 1rem",
  backgroundColor: "#2196F3",
  color: "white",
  border: "none",
  borderRadius: "4px",
  cursor: "pointer",
  marginTop: "1rem",
  float: "right"
};

const saveButton = {
  ...editButton,
