import React, { useState } from "react";
import PropTypes from "prop-types";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faTrash, faPen, faSquareCheck } from '@fortawesome/free-solid-svg-icons';

// Styles pour les dialogues et les modals
const styles = `
  .fixed-dialog-container {
    position: fixed;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background-color: rgba(0, 0, 0, 0.5);
    display: flex;
    align-items: center;
    justify-content: center;
    z-index: 50;
  }

  .dialog-content {
    background-color: #f7fafc;
    padding: 2rem;
    border-radius: 0.5rem;
    box-shadow: 0 4px 10px rgba(0, 0, 0, 0.1);
    width: 100%;
    max-width: 30rem;
  }

  .modal-container {
    position: fixed;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background-color: rgba(0, 0, 0, 0.5);
    display: flex;
    align-items: center;
    justify-content: center;
    z-index: 50;
  }

  .modal-content {
    background-color: white;
    padding: 2rem;
    border-radius: 0.5rem;
    box-shadow: 0 4px 10px rgba(0, 0, 0, 0.1);
    width: 100%;
    max-width: 30rem;
  }

  .modal-content button {
    padding: 0.75rem 1.5rem;
    border-radius: 0.25rem;
    cursor: pointer;
    transition: background-color 0.3s;
  }

  .modal-content button.cancel {
    background-color: #e2e8f0;
    margin-right: 1rem;
    color: #4a5568;
  }

  .modal-content button.cancel:hover {
    background-color: #cbd5e0;
  }

  .modal-content button.submit {
    background-color: #38a169;
    color: white;
  }

  .modal-content button.submit:hover {
    background-color: #2f855a;
  }
`;

export default function CardTable({ color }) {
  const [items, setItems] = useState([
    { id: 1, subject: 'Problème d\'accès à l\'internet', status: 'En attente', date: '2025-03-29', description: 'Le site intranet était inaccessible depuis plusieurs jours.' }
  ]);
  const [isDialogOpen, setIsDialogOpen] = useState(false);
  const [selectedItemId, setSelectedItemId] = useState(null);

  // Fonction pour afficher la confirmation de suppression
  const handleDeleteConfirmation = (id) => {
    setSelectedItemId(id);
    setIsDialogOpen(true);
  };

  const handleConfirmDelete = () => {
    setItems(items.filter(item => item.id !== selectedItemId));
    setIsDialogOpen(false);
  };

  const handleCancelDelete = () => {
    setIsDialogOpen(false);
    setSelectedItemId(null);
  };

  const handleComplete = (id) => {
    setItems(items.map(item =>
      item.id === id ? { ...item, status: 'Terminé' } : item
    ));
  };

  const handleEdit = (id) => {
    setItems(items.map(item =>
      item.id === id ? { ...item, subject: 'Problème résolu', description: 'Le problème a été résolu avec succès.' } : item
    ));
  };

  return (
    <div
      className={
        "relative flex flex-col min-w-0 break-words w-full mb-6 shadow-lg rounded " +
        (color === "light" ? "bg-white" : "bg-lightBlue-900 text-white")
      }
    >
      {/* Injection des styles CSS */}
      <style>{styles}</style>

      {/* Dialog de confirmation de suppression */}
      {isDialogOpen && (
        <div className="fixed-dialog-container">
          <div className="dialog-content">
            <p className="text-lg text-gray-700 mb-4">
              Êtes-vous sûr de vouloir supprimer ce ticket ?
            </p>
            <div className="flex justify-end space-x-4">
              <button
                onClick={handleCancelDelete}
                className="px-4 py-2 bg-gray-300 text-gray-700 rounded hover:bg-gray-400 focus:outline-none"
              >
                Annuler
              </button>
              <button
                onClick={handleConfirmDelete}
                className="px-4 py-2 bg-red-500 text-white rounded hover:bg-red-600 focus:outline-none"
              >
                Supprimer
              </button>
            </div>
          </div>
        </div>
      )}

      <div className="block w-full overflow-x-auto">
        <table className="items-center w-full bg-transparent border-collapse">
          <thead>
            <tr>
              {["Id", "Sujet", "Statut", "Date", "Description", "Actions"].map((header, index) => (
                <th
                  key={index}
                  className={
                    "px-6 py-3 border border-solid text-xs uppercase whitespace-nowrap font-semibold text-left " +
                    (color === "light"
                      ? "bg-blueGray-50 text-blueGray-500 border-blueGray-100"
                      : "bg-lightBlue-800 text-lightBlue-300 border-lightBlue-700")
                  }
                >
                  {header}
                </th>
              ))}
            </tr>
          </thead>
          <tbody>
            {items.map((item) => (
              <tr key={item.id} className="hover:bg-gray-100 transition-colors duration-200">
                <td className="border-t p-4 text-xs whitespace-nowrap">
                  <strong>{item.id}</strong>
                </td>
                <td className="border-t p-4 text-xs whitespace-nowrap">
                  {item.subject}
                </td>
                <td className="border-t p-4 text-xs whitespace-nowrap">
                  <span className="flex items-center">
                    <i className={`fas fa-circle ${item.status === 'En attente' ? 'text-orange-500' : 'text-green-500'} mr-2`}></i> {item.status}
                  </span>
                </td>
                <td className="border-t p-4 text-xs whitespace-nowrap">
                  {item.date}
                </td>
                <td className="border-t p-4 text-xs whitespace-nowrap">
                  {item.description}
                </td>
                <td className="border-t p-4 text-xs whitespace-nowrap">
                  <button
                    className="bg-red-500 hover:bg-red-600 text-white text-xs font-bold uppercase px-3 py-2 rounded shadow transition-all duration-150 mr-2"
                    onClick={() => handleDeleteConfirmation(item.id)}
                  >
                    <FontAwesomeIcon icon={faTrash} />
                  </button>
                  <button
                    className="bg-indigo-500 hover:bg-indigo-600 text-white text-xs font-bold uppercase px-3 py-2 rounded shadow transition-all duration-150 mr-2"
                    onClick={() => handleEdit(item.id)}
                  >
                    <FontAwesomeIcon icon={faPen} />
                  </button>
                  <button
                    className="bg-indigo-500 hover:bg-green-600 text-white text-xs font-bold uppercase px-3 py-2 rounded shadow transition-all duration-150"
                    onClick={() => handleComplete(item.id)}
                  >
                    <FontAwesomeIcon icon={faSquareCheck} />
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

CardTable.defaultProps = {
  color: "light",
};

CardTable.propTypes = {
  color: PropTypes.oneOf(["light", "dark"]),
};
