import React from "react";
import IndexNavbar from "components/Navbars/IndexNavbar.js";
import Footerr from "components/Footers/Footerr";
import { Link } from "react-router-dom";

function NewTicketForm() {
  return (
    <>
      <IndexNavbar />

      <div style={pageContainer}>
        <div style={cardWrapper}>
          {/* Formulaire à gauche */}
          <div style={formContainer}>
            <div style={headerStyle}>
              <h2 style={titleStyle}>Nouveau ticket</h2>
              <Link to="MyTickets">
                <button style={backButtonStyle}>← Retour</button>
              </Link>
            </div>

            <div style={formStyle}>
              <div style={groupStyle}>
                <label style={labelStyle}>Sujet</label>
                <input
                  type="text"
                  placeholder="Ex : Erreur lors de virement"
                  style={inputStyle}
                />
              </div>

              <div style={groupStyle}>
                <label style={labelStyle}>Département</label>
                <select style={inputStyle}>
                  <option>IT - Informatique</option>
                  <option>RH - Ressources Humaines</option>
                  <option>Comptabilité</option>
                </select>
              </div>

              <div style={groupStyle}>
                <label style={labelStyle}>Type de ticket</label>
                <select style={inputStyle}>
                  <option>Urgent</option>
                  <option>Normal</option>
                  <option>Faible priorité</option>
                </select>
              </div>

              <div style={groupStyle}>
                <label style={labelStyle}>Description</label>
                <textarea
                  rows="5"
                  placeholder="Décrivez votre demande ici..."
                  style={textareaStyle}
                />
              </div>

              <div style={buttonContainer}>
                <button style={submitButtonStyle}>Créer le ticket</button>
              </div>
            </div>
          </div>

          {/* Image à droite */}
          <div style={imageContainer}>
            <img
              src="https://www.stb.com.tn/wp-content/uploads/2020/01/logo-stb.jpg"
              alt="STB Banque"
              style={imageStyle}
            />
          </div>
        </div>
      </div>

      <Footerr />
    </>
  );
}

// 🎨 Styles
const pageContainer = {
  minHeight: "90vh",
  backgroundColor: "#f5f7fa",
  padding: "2rem",
  display: "flex",
  justifyContent: "center",
  alignItems: "center",
};

const cardWrapper = {
  display: "flex",
  flexDirection: "row",
  width: "100%",
  maxWidth: "1000px",
  backgroundColor: "#fff",
  borderRadius: "12px",
  boxShadow: "0 4px 16px rgba(0,0,0,0.08)",
  overflow: "hidden",
};

const formContainer = {
  flex: 1,
  padding: "2rem",
};

const imageContainer = {
  flex: 1,
  backgroundColor: "#e3eaf3",
  display: "flex",
  justifyContent: "center",
  alignItems: "center",
};

const imageStyle = {
  width: "80%",
  height: "auto",
  objectFit: "contain",
};

const headerStyle = {
  display: "flex",
  justifyContent: "space-between",
  alignItems: "center",
  marginBottom: "1.5rem",
};

const titleStyle = {
  fontSize: "1.6rem",
  fontWeight: "600",
  color: "#003366",
};

const backButtonStyle = {
  background: "none",
  border: "none",
  color: "#003366",
  fontWeight: "600",
  cursor: "pointer",
};

const formStyle = {
  display: "flex",
  flexDirection: "column",
  gap: "1.2rem",
};

const groupStyle = {
  display: "flex",
  flexDirection: "column",
};

const labelStyle = {
  marginBottom: "0.5rem",
  fontWeight: "500",
  color: "#333",
};

const inputStyle = {
  padding: "0.8rem",
  border: "1px solid #ccc",
  borderRadius: "6px",
  fontSize: "1rem",
};

const textareaStyle = {
  ...inputStyle,
  resize: "vertical",
  minHeight: "100px",
};

const buttonContainer = {
  display: "flex",
  justifyContent: "flex-end",
};

const submitButtonStyle = {
  backgroundColor: "#003366",
  color: "#fff",
  padding: "0.9rem 1.8rem",
  border: "none",
  borderRadius: "6px",
  fontWeight: "600",
  fontSize: "1rem",
  cursor: "pointer",
};

export default NewTicketForm;
