import React, { useEffect, useState } from "react";
import axios from 'axios';
import PropTypes from "prop-types";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faTrash, faPen } from "@fortawesome/free-solid-svg-icons";

export default function CardTable({ color }) {
  const [users, setUsers] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [editUser, setEditUser] = useState({
    nom: "",
    prenom: "",
    email: "",
    id: "",
    role: "",
    tel: "",
    password: ""
  });

  // Chargement initial des utilisateurs
  useEffect(() => {
    axios.get("/allusers")
      .then(res => setUsers(res.data))
      .catch(err => console.error("Erreur chargement:", err));
  }, []);

  // Gestion des modifications de formulaire
  const handleInputChange = (e) => {
    setEditUser({ ...editUser, [e.target.name]: e.target.value });
  };

  // Suppression d'utilisateur
  const handleDelete = (userId, index) => {
    if (window.confirm("Confirmer la suppression ?")) {
      axios.delete(`/deleteuser/${userId}`)
        .then(() => {
          const newUsers = [...users];
          newUsers.splice(index, 1);
          setUsers(newUsers);
        })
        .catch(err => console.error("Erreur suppression:", err));
    }
  };

  // Mise à jour d'utilisateur
  const handleSubmit = (e) => {
    e.preventDefault();
    
    axios.put(`/updateuser/${editUser.id}`, editUser)
      .then(res => {
        setUsers(users.map(user => 
          user.id === editUser.id ? res.data : user
        ));
        setShowForm(false);
      })
      .catch(err => console.error("Erreur mise à jour:", err));
  };

  // Préparation de l'édition
  const prepareEdit = (user) => {
    setEditUser({ ...user, password: "" });
    setShowForm(true);
  };

  return (
    <div className={`relative flex flex-col min-w-0 break-words w-full mb-6 shadow-lg rounded-lg 
      ${color === "light" ? "bg-white" : "bg-blue-900 text-white"}`}>

      <div className="px-6 py-4 border-b">
        <h3 className={`text-xl font-bold ${color === "light" ? "text-gray-800" : "text-white"}`}>
          Liste des Utilisateurs
        </h3>
      </div>

      <div className="p-4 overflow-x-auto">
        <table className="w-full">
          <thead className="text-xs uppercase bg-blue-800 text-white">
            <tr>
              <th className="px-6 py-3">ID</th>
              <th className="px-6 py-3">Nom</th>
              <th className="px-6 py-3">Prénom</th>
              <th className="px-6 py-3">Email</th>
              <th className="px-6 py-3">Rôle</th>
              <th className="px-6 py-3">Téléphone</th>
              <th className="px-6 py-3 text-right">Actions</th>
            </tr>
          </thead>
          <tbody>
            {users.map((user, index) => (
              <tr key={user.id} className="hover:bg-blue-50">
                <td className="px-6 py-4">{user.id}</td>
                <td className="px-6 py-4">{user.nom}</td>
                <td className="px-6 py-4">{user.prenom}</td>
                <td className="px-6 py-4">{user.email}</td>
                <td className="px-6 py-4">{user.role}</td>
                <td className="px-6 py-4">{user.tel}</td>
                <td className="px-6 py-4 text-right">
                  <button
                    onClick={() => prepareEdit(user)}
                    className="text-blue-500 hover:text-blue-700 mx-2"
                  >
                    <FontAwesomeIcon icon={faPen} />
                  </button>
                  <button
                    onClick={() => handleDelete(user.id, index)}
                    className="text-red-500 hover:text-red-700 mx-2"
                  >
                    <FontAwesomeIcon icon={faTrash} />
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>

        {showForm && (
          <form onSubmit={handleSubmit} className="mt-6 p-6 bg-white shadow-lg rounded-lg">
            <h4 className="text-lg font-bold mb-4">Modifier l'utilisateur</h4>

            <div className="grid grid-cols-2 gap-4">
              <div>
                <label className="block mb-2">ID</label>
                <input
                  value={editUser.id}
                  className="w-full p-2 bg-gray-100 rounded"
                  disabled
                />
              </div>

              <div>
                <label className="block mb-2">Nom *</label>
                <input
                  name="nom"
                  value={editUser.nom}
                  onChange={handleInputChange}
                  className="w-full p-2 border rounded"
                  required
                />
              </div>

              <div>
                <label className="block mb-2">Prénom *</label>
                <input
                  name="prenom"
                  value={editUser.prenom}
                  onChange={handleInputChange}
                  className="w-full p-2 border rounded"
                  required
                />
              </div>

              <div>
                <label className="block mb-2">Email *</label>
                <input
                  type="email"
                  name="email"
                  value={editUser.email}
                  onChange={handleInputChange}
                  className="w-full p-2 border rounded"
                  required
                />
              </div>

              <div>
                <label className="block mb-2">Rôle *</label>
                <select
                  name="role"
                  value={editUser.role}
                  onChange={handleInputChange}
                  className="w-full p-2 border rounded"
                  required
                >
                  <option value="user">Utilisateur</option>
                  <option value="admin">Administrateur</option>
                </select>
              </div>

              <div>
                <label className="block mb-2">Téléphone</label>
                <input
                  name="tel"
                  value={editUser.tel}
                  onChange={handleInputChange}
                  className="w-full p-2 border rounded"
                />
              </div>

              <div>
                <label className="block mb-2">Nouveau mot de passe</label>
                <input
                  type="password"
                  name="password"
                  value={editUser.password}
                  onChange={handleInputChange}
                  className="w-full p-2 border rounded"
                  placeholder="Laisser vide pour garder actuel"
                />
              </div>
            </div>

            <div className="mt-6 flex justify-end gap-3">
              <button
                type="button"
                onClick={() => setShowForm(false)}
                className="px-4 py-2 text-gray-600"
              >
                Annuler
              </button>
              <button
                type="submit"
                className="px-4 py-2 bg-blue-500 text-white rounded hover:bg-blue-600"
              >
                Sauvegarder
              </button>
            </div>
          </form>
        )}
      </div>
    </div>
  );
}

CardTable.propTypes = {
  color: PropTypes.oneOf(["light", "dark"]),
};
