import { useState } from "react";
import { FaGoogle } from "react-icons/fa";
import axios from "axios"; // Assure-toi que axios est bien installé

export default function Register() {
  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  const [email, setEmail] = useState("");
  const [phoneNumber, setPhoneNumber] = useState("");
  const [message, setMessage] = useState("");

  const handleRegister = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.post("/adduser", {
        nom: firstName,
        prenom: lastName,
        email: email,
        tel: phoneNumber,
      });

      if (response.status === 201) {
        setMessage("Utilisateur ajouté avec succès !");
      }
    } catch (error) {
      console.error("Erreur lors de l'ajout de l'utilisateur :", error);
      setMessage("Erreur lors de l'ajout de l'utilisateur !");
    }
  };

  return (
    <div
      style={{
        display: "flex",
        height: "100vh",
        alignItems: "center",
        justifyContent: "center",
        backgroundImage:
          "url('https://img.freepik.com/premium-vector/abstract-geometric-white-gray-color-background-vector_665257-159.jpg?semt=ais_hybrid')",
        backgroundSize: "cover",
        backgroundPosition: "center",
        padding: "16px",
      }}
    >
      <form
        onSubmit={handleRegister}
        style={{
          backgroundColor: "white",
          padding: "24px",
          borderRadius: "10px",
          boxShadow: "0 4px 10px rgba(0,0,0,0.1)",
          display: "flex",
          flexDirection: "column",
          gap: "12px",
          width: "300px"
        }}
      >
        <h2 style={{ textAlign: "center", color: "#1f2937" }}>Inscription</h2>

        <input
          type="text"
          placeholder="Nom"
          value={firstName}
          onChange={(e) => setFirstName(e.target.value)}
          required
          style={{ padding: "8px", borderRadius: "6px", border: "1px solid #d1d5db" }}
        />
        <input
          type="text"
          placeholder="Prenom"
          value={lastName}
          onChange={(e) => setLastName(e.target.value)}
          required
          style={{ padding: "8px", borderRadius: "6px", border: "1px solid #d1d5db" }}
        />
        <input
          type="email"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
          style={{ padding: "8px", borderRadius: "6px", border: "1px solid #d1d5db" }}
        />
        <input
          type="tel"
          placeholder="Téléphone"
          value={phoneNumber}
          onChange={(e) => setPhoneNumber(e.target.value)}
          required
          style={{ padding: "8px", borderRadius: "6px", border: "1px solid #d1d5db" }}
        />
        <button
          type="submit"
          style={{
            padding: "10px",
            borderRadius: "6px",
            backgroundColor: "#3b82f6",
            color: "white",
            fontWeight: "bold",
            cursor: "pointer",
          }}
        >
          REGISTER
        </button>
        {message && (
          <p style={{ textAlign: "center", color: "#6b7280" }}>{message}</p>
        )}
      </form>
    </div>
  );
}
