import React from 'react';
import { FaUserCircle } from 'react-icons/fa'; // npm install react-icons

function Headeruser() {
  return (
    <div
      style={{
        width: '100%',
        height: '320px',
        background: 'linear-gradient(to right, #e0eafc, #cfdef3)', // Dégradé bleu clair
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        flexDirection: 'column',
        color: '#1f2937', // Gris très foncé (pro)
        textAlign: 'center',
        borderBottom: '2px solid #d1d9e6',
        boxShadow: '0 2px 6px rgba(0, 0, 0, 0.05)',
        fontFamily: 'Segoe UI, Roboto, sans-serif',
      }}
    >
      <FaUserCircle size={60} color="#3b82f6" style={{ marginBottom: '10px' }} />
      <h1 style={{ margin: 0, fontSize: '2.4rem', fontWeight: '600' }}>
        Espace Personnel
      </h1>
      <p style={{ fontSize: '1.1rem', marginTop: '12px', color: '#374151' }}>
        Modifier mes informations personnelles
      </p>
    </div>
  );
}

export default Headeruser;
