import React, { useEffect, useState } from "react";
import PropTypes from "prop-types";
import axios from "axios";

// Composant de carte individuelle (inchangé)
function CardStats({
  statSubtitle,
  statTitle,
  statArrow,
  statPercent,
  statPercentColor,
  statDescripiron,
  statIconName,
  statIconColor,
}) {
  return (
    <div className="relative flex flex-col min-w-0 break-words bg-white rounded shadow-lg p-4 w-full md:w-1/3 m-2">
      <div className="flex justify-between items-center">
        <div>
          <h5 className="uppercase text-blueGray-400 text-xs font-bold">{statSubtitle}</h5>
          <span className="font-semibold text-xl text-blueGray-700">{statTitle}</span>
        </div>
        <div className={"text-white p-3 text-center inline-flex items-center justify-center w-12 h-12 shadow-lg rounded-full " + statIconColor}>
          <i className={statIconName}></i>
        </div>
      </div>
      <p className="text-sm text-blueGray-400 mt-4">
        <span className={statPercentColor + " mr-2"}>
          <i className={statArrow === "up" ? "fas fa-arrow-up" : statArrow === "down" ? "fas fa-arrow-down" : ""}></i> {statPercent}%
        </span>
        <span className="whitespace-nowrap">{statDescripiron}</span>
      </p>
    </div>
  );
}


CardStats.defaultProps = {
  statSubtitle: "Titre",
  statTitle: "0",
  statArrow: "up",
  statPercent: "100",
  statPercentColor: "text-emerald-500",
  statDescripiron: "",
  statIconName: "fas fa-info-circle",
  statIconColor: "bg-gray-400",
};

CardStats.propTypes = {
  statSubtitle: PropTypes.string,
  statTitle: PropTypes.string,
  statArrow: PropTypes.oneOf(["up", "down"]),
  statPercent: PropTypes.string,
  statPercentColor: PropTypes.string,
  statDescripiron: PropTypes.string,
  statIconName: PropTypes.string,
  statIconColor: PropTypes.string,
};

// ⬇️ Nouveau composant qui affiche les 3 cartes
export default function CardStatsSection() {
  const [stats, setStats] = useState({
    totalUsers: 0,
    techniciens: 0,
    admins: 0,
  });

  useEffect(() => {
    axios
      .get("/api/stats")
      .then((res) => {
        setStats({
          totalUsers: res.data.totalUsers,
          techniciens: res.data.techniciens,
          admins: res.data.admins,
        });
      })
      .catch((err) => {
        console.error("Erreur chargement stats:", err);
      });
  }, []);

  return (
    <div className="flex flex-wrap">
      <div className="w-full lg:w-4/12 xl:w-4/12 px-4">
        <CardStats
          statSubtitle="UTILISATEURS"
          statTitle={stats.totalUsers.toString()}
          statDescripiron="Nombre total"
          statIconName="fas fa-users"
          statIconColor="bg-blue-500"
        />
      </div>
      <div className="w-full lg:w-4/12 xl:w-4/12 px-4">
        <CardStats
          statSubtitle="TECHNICIENS"
          statTitle={stats.techniciens.toString()}
          statDescripiron="Nombre total"
          statIconName="fas fa-tools"
          statIconColor="bg-green-500"
        />
      </div>
      <div className="w-full lg:w-4/12 xl:w-4/12 px-4">
        <CardStats
          statSubtitle="ADMINS"
          statTitle={stats.admins.toString()}
          statDescripiron="Nombre total"
          statIconName="fas fa-user-shield"
          statIconColor="bg-red-500"
        />
      </div>
    </div>
  );
}
