import React, { useState } from "react";
import IndexNavbar from "components/Navbars/IndexNavbar.js";
import Footerr from "components/Footers/Footerr";

function MyTickets() {
  const [selectedTicket, setSelectedTicket] = useState(null);

  const tickets = [
    {
      id: 1,
      sujet: "Connexion VPN bloquée",
      departement: "IT",
      statut: "Ouvert",
      date: "2025-04-04",
      description: "Je n'arrive plus à me connecter au VPN depuis ce matin.",
    },
    {
      id: 2,
      sujet: "Erreur sur fiche de paie",
      departement: "RH",
      statut: "En cours",
      date: "2025-04-01",
      description: "Il y a une erreur dans le montant net affiché.",
    },
    {
      id: 3,
      sujet: "Problème d'accès à l'intranet",
      departement: "IT",
      statut: "Résolu",
      date: "2025-03-29",
      description: "Le site intranet était inaccessible depuis plusieurs jours.",
    },
  ];

  return (
    <>
      <IndexNavbar />

      <div style={pageWrapper}>
        <div style={backgroundOverlay}></div>

        <div style={contentBox}>
          <h1 style={title}>📋 Mes Tickets</h1>

          {tickets.map((ticket) => (
            <div key={ticket.id} style={ticketCard}>
              <div style={ticketHeader}>
                <h3>{ticket.sujet}</h3>
                <span style={statusStyle(ticket.statut)}>{ticket.statut}</span>
              </div>
              <p style={meta}>
                🏢 {ticket.departement} | 📅 {ticket.date}
              </p>
              <button
                style={detailBtn}
                onClick={() => setSelectedTicket(ticket)}
              >
                🔍 Voir Détails
              </button>
            </div>
          ))}

          {selectedTicket && (
            <div style={detailBox}>
              <h3 style={{ color: "#0d47a1" }}>🎫 Détails du ticket</h3>
              <p><strong>Sujet :</strong> {selectedTicket.sujet}</p>
              <p><strong>Département :</strong> {selectedTicket.departement}</p>
              <p><strong>Statut :</strong> {selectedTicket.statut}</p>
              <p><strong>Date :</strong> {selectedTicket.date}</p>
              <p><strong>Description :</strong> {selectedTicket.description}</p>
              <button onClick={() => setSelectedTicket(null)} style={closeBtn}>❌ Fermer</button>
            </div>
          )}
        </div>
      </div>

      <Footerr />
    </>
  );
}

// 🎨 STYLES
const pageWrapper = {
  minHeight: "100vh",
  backgroundImage: "url('/assets/images/bank-ui-background.png')",
  backgroundSize: "cover",
  backgroundPosition: "center",
  display: "flex",
  justifyContent: "center",
  alignItems: "center",
  position: "relative",
  padding: "3rem 1rem",
};

const backgroundOverlay = {
  position: "absolute",
  top: 0,
  left: 0,
  height: "100%",
  width: "100%",
  backgroundColor: "rgba(255,255,255,0.85)",
  backdropFilter: "blur(4px)",
  zIndex: 0,
};

const contentBox = {
  position: "relative",
  zIndex: 1,
  background: "#fff",
  borderRadius: "16px",
  padding: "2rem",
  boxShadow: "0 10px 40px rgba(0,0,0,0.1)",
  maxWidth: "900px",
  width: "100%",
};

const title = {
  textAlign: "center",
  fontSize: "2.2rem",
  fontWeight: "bold",
  color: "#0d47a1",
  marginBottom: "2rem",
};

const ticketCard = {
  background: "#f9f9f9",
  padding: "1.2rem",
  marginBottom: "1.2rem",
  borderRadius: "12px",
  borderLeft: "5px solid #0d47a1",
  boxShadow: "0 2px 8px rgba(0,0,0,0.05)",
};

const ticketHeader = {
  display: "flex",
  justifyContent: "space-between",
  alignItems: "center",
};

const statusStyle = (statut) => {
  let color;
  switch (statut) {
    case "Ouvert": color = "#ff9800"; break;
    case "En cours": color = "#2196f3"; break;
    case "Résolu": color = "#4caf50"; break;
    default: color = "#999";
  }
  return {
    backgroundColor: color,
    color: "#fff",
    padding: "4px 10px",
    borderRadius: "8px",
    fontSize: "0.8rem",
    fontWeight: "600",
  };
};

const meta = {
  fontSize: "0.95rem",
  color: "#555",
  marginTop: "0.5rem",
};

const detailBtn = {
  marginTop: "1rem",
  backgroundColor: "#0d47a1",
  color: "#fff",
  border: "none",
  borderRadius: "8px",
  padding: "0.5rem 1rem",
  cursor: "pointer",
};

const detailBox = {
  marginTop: "2rem",
  backgroundColor: "#f1f1f1",
  borderRadius: "12px",
  padding: "1.5rem",
};

const closeBtn = {
  marginTop: "1rem",
  backgroundColor: "#f44336",
  color: "#fff",
  border: "none",
  padding: "0.6rem 1.2rem",
  borderRadius: "8px",
  cursor: "pointer",
  fontWeight: "600",
};

export default MyTickets;
