import React, { useEffect, useState } from "react";
import PropTypes from "prop-types";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faTrash, faPen } from "@fortawesome/free-solid-svg-icons";
import { getAllUsers } from "services/ApiUser";

export default function CardTable() {
  const [users, setUsers] = useState([
    {
      nom: "Toumi",
      prenom: "Med Rayen",
      email: "toumirayen130@gmail.com",
      id: "05",
    },
  ]);
  const [showForm, setShowForm] = useState(false);
  const [newUser, setNewUser] = useState({
    nom: "",
    prenom: "",
    email: "",
    id: "",
  });

  const getUsers = async () => {
    try {
      const res = await getAllUsers();
      if (res.data?.userList) {
        setUsers(res.data.userList);
      }
    } catch (error) {
      console.log(error);
    }
  };

  useEffect(() => {
    // getUsers(); // DÃ©commente pour charger depuis l'API rÃ©elle
  }, []);

  const handleInputChange = (e) => {
    setNewUser({ ...newUser, [e.target.name]: e.target.value });
  };

  const handleAddUser = (e) => {
    e.preventDefault();
    setUsers([...users, newUser]);
    setNewUser({ nom: "", prenom: "", email: "", id: "" });
    setShowForm(false);
  };

  return (
    <div className="relative flex flex-col min-w-0 break-words w-full mb-6 shadow-xl rounded-2xl overflow-hidden bg-white text-black border border-black">
      <div className="flex justify-between items-center px-6 py-5 border-b border-black">
        <h3 className="font-bold text-2xl">ðŸ‘¤ Liste des utilisateurs</h3>
        <button
          className="bg-black hover:bg-gray-800 transition-all duration-200 text-white text-sm font-semibold px-4 py-2 rounded-xl shadow-md"
          onClick={() => setShowForm(!showForm)}
        >
          {showForm ? "Annuler" : "+ Ajouter un utilisateur"}
        </button>
      </div>

      <div className="overflow-x-auto p-4">
        <table className="w-full text-sm text-left rounded-lg overflow-hidden border border-black">
          <thead className="text-xs uppercase bg-gray-200 text-black border-b border-black">
            <tr>
              <th className="px-6 py-3 border-r border-black">Nom</th>
              <th className="px-6 py-3 border-r border-black">PrÃ©nom</th>
              <th className="px-6 py-3 border-r border-black">Email</th>
              <th className="px-6 py-3 border-r border-black">ID</th>
              <th className="px-6 py-3 text-right">Actions</th>
            </tr>
          </thead>
          <tbody>
            {users.map((user, index) => (
              <tr key={index} className="border-b border-black hover:bg-gray-100">
                <td className="px-6 py-4 border-r border-black">{user.nom}</td>
                <td className="px-6 py-4 border-r border-black">{user.prenom}</td>
                <td className="px-6 py-4 border-r border-black">{user.email}</td>
                <td className="px-6 py-4 border-r border-black">{user.id}</td>
                <td className="px-6 py-4 text-right">
                  <button className="bg-red-600 hover:bg-red-700 transition duration-200 text-white px-3 py-1 rounded-xl mr-2 text-xs shadow-md">
                    <FontAwesomeIcon icon={faTrash} />
                  </button>
                  <button className="bg-black hover:bg-gray-800 transition duration-200 text-white px-3 py-1 rounded-xl text-xs shadow-md">
                    <FontAwesomeIcon icon={faPen} />
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>

        {showForm && (
          <form
            onSubmit={handleAddUser}
            className="mt-6 bg-gray-100 p-6 rounded-2xl shadow-md space-y-4"
          >
            <h4 className="text-md font-semibold text-black">
              Ajouter un utilisateur
            </h4>
            <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
              <input
                type="text"
                name="nom"
                placeholder="Nom"
                value={newUser.nom}
                onChange={handleInputChange}
                className="p-2 bg-white border border-black text-black placeholder-gray-400 rounded-xl focus:outline-none focus:ring-2 focus:ring-black"
                required
              />
              <input
                type="text"
                name="prenom"
                placeholder="PrÃ©nom"
                value={newUser.prenom}
                onChange={handleInputChange}
                className="p-2 bg-white border border-black text-black placeholder-gray-400 rounded-xl focus:outline-none focus:ring-2 focus:ring-black"
                required
              />
              <input
                type="email"
                name="email"
                placeholder="Email"
                value={newUser.email}
                onChange={handleInputChange}
                className="p-2 bg-white border border-black text-black placeholder-gray-400 rounded-xl focus:outline-none focus:ring-2 focus:ring-black"
                required
              />
              <input
                type="text"
                name="id"
                placeholder="ID"
                value={newUser.id}
                onChange={handleInputChange}
                className="p-2 bg-white border border-black text-black placeholder-gray-400 rounded-xl focus:outline-none focus:ring-2 focus:ring-black"
                required
              />
            </div>
            <div className="flex justify-end pt-2">
              <button
                type="submit"
                className="bg-black hover:bg-gray-800 transition-all duration-200 text-white text-sm font-semibold px-5 py-2 rounded-xl shadow-md"
              >
                âœ… Ajouter
              </button>
            </div>
          </form>
        )}
      </div>
    </div>
  );
}
