import React, { useState, useEffect } from "react";

export default function CardSettings({ userData, onSave }) {
  const [role, setRole] = useState(userData?.role || "admin");
  const [service, setService] = useState(userData?.service || "");
  const [password, setPassword] = useState(userData?.password || "");

  // Fonction pour générer un mot de passe aléatoire
  const generatePassword = () => {
    const length = 12; // Longueur du mot de passe
    const charset = "abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ0123456789!@#$%^&*()";
    let password = "";
    for (let i = 0; i < length; i++) {
      const randomIndex = Math.floor(Math.random() * charset.length);
      password += charset[randomIndex];
    }
    return password;
  };

  useEffect(() => {
    // Si aucun mot de passe n'est défini et que c'est un nouvel utilisateur, générer un mot de passe
    if (!userData && !password) {
      setPassword(generatePassword());
    }
  }, [userData, password]);

  const handleRoleChange = (e) => {
    setRole(e.target.value);
    if (e.target.value !== "technician") {
      setService(""); // Reset service if not technician
    }
  };

  const handleSave = (e) => {
    e.preventDefault();
    // Sauvegarder l'utilisateur avec toutes les informations
    const user = {
      firstName: e.target.firstName.value,
      lastName: e.target.lastName.value,
      email: e.target.email.value,
      phone: e.target.phone.value,
      role,
      service,
      password
    };
    onSave(user); // Appeler la fonction pour sauvegarder les données
  };

  return (
    <div className="min-h-screen bg-blueGray-50 flex items-center justify-start pl-20">
      <div className="relative flex flex-col min-w-0 break-words w-full max-w-2xl mb-6 shadow-lg rounded-lg bg-gradient-to-r from-blue-400 to-blue-600 border-0">
        <div className="rounded-t bg-white mb-0 px-6 py-6">
          <div className="text-center flex justify-between">
            <h6 className="text-blueGray-700 text-xl font-bold">User Settings</h6>
          </div>
        </div>
        <div className="flex-auto px-4 lg:px-10 py-10 pt-0 bg-blueGray-50 rounded-b-lg">
          <form onSubmit={handleSave}>
            <h6 className="text-blueGray-400 text-sm mt-3 mb-6 font-bold uppercase">
              Personal Information
            </h6>
            <div className="flex flex-wrap">
              <div className="w-full lg:w-6/12 px-4 mb-4">
                <label className="block uppercase text-blueGray-600 text-xs font-bold mb-2" htmlFor="firstName">
                  First Name
                </label>
              
              </div>
              <div className="w-full lg:w-6/12 px-4 mb-4">
                <label className="block uppercase text-blueGray-600 text-xs font-bold mb-2" htmlFor="lastName">
                  Last Name
                </label>
                <input
                  type="text"
                  id="lastName"
                  defaultValue={userData?.lastName || "Med Rayen"}
                  className="border-2 border-blueGray-300 px-3 py-3 text-blueGray-600 bg-white rounded text-sm shadow w-full"
                />
              </div>
              <div className="w-full px-4 mb-4">
                <label className="block uppercase text-blueGray-600 text-xs font-bold mb-2" htmlFor="email">
                  Email
                </label>
        
              </div>
              <div className="w-full lg:w-6/12 px-4 mb-4">
                <label className="block uppercase text-blueGray-600 text-xs font-bold mb-2" htmlFor="phone">
                  Phone Number
                </label>
                <input
                  type="text"
                  id="phone"
                  defaultValue={userData?.phone || "+21658166733"}
                  className="border-2 border-blueGray-300 px-3 py-3 text-blueGray-600 bg-white rounded text-sm shadow w-full"
                />
              </div>
              <div className="w-full lg:w-6/12 px-4 mb-4">
                <label className="block uppercase text-blueGray-600 text-xs font-bold mb-2" htmlFor="role">
                  Role
                </label>
                <select
                  id="role"
                  value={role}
                  onChange={handleRoleChange}
                  className="border-2 border-blueGray-300 px-3 py-3 text-blueGray-600 bg-white rounded text-sm shadow w-full"
                >
                  <option value="admin">Admin</option>
                  <option value="user">Utilisateur</option>
                  <option value="technician">Technicien</option>
                </select>
              </div>

              {role === "technician" && (
                <div className="w-full px-4 mb-4">
                  <label className="block uppercase text-blueGray-600 text-xs font-bold mb-2" htmlFor="service">
                    Service / Domaine
                  </label>
                  <input
                    type="text"
                    id="service"
                    placeholder="ex: informatique, tech, RH..."
                    value={service}
                    onChange={(e) => setService(e.target.value)}
                    className="border-2 border-blueGray-300 px-3 py-3 text-blueGray-600 bg-white rounded text-sm shadow w-full"
                  />
                </div>
              )}

              {/* Champ mot de passe */}
              <div className="w-full lg:w-6/12 px-4 mb-4">
                <label className="block uppercase text-blueGray-600 text-xs font-bold mb-2" htmlFor="password">
                  Password
                </label>
                <input
                  type="text"
                  id="password"
                  value={password}
                  readOnly
                  className="border-2 border-blueGray-300 px-3 py-3 text-blueGray-600 bg-white rounded text-sm shadow w-full"
                />
              </div>
            </div>

            <div className="mt-6 text-center">
              <button
                type="submit"
                className="bg-lightBlue-500 text-white active:bg-lightBlue-600 font-bold uppercase text-xs px-4 py-2 rounded shadow hover:shadow-md"
              >
                Save Changes
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
}
