import React, { useState } from "react";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faTrash, faPen, faSquareCheck } from '@fortawesome/free-solid-svg-icons';

const styles = `
  .fixed-dialog-container {
    position: fixed;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background-color: rgba(0, 0, 0, 0.5);
    display: flex;
    align-items: flex-start;
    justify-content: center;
    padding-top: 6rem;
    z-index: 50;
  }

  .dialog-content {
    background-color: #fff;
    padding: 2rem;
    border-radius: 0.5rem;
    box-shadow: 0 4px 10px rgba(0, 0, 0, 0.1);
    width: 100%;
    max-width: 30rem;
    border-top: 6px solid #e53e3e;
  }

  .dialog-content button {
    padding: 0.75rem 1.5rem;
    border-radius: 0.25rem;
    cursor: pointer;
    transition: background-color 0.3s;
  }

  .dialog-content button.cancel {
    background-color: #e2e8f0;
    margin-right: 1rem;
    color: #4a5568;
  }

  .dialog-content button.cancel:hover {
    background-color: #cbd5e0;
  }

  .dialog-content button.submit {
    background-color: #e53e3e;
    color: white;
  }

  .dialog-content button.submit:hover {
    background-color: #c53030;
  }
`;

export default function TechnicianTable({ color = "light" }) {
  const [technicians, setTechnicians] = useState([
    { _id: 1, nom: "Ali Ben Salem", specialite: "Réseaux" },
    { _id: 2, nom: "Sami Trabelsi", specialite: "Systèmes" }
  ]);
  const [isDialogOpen, setIsDialogOpen] = useState(false);
  const [selectedTechId, setSelectedTechId] = useState(null);

  const handleAction = (action, id, nom) => {
    if (action === "supprimer") {
      setSelectedTechId(id);
      setIsDialogOpen(true);
    } else if (action === "modifier") {
      alert(`Modifier le technicien : ${nom}`);
    } else if (action === "completer") {
      alert(`Compléter l'action pour : ${nom}`);
    }
  };

  const handleConfirmDelete = () => {
    setTechnicians(technicians.filter(tech => tech._id !== selectedTechId));
    setIsDialogOpen(false);
  };

  const handleCancelDelete = () => {
    setIsDialogOpen(false);
    setSelectedTechId(null);
  };

  return (
    <div className={"relative flex flex-col min-w-0 break-words w-full mb-6 shadow-lg rounded " + (color === "light" ? "bg-white" : "bg-lightBlue-900 text-white")}>
      <style>{styles}</style>

      {isDialogOpen && (
        <div className="fixed-dialog-container">
          <div className="dialog-content">
            <p className="text-lg text-gray-700 mb-4 font-semibold">
              Êtes-vous sûr de vouloir supprimer ce technicien ?
            </p>
            <div className="flex justify-end space-x-4">
              <button onClick={handleCancelDelete} className="cancel">Annuler</button>
              <button onClick={handleConfirmDelete} className="submit">Supprimer</button>
            </div>
          </div>
        </div>
      )}

      <div className="block w-full overflow-x-auto">
        <table className="items-center w-full bg-transparent border-collapse">
          <thead>
            <tr>
              {["ID", "Nom", "Spécialité", "Actions"].map((header, index) => (
                <th key={index} className={"px-6 py-3 border border-solid text-xs uppercase whitespace-nowrap font-semibold text-left " + (color === "light" ? "bg-blueGray-50 text-blueGray-500 border-blueGray-100" : "bg-lightBlue-800 text-lightBlue-300 border-lightBlue-700")}>
                  {header}
                </th>
              ))}
            </tr>
          </thead>
          <tbody>
            {technicians.map((tech) => (
              <tr key={tech._id} className="hover:bg-gray-100 transition-colors duration-200">
                <td className="border-t p-4 text-xs whitespace-nowrap"><strong>{tech._id}</strong></td>
                <td className="border-t p-4 text-xs whitespace-nowrap">{tech.nom}</td>
                <td className="border-t p-4 text-xs whitespace-nowrap">{tech.specialite}</td>
                <td className="border-t p-4 text-xs whitespace-nowrap">
                  <button
                    className="bg-indigo-500 hover:bg-indigo-600 text-white text-xs font-bold uppercase px-3 py-2 rounded shadow transition-all duration-150 mr-2"
                    onClick={() => handleAction("modifier", tech._id, tech.nom)}
                    title="Modifier"
                  >
                    <FontAwesomeIcon icon={faPen} />
                  </button>

                  <button
                    className="bg-red-500 hover:bg-red-600 text-white text-xs font-bold uppercase px-3 py-2 rounded shadow transition-all duration-150 mr-2"
                    onClick={() => handleAction("supprimer", tech._id, tech.nom)}
                    title="Supprimer"
                  >
                    <FontAwesomeIcon icon={faTrash} />
                  </button>

                  <button
                    className="bg-green-600 hover:bg-green-700 text-white text-xs font-bold uppercase px-3 py-2 rounded shadow transition-all duration-150"
                    onClick={() => handleAction("completer", tech._id, tech.nom)}
                    title="Compléter"
                  >
                    <FontAwesomeIcon icon={faSquareCheck} />
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}
