import React, { useState, useEffect } from "react";
import bcrypt from "bcryptjs";
import { FaEye, FaEyeSlash } from "react-icons/fa";

export default function CardSettings() {
  const [userData, setUserData] = useState({
    _id: "",
    nom: "",
    prenom: "",
    email: "",
    tel: "",
    password: "",
  });

  const [initialUserData, setInitialUserData] = useState(null);
  const [passwordData, setPasswordData] = useState({
    currentPassword: "",
    newPassword: "",
    confirmPassword: "",
  });

  const [errors, setErrors] = useState({});
  const [showPassword, setShowPassword] = useState(false);
  const [showConfirmPassword, setShowConfirmPassword] = useState(false);

  useEffect(() => {
    const fetchUserData = async () => {
      try {
        const token = localStorage.getItem("token");
        const response = await fetch("/me", {
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${token}`,
          },
        });

        if (!response.ok) throw new Error("Échec de la récupération des données");

        const data = await response.json();

        const parsedUser = {
          _id: data.id,
          nom: data.nom || "",
          prenom: data.prenom || "",
          email: data.email || "",
          tel: data.tel || "",
          password: data.password || "",
        };

        setUserData(parsedUser);
        setInitialUserData(parsedUser);
        localStorage.setItem("user", JSON.stringify(parsedUser));
      } catch (error) {
        console.error("Erreur dans fetchUserData:", error);
      }
    };

    fetchUserData();
  }, []);

  const handleUserChange = (e) => {
    setUserData({ ...userData, [e.target.id]: e.target.value });
  };

  const handlePasswordChange = (e) => {
    setPasswordData({ ...passwordData, [e.target.id]: e.target.value });
  };

  const handleCancel = () => {
    if (initialUserData) setUserData(initialUserData);
    setPasswordData({ currentPassword: "", newPassword: "", confirmPassword: "" });
    setErrors({});
  };

  const validatePhoneNumber = (tel) => /^[259]\d{7}$/.test(tel);

  const validateFields = () => {
    const newErrors = {};

    if (!validatePhoneNumber(userData.tel)) {
      newErrors.tel = "Numéro de téléphone incorrect.";
    }

    if (passwordData.currentPassword && !passwordData.newPassword) {
      newErrors.newPassword = "Veuillez entrer un nouveau mot de passe.";
    } else if (passwordData.newPassword.length > 0 && passwordData.newPassword.length < 6) {
      newErrors.newPassword = "Le mot de passe doit contenir au moins 6 caractères.";
    }

    if (passwordData.newPassword !== passwordData.confirmPassword) {
      newErrors.confirmPassword = "Les mots de passe ne correspondent pas.";
    }

    return newErrors;
  };

  const updateUserDataOnServer = async (dataToSend) => {
    try {
      const response = await fetch(`/updateuser/${userData._id}`, {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(dataToSend),
      });

      const data = await response.json();
      if (response.ok) {
        const updatedData = { ...data, password: data.password || userData.password };
        setUserData(updatedData);
        setInitialUserData(updatedData);
        localStorage.setItem("user", JSON.stringify(updatedData));
        setPasswordData({ currentPassword: "", newPassword: "", confirmPassword: "" });
        setErrors({});
      } else {
        setErrors({ general: "Erreur lors de la mise à jour." });
      }
    } catch (error) {
      setErrors({ general: "Erreur de communication avec le serveur." });
    }
  };

  const handleSave = async (e) => {
    e.preventDefault();
    const validationErrors = validateFields();
    if (Object.keys(validationErrors).length > 0) {
      setErrors(validationErrors);
      return;
    }

    const hasUserDataChanged = Object.keys(userData).some(
      (key) => userData[key] !== initialUserData[key]
    );

    const hasPasswordChange = passwordData.newPassword !== "";

    if (!hasUserDataChanged && !hasPasswordChange) {
      setErrors({ general: "Aucune modification effectuée." });
      return;
    }

    if (passwordData.currentPassword) {
      bcrypt.compare(
        passwordData.currentPassword,
        initialUserData.password,
        async (err, isMatch) => {
          if (err) {
            setErrors({ currentPassword: "Erreur de vérification." });
            return;
          }

          if (!isMatch) {
            setErrors({ currentPassword: "Mot de passe actuel incorrect." });
          } else {
            const dataToSend = { ...userData };
            if (hasPasswordChange) dataToSend.password = passwordData.newPassword;
            await updateUserDataOnServer(dataToSend);
          }
        }
      );
    } else {
      await updateUserDataOnServer(userData);
    }
  };

  return (
    <div className="relative w-full max-w-[1000px] mb-6 shadow-lg rounded-lg bg-white border overflow-hidden">
      <div className="px-6 py-6 border-b">
        <h6 className="text-xl font-bold">Mon Compte</h6>
      </div>
      <div className="px-10 py-8">
        <form onSubmit={handleSave}>
          <h6 className="text-sm mb-6 font-bold uppercase">Informations Personnelles</h6>
          <div className="flex flex-wrap">
            <div className="w-full lg:w-6/12 px-4 mb-4">
              <label className="block font-semibold mb-1" htmlFor="nom">Nom</label>
              <input type="text" id="nom" className="gp-form-input" value={userData.nom} onChange={handleUserChange} />
            </div>
            <div className="w-full lg:w-6/12 px-4 mb-4">
              <label className="block font-semibold mb-1" htmlFor="prenom">Prénom</label>
              <input type="text" id="prenom" className="gp-form-input" value={userData.prenom} onChange={handleUserChange} />
            </div>
            <div className="w-full lg:w-6/12 px-4 mb-4">
              <label className="block font-semibold mb-1" htmlFor="email">Email</label>
              <input type="email" id="email" className="gp-form-input" value={userData.email} disabled style={{ backgroundColor: "#f3f3f3", cursor: "not-allowed" }} />
            </div>
            <div className="w-full lg:w-6/12 px-4 mb-4">
              <label className="block font-semibold mb-1" htmlFor="tel">Num Tel</label>
              <input type="text" id="tel" className="gp-form-input" value={userData.tel} onChange={handleUserChange} />
              {errors.tel && <p className="error-message">{errors.tel}</p>}
            </div>
          </div>

          <hr className="my-6" />

          <h6 className="text-sm mb-6 font-bold uppercase">Changer le mot de passe</h6>
          <div className="flex flex-wrap">
            <div className="w-full lg:w-6/12 px-4 mb-4 relative">
              <label className="block font-semibold mb-1" htmlFor="currentPassword">Ancien mot de passe</label>
              <input type={showPassword ? "text" : "password"} id="currentPassword" className="gp-form-input" value={passwordData.currentPassword} onChange={handlePasswordChange} />
              <span className="absolute right-6 top-10 cursor-pointer" onClick={() => setShowPassword(!showPassword)}>
                {showPassword ? <FaEyeSlash /> : <FaEye />}
              </span>
              {errors.currentPassword && <p className="error-message">{errors.currentPassword}</p>}
            </div>
            <div className="w-full lg:w-6/12 px-4 mb-4 relative">
              <label className="block font-semibold mb-1" htmlFor="newPassword">Nouveau mot de passe</label>
              <input type={showConfirmPassword ? "text" : "password"} id="newPassword" className="gp-form-input" value={passwordData.newPassword} onChange={handlePasswordChange} />
              <span className="absolute right-6 top-10 cursor-pointer" onClick={() => setShowConfirmPassword(!showConfirmPassword)}>
                {showConfirmPassword ? <FaEyeSlash /> : <FaEye />}
              </span>
              {errors.newPassword && <p className="error-message">{errors.newPassword}</p>}
            </div>
            <div className="w-full lg:w-6/12 px-4 mb-4">
              <label className="block font-semibold mb-1" htmlFor="confirmPassword">Confirmer mot de passe</label>
              <input type={showConfirmPassword ? "text" : "password"} id="confirmPassword" className="gp-form-input" value={passwordData.confirmPassword} onChange={handlePasswordChange} />
              {errors.confirmPassword && <p className="error-message">{errors.confirmPassword}</p>}
            </div>
          </div>

          <div className="flex justify-end mt-6">
            <button type="button" className="gp-button-cancel" onClick={handleCancel}>Annuler</button>
            <button type="submit" className="gp-button-save">Sauvegarder</button>
          </div>

          {errors.general && <p className="error-message text-center mt-4">{errors.general}</p>}
        </form>
      </div>

      {/* Styles CSS */}
      <style jsx>{`
        .gp-form-input {
          width: 100%;
          padding: 12px;
          border-radius: 8px;
          border: 1px solid #ccc;
          font-size: 14px;
          color: #333;
        }

        .gp-form-input:focus {
          border-color: #4C9AFF;
        }

        .gp-button-cancel {
          background-color: rgb(110, 114, 117);
          color: white;
          padding: 10px 20px;
          border-radius: 8px;
          border: none;
          cursor: pointer;
          margin-right: 10px;
        }

        .gp-button-cancel:hover {
          background-color: rgb(114, 125, 126);
        }

        .gp-button-save {
          background-color: #4C9AFF;
          color: white;
          padding: 10px 20px;
          border-radius: 8px;
          border: none;
          cursor: pointer;
        }

        .gp-button-save:hover {
          background-color: #3498db;
        }

        .error-message {
          color: #e74c3c;
          font-size: 12px;
        }
      `}</style>
    </div>
  );
}
