import React, { useState, useEffect } from "react";
import IndexNavbar from "components/Navbars/IndexNavbar.js";
import Footerr from "components/Footers/Footerr";
import axios from "axios";

function MyTickets() {
  const [tickets, setTickets] = useState([]);
  const [selectedTicket, setSelectedTicket] = useState(null);

  useEffect(() => {
    document.body.style.margin = "0";
    document.body.style.minHeight = "100vh";
    document.body.style.display = "flex";
    document.body.style.flexDirection = "column";

    axios.get("http://localhost:5000/api/getticket")
      .then((res) => setTickets(res.data))
      .catch((err) => console.error("Erreur de récupération :", err));
  }, []);

  const status = (statut) => ({
    padding: "0.3rem 0.8rem",
    borderRadius: "4px",
    fontSize: "0.85rem",
    backgroundColor: {
      "Ouvert": "#ffebee",
      "En cours": "#e3f2fd",
      "Résolu": "#e8f5e9",
    }[statut] || "#f0f0f0",
    color: {
      "Ouvert": "#c62828",
      "En cours": "#1565c0",
      "Résolu": "#2e7d32",
    }[statut] || "#333",
    fontWeight: "bold",
  });

  return (
    <>
      <IndexNavbar />

      <div style={container}>
        <div style={contentWrapper}>
          <img src="/images/support-banner.jpg" alt="Bannière Support" style={bannerImage} />
          <h1 style={title}>Espace Support Client</h1>
          <p style={subtitle}>Consultez vos tickets et suivez leur avancement</p>

          <div style={ticketGrid}>
            <div style={ticketList}>
              {tickets.map((ticket) => (
                <div key={ticket._id} style={ticketItem} onClick={() => setSelectedTicket(ticket)}>
                  <div style={ticketHeader}>
                    <span style={ticketId}>#{ticket._id.slice(-6)}</span>
                    <span style={status("Ouvert")}>Ouvert</span>
                  </div>
                  <h3 style={ticketTitle}>{ticket.sujet}</h3>
                  <p style={ticketDate}>{new Date(ticket.createdAt).toLocaleDateString("fr-FR")}</p>
                </div>
              ))}
            </div>

            {selectedTicket && (
              <div style={detailsPanel}>
                <div style={detailsHeader}>
                  <h2 style={detailsTitle}>Détails du ticket</h2>
                  <button style={closeButton} onClick={() => setSelectedTicket(null)}>×</button>
                </div>
                <div style={detailsContent}>
                  <div style={detailRow}><span style={detailLabel}>Numéro :</span><span>#{selectedTicket._id.slice(-6)}</span></div>
                  <div style={detailRow}><span style={detailLabel}>Statut :</span><span style={status("Ouvert")}>Ouvert</span></div>
                  <div style={detailRow}><span style={detailLabel}>Date :</span><span>{new Date(selectedTicket.createdAt).toLocaleDateString("fr-FR")}</span></div>
                  <div style={detailDescription}><p style={detailLabel}>Description :</p><p>{selectedTicket.description}</p></div>
                </div>
              </div>
            )}
          </div>
        </div>
      </div>

      <Footerr />
    </>
  );
}

// Styles (identiques à ta version)

export default MyTickets;
