import React from "react";
import {
  FaFacebookF,
  FaInstagram,
  FaTwitter,
  FaLinkedinIn,
  FaYoutube,
} from "react-icons/fa";
import logo from "assets/img/logo-stb.png"; // Assure-toi que le nom du fichier n'a pas d'espace

export default function Footer() {
  return (
    <footer style={styles.footer}>
      <div style={styles.container}>
        <img src={logo} alt="STB Logo" style={styles.logo} />
        <h1 style={styles.title}>Stb Banque</h1>

        <div style={styles.divider}></div>

        <div style={styles.socialIcons}>
          <a href="#" style={styles.icon}><FaFacebookF /></a>
          <a href="#" style={styles.icon}><FaInstagram /></a>
          <a href="#" style={styles.icon}><FaTwitter /></a>
          <a href="#" style={styles.icon}><FaLinkedinIn /></a>
          <a href="#" style={styles.icon}><FaYoutube /></a>
        </div>

        <p style={styles.tagline}>Votre banque, partout, à tout moment.</p>

        <p style={styles.copy}>
          © {new Date().getFullYear()} Stb Banque. Tous droits réservés.
        </p>
      </div>
    </footer>
  );
}

// CSS en JS (compact + sticky)
const styles = {
  footer: {
    backgroundColor: "#f3f4f6",
    padding: "2rem 1rem",
    borderTop: "4px solid #0d47a1",
    boxShadow: "0 -3px 15px rgba(0,0,0,0.05)",
    position: "relative", // Sticky basic
    bottom: "0",
    width: "100%",
  },
  container: {
    maxWidth: "900px",
    margin: "0 auto",
    textAlign: "center",
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
  },
  logo: {
    width: "65px",
    height: "auto",
    marginBottom: "0.8rem",
    filter: "drop-shadow(0 3px 6px rgba(0,0,0,0.12))",
  },
  title: {
    fontSize: "1.6rem",
    fontWeight: "700",
    color: "#0f172a",
    marginBottom: "0.8rem",
  },
  divider: {
    width: "40px",
    height: "3px",
    backgroundColor: "#0d47a1",
    margin: "0.8rem auto 1.5rem",
    borderRadius: "3px",
  },
  socialIcons: {
    display: "flex",
    gap: "0.8rem",
    justifyContent: "center",
    marginBottom: "1.5rem",
  },
  icon: {
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    backgroundColor: "#e0e7ff",
    color: "#0d47a1",
    borderRadius: "50%",
    width: "38px",
    height: "38px",
    fontSize: "1.1rem",
    transition: "0.3s ease",
    boxShadow: "0 2px 5px rgba(0,0,0,0.08)",
    textDecoration: "none",
  },
  tagline: {
    fontSize: "1rem",
    color: "#334155",
    marginBottom: "0.8rem",
    fontStyle: "italic",
  },
  copy: {
    fontSize: "0.85rem",
    color: "#64748b",
  },
};
