import React, { useState, useEffect } from 'react';

// Composant pour afficher une liste de tickets
const TicketList = ({ tickets }) => {
  return (
    <div className="ticket-list">
      <h2>Mes Tickets</h2>
      {tickets.length === 0 ? (
        <p>Aucun ticket disponible.</p>
      ) : (
        <ul>
          {tickets.map((ticket) => (
            <li key={ticket.id} className="ticket-item">
              <div>
                <h4>Ticket #{ticket.id}</h4>
                <p><strong>Statut:</strong> {ticket.status}</p>
                <p><strong>Description:</strong> {ticket.description}</p>
              </div>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

// Composant principal de la page d'accueil
const HomePage = () => {
  const [tickets, setTickets] = useState([]);
  const [loading, setLoading] = useState(true);

  // Simulation de l'appel API pour récupérer les tickets
  useEffect(() => {
    setTimeout(() => {
      const fetchedTickets = [
        { id: 1, status: 'En cours', description: 'Problème de connexion' },
        { id: 2, status: 'Résolu', description: 'Erreur de virement' },
        { id: 3, status: 'Ouvert', description: 'Problème d\'authentification' },
      ];
      setTickets(fetchedTickets);
      setLoading(false);
    }, 2000);
  }, []);

  if (loading) {
    return (
      <div className="loading">
        <p>Chargement des tickets...</p>
      </div>
    );
  }

  return (
    <div className="home-page">
      <header className="header">
        <h1>STB - Gestion des Tickets</h1>
        <p className="description">
          Gérez vos demandes de manière simple et efficace. Ouvrez un ticket pour toute question ou problème.
        </p>
      </header>

      <section className="ticket-actions">
        <button className="create-ticket-button">Créer un ticket</button>
      </section>

      <TicketList tickets={tickets} />

      <footer>
        <p>&copy; 2025 STB Banque Tunisie</p>
      </footer>

      <style jsx>{`
        body {
          font-family: Arial, sans-serif;
          background-color: #f9f9f9;
          margin: 0;
          padding: 0;
          color: #333;
          display: flex;
    justify-content: center;
    align-items: center;
    height: 100vh;
        }

        .home-page {
          padding: 20px;
          max-width: 1200px;
          margin: auto;
          background-color: white;
          box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
          border-radius: 10px;
        }

        .header {
          text-align: center;
          background-color: #0084ff;
          color: white;
          padding: 20px;
          border-radius: 10px;
        }

        .header h1 {
          margin: 0;
          font-size: 36px;
        }

        .description {
          font-size: 18px;
          margin-top: 10px;
        }

        .ticket-actions {
          text-align: center;
          margin-top: 30px;
        }

        .create-ticket-button {
          padding: 12px 24px;
          background-color: #0084ff;
          color: white;
          border: none;
          border-radius: 5px;
          font-size: 16px;
          cursor: pointer;
          transition: background-color 0.3s;
        }

        .create-ticket-button:hover {
          background-color: #005bb5;
        }

        .ticket-list {
          margin-top: 30px;
        }

        .ticket-item {
          background-color: white;
          margin: 10px 0;
          padding: 10px;
          border: 1px solid #ccc;
          border-radius: 5px;
        }

        .ticket-item h4 {
          margin: 0;
        }

        .ticket-item p {
          margin: 5px 0;
        }

        footer {
          text-align: center;
          margin-top: 20px;
          font-size: 12px;
        }

        .loading {
          text-align: center;
          font-size: 18px;
          padding: 20px;
        }
      `}</style>
    </div>
  );
};

export default HomePage;
