import React from "react";
import {
  FaFacebookF,
  FaInstagram,
  FaTwitter,
  FaLinkedinIn,
} from "react-icons/fa";
import logo from "../assets/img/logo-stb.png"; // V√©rifie le chemin vers ton image

export default function Footer() {
  return (
    <footer style={styles.footer}>
      <div style={styles.container}>
        <img src={logo} alt="STB Logo" style={styles.logo} />
        <p style={styles.title}>Stb Banque</p>

        <div style={styles.socialIcons}>
          <a href="#" style={styles.icon}><FaFacebookF /></a>
          <a href="#" style={styles.icon}><FaInstagram /></a>
          <a href="#" style={styles.icon}><FaTwitter /></a>
          <a href="#" style={styles.icon}><FaLinkedinIn /></a>
        </div>

        <p style={styles.copy}>
          ¬© {new Date().getFullYear()} Stb Banque. Tous droits r√©serv√©s.
        </p>
      </div>
    </footer>
  );
}

// üßº Style √©quilibr√© : visible mais compact
const styles = {
  footer: {
    backgroundColor: "#f3f4f6",
    padding: "2rem 1rem", // Taille mod√©r√©e
    borderTop: "3px solid #0d47a1",
    fontSize: "0.9rem",
  },
  container: {
    maxWidth: "1000px",
    margin: "0 auto",
    textAlign: "center",
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    gap: "0.8rem",
  },
  logo: {
    width: "55px", // Taille interm√©diaire
    height: "auto",
  },
  title: {
    fontSize: "1.2rem",
    fontWeight: "600",
    color: "#0f172a",
  },
  socialIcons: {
    display: "flex",
    gap: "0.8rem",
    justifyContent: "center",
    marginTop: "0.5rem",
  },
  icon: {
    backgroundColor: "#e0e7ff",
    color: "#0d47a1",
    borderRadius: "50%",
    width: "34px", // Taille interm√©diaire
    height: "34px",
    fontSize: "1rem",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    textDecoration: "none",
  },
  copy: {
    fontSize: "0.85rem",
    color: "#64748b",
    marginTop: "0.5rem",
  },
};
