import React, { useState } from "react";
import PropTypes from "prop-types";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faTrash, faPen, faSquareCheck } from '@fortawesome/free-solid-svg-icons';

export default function CardTable({ color }) {
  const [items, setItems] = useState([
    { id: 1, subject: 'Problème d\'accès à internet', status: 'En attente', date: '2025-03-29', description: 'Le site intranet était inaccessible.', type: 'Urgent' },
    { id: 2, subject: 'Demande de congé', status: 'Terminé', date: '2025-03-25', description: 'Validation de congé annuel.', type: 'Normal' },
    { id: 3, subject: 'Erreur de virement', status: 'En attente', date: '2025-03-27', description: 'Problème sur un virement automatique.', type: 'Urgent' },
  ]);

  const [isDialogOpen, setIsDialogOpen] = useState(false);
  const [selectedItemId, setSelectedItemId] = useState(null);
  const [filterType, setFilterType] = useState("Tous");

  const handleDeleteConfirmation = (id) => {
    setSelectedItemId(id);
    setIsDialogOpen(true);
  };

  const handleConfirmDelete = () => {
    setItems(items.filter(item => item.id !== selectedItemId));
    setIsDialogOpen(false);
  };

  const handleCancelDelete = () => {
    setIsDialogOpen(false);
    setSelectedItemId(null);
  };

  const handleComplete = (id) => {
    setItems(items.map(item =>
      item.id === id ? { ...item, status: 'Terminé' } : item
    ));
  };

  const handleEdit = (id) => {
    setItems(items.map(item =>
      item.id === id ? { ...item, subject: 'Mis à jour', description: 'Contenu modifié.' } : item
    ));
  };

  const filteredItems = items
    .filter(item => filterType === "Tous" || item.type === filterType)
    .sort((a, b) => (a.type === 'Urgent' ? -1 : 1));

  return (
    <div className="p-6 bg-white shadow-lg rounded-lg">
      {isDialogOpen && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white p-6 rounded-lg shadow-md w-full max-w-md">
            <p className="text-lg font-semibold mb-4 text-gray-700">
              Êtes-vous sûr de vouloir supprimer ce ticket ?
            </p>
            <div className="flex justify-end gap-4">
              <button
                onClick={handleCancelDelete}
                className="bg-gray-300 hover:bg-gray-400 text-gray-800 px-4 py-2 rounded"
              >Annuler</button>
              <button
                onClick={handleConfirmDelete}
                className="bg-red-500 hover:bg-red-600 text-white px-4 py-2 rounded"
              >Supprimer</button>
            </div>
          </div>
        </div>
      )}

      <div className="mb-4">
        <label className="font-medium text-gray-700 mr-2">Filtrer par type :</label>
        <select
          value={filterType}
          onChange={(e) => setFilterType(e.target.value)}
          className="border border-gray-300 rounded px-3 py-2"
        >
          <option value="Tous">Tous</option>
          <option value="Urgent">Urgent</option>
          <option value="Normal">Normal</option>
        </select>
      </div>

      <div className="overflow-x-auto">
        <table className="w-full text-sm text-left text-gray-700 border border-gray-300 rounded-lg">
          <thead className="bg-blue-50 text-blue-900 uppercase text-xs">
            <tr>
              <th className="px-6 py-3">Id</th>
              <th className="px-6 py-3">Sujet</th>
              <th className="px-6 py-3">Statut</th>
              <th className="px-6 py-3">Date</th>
              <th className="px-6 py-3">Type</th>
              <th className="px-6 py-3">Description</th>
              <th className="px-6 py-3">Actions</th>
            </tr>
          </thead>
          <tbody>
            {filteredItems.map(item => (
              <tr
                key={item.id}
                className={`border-t text-sm ${item.type === 'Urgent' ? 'bg-red-50' : 'bg-white'} hover:bg-gray-100`}
              >
                <td className="px-6 py-4 font-semibold">{item.id}</td>
                <td className="px-6 py-4">{item.subject}</td>
                <td className="px-6 py-4">
                  <span className={`inline-flex items-center px-2 py-1 text-xs font-medium rounded-full ${item.status === 'En attente' ? 'bg-yellow-100 text-yellow-800' : 'bg-green-100 text-green-800'}`}>
                    {item.status}
                  </span>
                </td>
                <td className="px-6 py-4">{item.date}</td>
                <td className="px-6 py-4">
                  <span className={`text-sm font-medium ${item.type === 'Urgent' ? 'text-red-600' : 'text-gray-600'}`}>{item.type}</span>
                </td>
                <td className="px-6 py-4">{item.description}</td>
                <td className="px-6 py-4 flex gap-2">
                  <button
                    onClick={() => handleDeleteConfirmation(item.id)}
                    className="bg-red-500 hover:bg-red-600 text-white px-3 py-1 rounded"
                  >
                    <FontAwesomeIcon icon={faTrash} />
                  </button>
                  <button
                    onClick={() => handleEdit(item.id)}
                    className="bg-indigo-500 hover:bg-indigo-600 text-white px-3 py-1 rounded"
                  >
                    <FontAwesomeIcon icon={faPen} />
                  </button>
                  <button
                    onClick={() => handleComplete(item.id)}
                    className="bg-green-500 hover:bg-green-600 text-white px-3 py-1 rounded"
                  >
                    <FontAwesomeIcon icon={faSquareCheck} />
                  </button>
                </td>
              </tr>
            ))}
            {filteredItems.length === 0 && (
              <tr>
                <td colSpan="7" className="text-center py-4 text-gray-500">
                  Aucun ticket trouvé.
                </td>
              </tr>
            )}
          </tbody>
        </table>
      </div>
    </div>
  );
}

CardTable.defaultProps = {
  color: "light",
};

CardTable.propTypes = {
  color: PropTypes.oneOf(["light", "dark"]),
};
