import React, { useEffect, useState } from "react";
import PropTypes from "prop-types";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faTrash, faPen } from "@fortawesome/free-solid-svg-icons";
import { getAllUsers } from "services/ApiUser";

export default function CardTable({ color }) {
  const [users, setUsers] = useState([]);
  const [showForm, setShowForm] = useState(false); // ⬅️ pour afficher/masquer le formulaire
  const [newUser, setNewUser] = useState({
    nom: "",
    prenom: "",
    email: "",
    id: "",
  });

  const getUsers = async () => {
    try {
      await getAllUsers().then((res) => {
        setUsers(res.data.userList);
      });
    } catch (error) {
      console.log(error);
    }
  };

  useEffect(() => {
    getUsers();
  }, []);

  const handleInputChange = (e) => {
    setNewUser({ ...newUser, [e.target.name]: e.target.value });
  };

  const handleAddUser = (e) => {
    e.preventDefault();
    console.log("New user added:", newUser); // remplace ça par une requête API si tu veux
    setUsers([...users, newUser]);
    setNewUser({ nom: "", prenom: "", email: "", id: "" });
    setShowForm(false);
  };

  return (
    <>
      <div
        className={
          "relative flex flex-col min-w-0 break-words w-full mb-6 shadow-lg rounded " +
          (color === "light" ? "bg-white" : "bg-lightBlue-900 text-white")
        }
      >
        <div className="rounded-t mb-0 px-4 py-3 border-0">
          <div className="flex flex-wrap items-center justify-between">
            <h3
              className={
                "font-semibold text-lg " +
                (color === "light" ? "text-blueGray-700" : "text-white")
              }
            >
              Users
            </h3>
            <button
              className="bg-emerald-500 text-white text-xs font-bold uppercase px-4 py-2 rounded shadow hover:shadow-md"
              onClick={() => setShowForm(!showForm)}
            >
              {showForm ? "Cancel" : "Add User"}
            </button>
          </div>
        </div>

        <div className="block w-full overflow-x-auto">
          <table className="items-center w-full bg-transparent border-collapse">
            <thead>
              <tr>
                {["Nom", "Prenom", "Email", "ID", ""].map((head, i) => (
                  <th
                    key={i}
                    className={
                      "px-6 py-3 text-xs font-semibold uppercase text-left border border-solid whitespace-nowrap " +
                      (color === "light"
                        ? "bg-blueGray-50 text-blueGray-500 border-blueGray-100"
                        : "bg-lightBlue-800 text-lightBlue-300 border-lightBlue-700")
                    }
                  >
                    {head}
                  </th>
                ))}
              </tr>
            </thead>
            <tbody>
              {users.map((u, index) => (
                <tr key={index}>
                  <td className="px-6 py-4 text-xs border-t">{u.nom}</td>
                  <td className="px-6 py-4 text-xs border-t">{u.prenom}</td>
                  <td className="px-6 py-4 text-xs border-t">{u.email}</td>
                  <td className="px-6 py-4 text-xs border-t">{u.id}</td>
                  <td className="px-6 py-4 text-xs border-t text-right">
                    <button className="bg-red-500 text-white px-4 py-2 rounded shadow hover:shadow-lg text-xs font-bold uppercase ml-2">
                      <FontAwesomeIcon icon={faTrash} />
                    </button>
                    <button className="bg-lightBlue-500 text-white px-4 py-2 rounded shadow hover:shadow-lg text-xs font-bold uppercase ml-2">
                      <FontAwesomeIcon icon={faPen} />
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>

          {showForm && (
            <form
              onSubmit={handleAddUser}
              className="p-4 bg-blueGray-50 rounded mt-4"
            >
              <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
                <input
                  type="text"
                  name="nom"
                  placeholder="Nom"
                  value={newUser.nom}
                  onChange={handleInputChange}
                  className="p-2 border rounded"
                  required
                />
                <input
                  type="text"
                  name="prenom"
                  placeholder="Prenom"
                  value={newUser.prenom}
                  onChange={handleInputChange}
                  className="p-2 border rounded"
                  required
                />
                <input
                  type="email"
                  name="email"
                  placeholder="Email"
                  value={newUser.email}
                  onChange={handleInputChange}
                  className="p-2 border rounded"
                  required
                />
                <input
                  type="text"
                  name="id"
                  placeholder="ID"
                  value={newUser.id}
                  onChange={handleInputChange}
                  className="p-2 border rounded"
                  required
                />
              </div>
              <button
                type="submit"
                className="mt-4 bg-green-500 text-white px-4 py-2 rounded hover:bg-green-600"
              >
                Submit
              </button>
            </form>
          )}
        </div>
      </div>
    </>
  );
}

CardTable.defaultProps = {
  color: "light",
};

CardTable.propTypes = {
  color: PropTypes.oneOf(["light", "dark"]),
};
