import React from "react";
import {
  FaFacebookF,
  FaInstagram,
  FaTwitter,
  FaLinkedinIn,
  FaYoutube,
} from "react-icons/fa";
import logo from "../assets/img/logo-stb.png"; // ajuste ce chemin selon ton projet

export default function Footer() {
  return (
    <footer style={styles.footer}>
      <div style={styles.container}>
        <img src={logo} alt="STB Logo" style={styles.logo} />
        <h1 style={styles.title}>Stb Banque</h1>

        <div style={styles.divider}></div>

        <div style={styles.socialIcons}>
          <a href="#" style={styles.icon}><FaFacebookF /></a>
          <a href="#" style={styles.icon}><FaInstagram /></a>
          <a href="#" style={styles.icon}><FaTwitter /></a>
          <a href="#" style={styles.icon}><FaLinkedinIn /></a>
          <a href="#" style={styles.icon}><FaYoutube /></a>
        </div>

        <p style={styles.tagline}>Votre banque, partout, à tout moment.</p>
        <p style={styles.copy}>© {new Date().getFullYear()} Stb Banque. Tous droits réservés.</p>
      </div>
    </footer>
  );
}

// Style très agrandi
const styles = {
  footer: {
    backgroundColor: "#f3f4f6",
    padding: "6rem 2rem", // très grand espace intérieur
    borderTop: "6px solid #0d47a1",
    boxShadow: "0 -6px 25px rgba(0,0,0,0.1)",
    width: "100%",
  },
  container: {
    maxWidth: "1200px",
    margin: "0 auto",
    textAlign: "center",
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
  },
  logo: {
    width: "140px", // logo plus large
    height: "auto",
    marginBottom: "2rem",
    filter: "drop-shadow(0 6px 10px rgba(0,0,0,0.2))",
  },
  title: {
    fontSize: "3rem", // texte très grand
    fontWeight: "800",
    color: "#0f172a",
    marginBottom: "1rem",
  },
  divider: {
    width: "80px",
    height: "6px",
    backgroundColor: "#0d47a1",
    margin: "1.5rem auto 2.5rem",
    borderRadius: "6px",
  },
  socialIcons: {
    display: "flex",
    gap: "1.5rem",
    justifyContent: "center",
    marginBottom: "3rem",
  },
  icon: {
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    backgroundColor: "#e0e7ff",
    color: "#0d47a1",
    borderRadius: "50%",
    width: "60px", // plus gros
    height: "60px",
    fontSize: "1.8rem", // taille des icônes
    transition: "0.3s ease",
    boxShadow: "0 3px 8px rgba(0,0,0,0.1)",
    textDecoration: "none",
  },
  tagline: {
    fontSize: "1.5rem", // grand sous-texte
    color: "#334155",
    marginBottom: "1.5rem",
    fontStyle: "italic",
  },
  copy: {
    fontSize: "1.1rem",
    color: "#64748b",
  },
};
