import React from "react";
import {
  FaFacebookF,
  FaInstagram,
  FaTwitter,
  FaLinkedinIn,
  FaYoutube,
} from "react-icons/fa";
import logo from "assets/img/logo-stb.png"; // ajuste ce chemin selon ton projet

export default function Footer() {
  return (
    <footer style={styles.footer}>
      <div style={styles.container}>
        <img src={logo} alt="STB Logo" style={styles.logo} />
        <h1 style={styles.title}>Stb Banque</h1>

        <div style={styles.socialIcons}>
          <a href="#" style={styles.icon}><FaFacebookF /></a>
          <a href="#" style={styles.icon}><FaInstagram /></a>
          <a href="#" style={styles.icon}><FaTwitter /></a>
          <a href="#" style={styles.icon}><FaLinkedinIn /></a>
          <a href="#" style={styles.icon}><FaYoutube /></a>
        </div>

        <p style={styles.copy}>
          © {new Date().getFullYear()} Stb Banque. Tous droits réservés.
        </p>
      </div>
    </footer>
  );
}

// Styles version mini
const styles = {
  footer: {
    backgroundColor: "#f3f4f6",
    padding: "2rem 1rem", // ← Moins de hauteur
    borderTop: "4px solid #0d47a1",
    boxShadow: "0 -3px 15px rgba(0,0,0,0.05)",
  },
  container: {
    maxWidth: "1000px",
    margin: "0 auto",
    textAlign: "center",
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
  },
  logo: {
    width: "60px", // ← Logo réduit
    height: "auto",
    marginBottom: "0.5rem",
  },
  title: {
    fontSize: "1.5rem", // ← Titre plus petit
    fontWeight: "600",
    color: "#0f172a",
    marginBottom: "0.5rem",
  },
  socialIcons: {
    display: "flex",
    gap: "0.8rem",
    justifyContent: "center",
    marginBottom: "1rem",
  },
  icon: {
    backgroundColor: "#e0e7ff",
    color: "#0d47a1",
    borderRadius: "50%",
    width: "36px", // ← Plus petit cercle
    height: "36px",
    fontSize: "1rem",
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    textDecoration: "none",
    boxShadow: "0 1px 4px rgba(0,0,0,0.08)",
  },
  copy: {
    fontSize: "0.9rem",
    color: "#64748b",
    marginTop: "0.5rem",
  },
};
