import React from "react";
import IndexNavbar from "components/Navbars/IndexNavbar.js";
import Footerr from "components/Footers/Footerr";

function MyTickets() {
  // üéüÔ∏è Donn√©es de tickets fictives (√† remplacer plus tard par des donn√©es r√©elles via API ou base de donn√©es)
  const tickets = [
    {
      id: 1,
      sujet: "Connexion VPN bloqu√©e",
      departement: "IT",
      statut: "Ouvert",
      date: "2025-04-04",
    },
    {
      id: 2,
      sujet: "Erreur sur fiche de paie",
      departement: "RH",
      statut: "En cours",
      date: "2025-04-01",
    },
    {
      id: 3,
      sujet: "Probl√®me d'acc√®s √† l'intranet",
      departement: "IT",
      statut: "R√©solu",
      date: "2025-03-29",
    },
  ];

  return (
    <>
      <IndexNavbar />

      <div style={pageWrapper}>
        <div style={ticketCard}>
          <h1 style={titleStyle}>üìã Mes Tickets</h1>

          <div style={ticketListStyle}>
            {tickets.map((ticket) => (
              <div key={ticket.id} style={ticketItemStyle}>
                <div style={ticketHeaderStyle}>
                  <h3 style={{ margin: 0 }}>{ticket.sujet}</h3>
                  <span style={statusStyle(ticket.statut)}>{ticket.statut}</span>
                </div>
                <p style={ticketMetaStyle}>
                  üè¢ {ticket.departement} | üìÖ {ticket.date}
                </p>
              </div>
            ))}
          </div>
        </div>
      </div>

      <Footerr />
    </>
  );
}

// ‚ú® Styles
const pageWrapper = {
  fontFamily: "'Segoe UI', sans-serif",
  background: "#f0f4f8",
  minHeight: "100vh",
  padding: "3rem 1rem",
  display: "flex",
  justifyContent: "center",
  alignItems: "flex-start",
};

const ticketCard = {
  background: "#ffffff",
  borderRadius: "16px",
  boxShadow: "0 6px 18px rgba(0,0,0,0.1)",
  padding: "2.5rem 3rem",
  maxWidth: "800px",
  width: "100%",
};

const titleStyle = {
  fontSize: "1.9rem",
  fontWeight: "700",
  color: "#0d47a1",
  marginBottom: "2rem",
};

const ticketListStyle = {
  display: "flex",
  flexDirection: "column",
  gap: "1.5rem",
};

const ticketItemStyle = {
  background: "#f9f9f9",
  padding: "1.2rem",
  borderRadius: "12px",
  borderLeft: "6px solid #0d47a1",
  boxShadow: "0 2px 5px rgba(0,0,0,0.05)",
};

const ticketHeaderStyle = {
  display: "flex",
  justifyContent: "space-between",
  alignItems: "center",
};

const ticketMetaStyle = {
  marginTop: "0.5rem",
  color: "#555",
  fontSize: "0.95rem",
};

const statusStyle = (statut) => {
  let color;
  switch (statut) {
    case "Ouvert":
      color = "#ff9800";
      break;
    case "En cours":
      color = "#2196f3";
      break;
    case "R√©solu":
      color = "#4caf50";
      break;
    default:
      color = "#999";
  }
  return {
    padding: "0.4rem 0.8rem",
    backgroundColor: color,
    color: "#fff",
    borderRadius: "8px",
    fontSize: "0.8rem",
    fontWeight: "600",
  };
};

export default MyTickets;
